app.controller('bannerCtrl', ['$scope', '$gmRestful', '$location', function ($scope, $gmRestful, $location) {
    $scope.backgoundImg = GLOBAL_INDEX_CONFIG.backgoundImg;
    $scope.logImg = GLOBAL_INDEX_CONFIG.logImg;
