}]);
app.controller('swiperBoxCtrl', ['$scope', '$timeout', function ($scope, $timeout) {
    //获取APP子系统列表信息
    var arrayApps = [];
    for (var ii = 0; ii < GM.AppInfos.length; ii++) {
        var appItem = GM.AppInfos[ii];
        var bHasPermission = GM.FrameManager.hasPermission(appItem.name);
        if (bHasPermission) {
            var addItem = {
                img: appItem.icon,
                url: appItem.host,
                txt: appItem.title,
                title: appItem.title,
                permission: appItem.name
            };
            arrayApps.push(addItem);
        }
    }

    $scope.swiperList = arrayApps;
    $timeout(function () {
        var mySwiper;
        if ($scope.swiperList.length < 7) {
            mySwiper = new Swiper('.swiper-container', {
                width: window.screen.width / 6,
                nextButton: '.swiper-button-next',
                prevButton: '.swiper-button-prev'
            });
        } else {
            mySwiper = new Swiper('.swiper-container', {
                width: window.screen.width / 6,
                slidesOffsetBefore: 50,
                slidesOffsetAfter: 50,
                nextButton: '.swiper-button-next',
                prevButton: '.swiper-button-prev'
            });
        }
        mySwiper.onResize();
    }, 10);
    $scope.goSubsystem = function (strUrl) {
        GM.CenterCommon.openSystem(strUrl);
        return false;
    };
}]);
app.controller('footerCtrl', ['$scope', function ($scope) {
    $scope.Copyright = GLOBAL_INDEX_CONFIG.Copyright;
}]);
app.run(['$templateCache', function ($templateCache) {
