'use strict';

require('../../frame/frame.js');

require('../center-common.js');

require('./system-center.less');

var app = angular.module('systemCenterApp', ['gm.ngCustom.control', 'ngResource']);

//主控制器
app.controller('systemCenterCtrl', ['$scope', '$gmRestful', function ($scope, $gmRestful) {
    //数据准备
    var pRestful = new $gmRestful();
    GM.FrameManager.prepareData($scope, pRestful);

    //获取APP子系统列表信息
    var arrayApps = [];
    for (var ii = 0; ii < GM.AppInfos.length; ii++) {
        var appItem = GM.AppInfos[ii];
        var addItem = {
            img: appItem.icon,
            url: appItem.host,
            txt: appItem.title,
            title: appItem.title,
            width: 1,
            height: 1,
            permission: appItem.name
        };
        if (GLOBAL_INDEX_CONFIG.metroBoxList && GLOBAL_INDEX_CONFIG.metroBoxList[ii]) {
            addItem.width = GLOBAL_INDEX_CONFIG.metroBoxList[ii].width;
            addItem.height = GLOBAL_INDEX_CONFIG.metroBoxList[ii].height;
            if (GLOBAL_INDEX_CONFIG.metroBoxList[ii].url) {
                addItem.url = GLOBAL_INDEX_CONFIG.metroBoxList[ii].url;
            }
        }
        arrayApps.push(addItem);
    }

    //子系统权限控制
    var noPermissionArry = GM.CenterCommon.getPermissionList(arrayApps);
    for (var i in noPermissionArry) {
        arrayApps[noPermissionArry[i]]['className'] = 'disable';
        arrayApps[noPermissionArry[i]]['title'] = '无此系统权限';
    }

    GLOBAL_INDEX_CONFIG.metroBoxList = arrayApps;
}]);

app.controller('bannerCtrl', ['$scope', function ($scope) {
    $scope.backgoundImg = GLOBAL_INDEX_CONFIG.backgoundImg;
    $scope.logImg = GLOBAL_INDEX_CONFIG.logImg;
    $scope.systemName = GLOBAL_INDEX_CONFIG.systemName;
    $scope.EnglishSystemName = GLOBAL_INDEX_CONFIG.EnglishSystemName;

    //暂时用的假头像和用户名
    if ($scope.user) {
        $scope.user.avatar = 'syscenter/img/touxiang.png';
        $scope.user.avatar = {
            background: 'url(' + require('./user.png') + ') no-repeat 0 0 / contain',
            'border-radius': '50px'
        };
    }

    //登出
    $scope.logout = function ($event) {
        GM.FrameManager.logout();
        $event.preventDefault();
    };
}]);
app.controller('metroBoxCtrl', ['$scope', '$rootScope', function ($scope, $rootScope) {
    $scope.metroBoxList = [];
    var moreItem = {
        img: 'syscenter/img/更多3.png',
        url: 'javascript:;',
        name: 'more',
        txt: '更多'
    };

    //获取当前页需要展示的列表
    getPageList();

    $rootScope.goSubsystem = function (item) {
        if (item.name !== 'more') {
            if (item.className !== 'disable') {
                GM.CenterCommon.openSystem(item.url);
                return false;
            } else {
                return false;
            }
        } else {
            $rootScope.moreOpen = 'open';
        }
    };

    /**
     * @hide
     * @description 获取页面的子系统集合
     */
    function getPageList() {
        var nPageWidth = 4;
        var nPageHeight = 3;
        var tempArray = initArray(nPageHeight, nPageWidth);
        var arrayPageOne = [];
        var arrayPageTwo = [];
        var ii;
        var pItem;
        var items = GLOBAL_INDEX_CONFIG.metroBoxList;

        for (ii = 0; ii < items.length; ii++) {
            pItem = items[ii];
            var bRt = pushIntoArray(pItem, tempArray);
            if (bRt === true) {
                //arrayPageOne.push(pItem);
            } else {
                arrayPageTwo.push(pItem);
            }
        }

        if (arrayPageTwo.length > 0) {
            //当有第二页的时候，需要将最后一个模块放到第二页，
            //然后出现更多按钮
            var lastItem = tempArray[nPageHeight - 1][nPageWidth - 1];

            //删掉第一页中的这个按钮
            for (ii = 0; ii < nPageHeight; ii++) {
                for (var jj = 0; jj < nPageWidth; jj++) {
                    pItem = tempArray[ii][jj];
                    if (pItem === lastItem) {
                        tempArray[ii][jj] = null;
                    }
                }
            }
            arrayPageTwo.unshift(lastItem);

            //第一页添加更多按钮
            tempArray[nPageHeight - 1][nPageWidth - 1] = moreItem;
        }

        for (ii = 0; ii < nPageHeight; ii++) {
            for (var jj = 0; jj < nPageWidth; jj++) {
                pItem = tempArray[ii][jj];
                if (arrayPageOne.indexOf(pItem) === -1 && pItem != null) {
                    arrayPageOne.push(pItem);
                }
            }
        }
        $scope.metroBoxList = arrayPageOne;
        $rootScope.moreMetroBoxList = arrayPageTwo;
    }

    /**
     * @hide
     * @description 初始化二维数组
     * @method initArray
     * @param {Number} nX
     * @param {Number} nY
     * @return {Array}
     */
    function initArray(nX, nY) {
        var result = [];
        for (var ii = 0; ii < nX; ii++) {
            result[ii] = [];
            for (var jj = 0; jj < nY; jj++) {
                result[ii][jj] = null;
            }
        }
        // result[nX - 1][nY - 1] = moreItem;
        return result;
    }

    /**
     * @hide
     * @description 将数组元素插入二维数组中
     * @method pushIntoArray
     * @param {Number} pItem 数组元素
     * @param {Array} arrayBox 二维数组
     * @return {boolean} 是否插入成功
     */
    function pushIntoArray(pItem, arrayBox) {
        var bResult = false;
        for (var ii = 0; ii < arrayBox.length; ii++) {
            for (var jj = 0; jj < arrayBox[ii].length; jj++) {
                if (arrayBox[ii][jj] == null) {
                    var bFit = isItemFit(pItem, arrayBox, jj, ii);
                    if (bFit) {
                        fitArray(pItem, arrayBox, jj, ii);
                        bResult = true;
                        return bResult;
                    } else {
                        // arrayBox[ii][jj] = {};
                    }
                }
            }
        }
        return bResult;
    }

    /**
     * @hide
     * @description 当前页是否能够放下该模块
     * @method isItemFit
     * @param pItem
     * @param arrayBox
     * @param nIndexX
     * @param nIndexY
     * @return {boolean}
     */
    function isItemFit(pItem, arrayBox, nIndexX, nIndexY) {
        var bFit = true;
        for (var ii = nIndexX; ii < nIndexX + pItem.width; ii++) {
            if (ii >= arrayBox[0].length) {
                return false;
            }
            for (var jj = nIndexY; jj < nIndexY + pItem.height; jj++) {
                if (jj >= arrayBox.length) {
                    return false;
                }
                if (arrayBox[jj][ii] != null) {
                    bFit = false;
                    return bFit;
                }
            }
        }
        return bFit;
    }

    /**
     * @hide
     * @description 能够放入二维数组的前提下插入到数组
     * @param pItem
     * @param arrayBox
     * @param nIndexX
     * @param nIndexY
     */
    function fitArray(pItem, arrayBox, nIndexX, nIndexY) {
        for (var ii = nIndexX; ii < nIndexX + pItem.width; ii++) {
            for (var jj = nIndexY; jj < nIndexY + pItem.height; jj++) {
                arrayBox[jj][ii] = pItem;
            }
        }
    }
}]);
