	_removeRedisString: function _removeRedisString(key) {
		GM.Form.CommonOper.syncAjaxGet(window.FORM_REST_URL.workflow.removeRedisString + '?key=' + key, function (res) {}, function (res) {});
	},
	/**
  * 缓存数据到Redis
  *
  * @param {String} key 键
