	},
	/**
  * 数据列表通用操作之一：弹出对话框
  *
  * @param {Object} scope    当前作用域
  * @param {Object} item     当前行对象
  */
	_dataGridOperA: function _dataGridOperA(scope, item) {
		var ele = event.srcElement;

		if (!ele) {
			return;
		}

		var linkText = $(ele).text();
		var templates = item.grid.element.attr('template-items').split(',');
		var formid, title, buttons;

		for (var i = 0; i < templates.length; i++) {
			var arr = templates[i].split(':');

			if (arr.length < 3) {
				continue;
			}

			if (arr[1] == linkText) {
				formid = arr[2];
				title = arr[1];
				buttons = arr[3].replace(/%/g, ',').replace(/\$/g, ':');
				break;
			}
		}

		if (formid) {
			this._showDataGridDlg(scope, item, formid, null, title, buttons);
		}
	},
	/**
  * 数据列表通用操作之二：调用REST服务
  *
  * @param {Object} scope    当前作用域
  * @param {Object} item     当前行对象
  */
	_dataGridOperB: function _dataGridOperB(scope, item) {
		var ele = event.srcElement;

		if (!ele) {
			return;
		}

		var linkText = $(ele).text();
		var templates = item.grid.element.attr('template-items').split(',');
		var service, method, tip, msg;

		for (var i = 0; i < templates.length; i++) {
			var arr = templates[i].split(':');

			if (arr.length < 3) {
				continue;
			}

			if (arr[1] == linkText) {
				service = arr[2];

				if (arr[3]) {
					method = arr[3];
				}

				if (arr[4]) {
					tip = arr[4].replace(/RDS_COLON/g, ':').replace(/RDS_COMMA/g, ',');
				}

				if (arr[5]) {
					msg = arr[5];
				}

				break;
			}
		}

		var _this = this;

		if (!GM.Form.parameters.urlParams.ywh && (linkText == '删除' || $(ele).attr('title') == '删除')) {
			if (tip) {
				GM.Alert.show('提示', tip, {
					showCancelButton: true
				}, function () {
					_this._deleteDataGridRecordWithoutBizKey(scope, item);
				});
			} else {
				_this._deleteDataGridRecordWithoutBizKey(scope, item);
			}
		} else if (service) {
			if (tip) {
				GM.Alert.show('提示', tip, {
					showCancelButton: true
				}, function () {
					_this._invokeDataGridREST(scope, item, service, method, msg);
				});
			} else {
				_this._invokeDataGridREST(scope, item, service, method, msg);
			}
		}
	},
	_deleteDataGridRecordWithoutBizKey: function _deleteDataGridRecordWithoutBizKey(scope, item) {
		var $dgid = item.entity.$$dgid;

		if ($dgid) {
			var uiGrid = item.grid.element.attr('ui-grid');
			var prefix = 'list$' + uiGrid.replace('Option', '') + '$';

			for (var key in scope) {
				if (key.indexOf(prefix) === 0) {
					for (var i = 0; i < scope[key].length; i++) {
						if (scope[key][i]['$$dgid'] == $dgid) {
							GM.CommonOper.removeItemFromArray(scope[key], scope[key][i], '$$dgid');
							break;
						}
					}

					if (scope[uiGrid].data) {
						for (var i = 0; i < scope[uiGrid].data.length; i++) {
							if (scope[uiGrid].data[i]['$$dgid'] == $dgid) {
								GM.CommonOper.removeItemFromArray(scope[uiGrid].data, scope[uiGrid].data[i], '$$dgid');
								break;
							}
						}
					}
				}
			}
		}
	},
	/**
  * 数据列表通用操作之一：弹出对话框
  *
  * @param {Object} scope    当前作用域
  * @param {Object} item     当前行对象
  * @param {String} formid   弹出对话框的formid
  * @param {String} url      弹出对话框的url
  * @param {String} title    弹出对话框的标题
  * @param {String} buttons  弹出对话框的按钮
  */
	_showDataGridDlg: function _showDataGridDlg(scope, item, formid, url, title, buttons) {
		GM.Form.FormEngine.clearTemporaryUrlParameters();
		var ds = item.grid.element.attr('data-items');
		var uiGrid = item.grid.element.attr('ui-grid');

		if (uiGrid) {
			uiGrid = uiGrid.replace('Option', '');
		}

		if (!GM.Form.parameters.urlParams.ywh) {
			//如果是新增，需要先从当前作用域中获取已编辑的数据
			var alias;
			var prefix = 'list$' + uiGrid;
			var $dgid = item.entity.$$dgid;

			for (var i = 0; i < GM.Form.parameters.formAlias.length; i++) {
				if (GM.Form.parameters.formAlias[i].formid == formid) {
					alias = GM.Form.parameters.formAlias[i].alias;
					break;
				}
			}

			if (alias) {
				for (var key in alias) {
					if (scope[prefix + '$' + key]) {
						for (var i = 0; i < scope[prefix + '$' + key].length; i++) {
							if (scope[prefix + '$' + key][i]['$$dgid'] == $dgid) {
								scope.result[key] = scope[prefix + '$' + key][i];
							}
						}
					}
				}
			}

			if (!GM.CommonOper.isArrayContainsEle(GM.Form.parameters.savingFormIds, formid)) {
				GM.Form.parameters.savingFormIds.push(formid);
			}

			GM.Form.formScope.showFormDialog(url, formid, title, buttons);
			return;
		}

		//向弹出的对话框作用域传入相关参数
		if (ds) {
			var arr = ds.split(',');

			for (var u = 0; u < arr.length; u++) {
				if (arr[u]) {
					var arrDataItems = arr[u].split(':');

					if (arrDataItems.length === 3) {
						var field = arrDataItems[0];

						if (field.indexOf('*') === 0) {
							field = field.substring(1, field.length);
						}

						GM.Form.parameters.urlParams[arrDataItems[2]] = item.entity[field];

						if (!GM.CommonOper.isArrayContainsEle(GM.Form.parameters.urlTempParams, arrDataItems[2])) {
							GM.Form.parameters.urlTempParams.push(arrDataItems[2]);
						}

						break;
					}
				}
			}
		}

		GM.Form.formScope.showFormDialog(url, formid, title, buttons);
		this._getData(formid, true);
	},
	/**
  * 数据列表通用操作之二：调用REST服务
  *
  * @param {Object} scope    当前作用域
  * @param {Object} item     当前行对象
  * @param {String} service  调用的service
  * @param {String} method   GET 或者 POST等
  * @param {String} msg      调用成功后的提示信息
  */
	_invokeDataGridREST: function _invokeDataGridREST(scope, item, service, method, msg) {
		if (!RESTURL.dg || !RESTURL.dg[service]) {
			GM.Form.CommonOper.showFailureMsg('未配置[' + service + ']服务。');
			return;
		}

		var _this = this;

		var formid = item.grid.element.attr('formid');
		var dataItems = item.grid.element.attr('data-items');
		var ds = item.grid.element.attr('ui-grid');

		if (ds) {
			ds = ds.replace('Option', '');
		}

		var param = {};

		//向弹出的对话框作用域传入相关参数
		if (dataItems) {
			var arr = dataItems.split(',');

			for (var u = 0; u < arr.length; u++) {
				if (arr[u]) {
					var arrDataItems = arr[u].split(':');

					if (arrDataItems.length === 3) {
						var field = arrDataItems[0];

						if (field.indexOf('*') === 0) {
							field = field.substring(1, field.length);
						}

						param[arrDataItems[2]] = item.entity[field];
						break;
					}
				}
			}
		}

		var invoke;

		if (method && method.toUpperCase() == 'POST') {
			invoke = GM.Form.restful.post(window.RESTURL.dg[service], param);
		} else {
			invoke = GM.Form.restful.get(window.RESTURL.dg[service], param);
		}

		invoke.then(function (res) {
			if (res.success === true && !!formid && !!ds) {
				_this._getSingleDataGrid(scope, formid, ds);
			} else if (res.success === false) {
				GM.Form.CommonOper.showFailureMsg(res.message);
			}
		}, function (res) {
			GM.Form.CommonOper.showFailureMsg(res.message);
		});
	},
	/**
  * 将选定的项目相关的信息展示在页面上
  *
  * @param {Object} scope
  * @param {Object} item
  */
	_dataGridSelect: function _dataGridSelect(scope, item) {
		var ds = item.grid.element.attr('ui-grid');

		if (ds) {
			ds = ds.replace('Option', '');
			var paras = scope[ds + 'KeyParas'];

			if (paras && paras.length > 0) {
				for (var u = 0; u < paras.length; u++) {
					GM.Form.parameters.urlParams[paras[u].para] = item.entity[paras[u].key];
				}
			}

			if (scope.gridQueryParams && !!scope.gridQueryParams.queryType && !!scope.gridQueryParams.queryValue) {
				GM.Form.parameters.urlParams.searchType = scope.gridQueryParams.queryType;
				GM.Form.parameters.urlParams.searchValue = scope.gridQueryParams.queryValue;
			}
		}

		this._getData(false, true);
		scope.closeFormDialog();
	},
	/**
  * 刷新单个数据列表
  *
  * @param {Object} scope    当前作用域
  * @param {String} formid   数据列表所在表单formid
  * @param {String} ds       数据列表的数据源别名
  */
	_getSingleDataGrid: function _getSingleDataGrid(scope, formid, ds) {
		if (!GM.Form.parameters.urlParams.ywh) {
			for (var key in scope) {
				if (key.indexOf('list$' + ds) === 0) {
					return;
				}
			}
		}

		var _this = this;
		var para = angular.copy(GM.Form.parameters.urlParams);

		if (!para.ywh) {
			para.ywh = para.did || para.duuid;
		}

		if (scope.gridQueryParams.queryValue) {
			para.queryType = scope.gridQueryParams.queryType;
			para.queryValue = scope.gridQueryParams.queryValue;
			scope.gridQueryParams = {
				queryType: '',
				queryValue: ''
			};
		}

		var up = {
			'up': para
		};

		var param = {
			formids: formid,
			dataSourceName: ds,
			urlParameters: JSON.stringify(up)
		};
		GM.Form.restful.get(GM.Form.parameters.servers.getSingleDataGridServer, param).then(function (res) {
			if (res.success === true && res.data) {
				_this._bindDataGrid(scope, ds, res.data);
			}
		}, function (res) {});
	},
	/**
  * 获取数据列表的相关配置
  *
  * @param {Object} scope            作用域
  * @param {String} ds               数据列表的数据源别名
  * @param {Array} data              数据
  * @param {Array} hd                表头列表
  * @param {String} dataItems        包含标题和字段对应关系的字符串（与hd不能同时为空）
  * @param {String} templateItems    操作列表
  */
	_getGridOption: function _getGridOption(scope, ds, data, hd, dataItems, templateItems) {
		var gridOptionName = ds + 'Option';
		scope[gridOptionName] = {};

		var gridOptions = {};
		var tableGrid = new GM.Form.gmTable();
		var templates = [],
		    heads = [];

		if (templateItems) {
			var arrTemps = templateItems.split(',');
			for (var i = 0; i < arrTemps.length; i++) {
				if (arrTemps[i] != '') {
					var arr = arrTemps[i].split(':');
					templates.push({
						name: arr[1],
						click: arr[0]
					});
				}
			}
		}

		if (dataItems) {
			var arrTemps = dataItems.split(',');
			var gridParas = [];

			for (var i = 0; i < arrTemps.length; i++) {
				if (!!arrTemps[i] && arrTemps[i].indexOf('*') !== 0) {
					var arr = arrTemps[i].split(':');
					heads.push({
						name: arr[0],
						displayName: arr[1],
						enableColumnMenu: false,
						cellTooltip: true
					});

					if (arr.length === 3) {
						gridParas.push({
							key: arr[0],
							para: arr[2]
						});
					}
				}
			}

			if (gridParas.length > 0) {
				scope[ds + 'KeyParas'] = gridParas;
			}
		} else if (hd && hd.length > 0) {
			for (var i = 0; i < hd.length; i++) {
				heads.push({
					name: hd[i],
					displayName: hd[i],
					enableColumnMenu: false,
					cellTooltip: true
				});
			}
		}

		if (templates.length > 0) {
			var strCellTemplate = tableGrid.getCellTemplate(templates);

			heads.push({
				name: 'operate',
				displayName: '操作',
				enableColumnMenu: false,
				cellTemplate: strCellTemplate
			});
		}

		gridOptions.data = data;
		gridOptions.columnDefs = heads;
		scope[gridOptionName] = gridOptions;
		GM.Form.timeout(function () {
			tableGrid.updateSize();
		}, 500);
	},
	_bindDataGrid: function _bindDataGrid(scope, ds, data) {
		var _this = this;

		for (var key in data) {
			if (!!ds && key != ds && key != 'page__' + ds) {
				continue;
			}

			if (key.indexOf('dg__') === 0) {
				if (!(data[key] instanceof Array)) {
					var dgData = data[key];
					var sDataItems = $("div[ui-grid='" + key + "Option']").attr('data-items');
					var sTemplateItems = $("div[ui-grid='" + key + "Option']").attr('template-items');

					_this._getGridOption(scope, key, dgData.data.page.records, dgData.headList, sDataItems, sTemplateItems);
				} else if ((scope.pstyle == 'landtree' || scope.isDataComplex === true) && scope.globalTreeData[key]) {
					scope.globalTreeData[key] = data[key];
					//updateDataGridsBind($scope.focusNode);
				}
			}

			if (key && key.indexOf('page__') === 0) {
				scope[key] = data[key];
			}
		}
	},
	_getDraftData: function _getDraftData(key) {
		GM.Form.restful.get(RESTURL.process.getDraftInfo + '?id=' + key).then(function (response) {
			if (response.success === true && response.data != null) {
				GM.Form.formScope.result = JSON.parse(response.data);
			}
		}, function (response) {});
	},
	_saveDraft: function _saveDraft(key) {
		var param = {
			id: key,
			userId: top.GM.userInfo.loginName,
			appId: top.GM.AppInfo.id,
			processDefId: GM.Form.parameters.urlParams.processDefId,
			processName: GM.Form.parameters.urlParams.processName,
			processKey: GM.Form.parameters.urlParams.processKey
		};

		var ctrl = angular.element("[data-role='bizname']");

		if (ctrl && ctrl > 0) {
			param.bizName = ctrl.val();
		}

		GM.Form.restful.get(RESTURL.process.saveDraftBaseInfo, param).then(function (res) {
			if (res.success === true) {
				param = {
					id: key,
					data: _this._getSavingData()
				};
				GM.Form.restful.post(RESTURL.process.saveDraftInfo, param).then(function (r) {
					if (r.success === true) {
						GM.Form.CommonOper.showSuccessMsg('保存成功！');
					} else {
						GM.Form.CommonOper.showFailureMsg(r.message);
					}
				}, function (r) {
					GM.Form.CommonOper.showFailureMsg(r.message);
				});
			}
		}, function (res) {
			GM.Form.CommonOper.showFailureMsg(res.message);
		});
	},
	/**
  * 初始化页面数据
  *
  * @param {String} formid
  * @param {Boolean} keepOldData 是否保留原始数据
  */
	_getData: function _getData(formid, keepOldData) {
		var _this = this;

		var params = {
			formids: formid || GM.Form.parameters.formIds.join(','),
			urlParameters: JSON.stringify(GM.Form.parameters.urlParams)
		};

		GM.Form.restful.get(GM.Form.parameters.servers.getDataServer, params).then(function (res) {
			if (res.success === false) {
				GM.Form.CommonOper.showFailureMsg('页面加载数据失败：' + res.message);
				return;
			}

			_this._datas.globe = res.data;

			//处理需要从REST服务调用的数据
			Object.keys(res.data).forEach(function (m) {
				if (m.startsWith("svc:")) {
					var sService = m.substring(4, m.length);
					GM.Form.CommonOper.syncAjaxGet(window.RESTURL.fr[sService] + "?ywh=" + GM.Form.parameters.urlParams.ywh).then(function (r) {
						if (r.success === true && r.data) {
							_this._datas.globe[m] = r.data;
						}
					}, function (r) {});
				}
			});

			//console.log(_this._datas.globe);
			//console.log(GM.Form.formScope.result);
			if (!_this._datas.globe.treeKey) {
				if (!keepOldData) {
					GM.Form.formScope.result = _this._datas.globe;
				} else {
					for (var key in GM.Form.formScope.result) {
						if (!_this._datas.globe[key]) {
							continue;
						}

						if (key.indexOf('dg__') < 0) {
							if (!$.isEmptyObject(_this._datas.globe[key]) && $.isEmptyObject(GM.Form.formScope.result[key])) {
								GM.Form.formScope.result[key] = _this._datas.globe[key];
							}
						} else if (key.indexOf('dg__') === 0) {
							if (_this._datas.globe[key].data.page.records.length > 0 && GM.Form.formScope.result[key].data.page.records.length === 0) {
								GM.Form.formScope.result[key] = _this._datas.globe[key];
							}
						} else if (key.indexOf('page__') === 0) {
							if (_this._datas.globe[key].totalItems > 0 && GM.Form.formScope.result[key].totalItems === 0) {
								GM.Form.formScope.result[key] = _this._datas.globe[key];
							}
						}
					}

					for (var key in _this._datas.globe) {
						if (!GM.Form.formScope.result[key] || $.isEmptyObject(GM.Form.formScope.result[key])) {
							GM.Form.formScope.result[key] = _this._datas.globe[key];
						}
					}
				}
			} else {
				for (var key in _this._datas.globe) {
					if (key == 'treeKey' || _this._datas.globe.treeKey[key]) {
						continue;
					}

					GM.Form.formScope.result[key] = _this._datas.globe[key];
				}
			}
			//初始化数据表格与其分页对象
			_this._bindDataGrid(GM.Form.formScope, null, _this._datas.globe);
		}, function (res) {
			GM.Form.CommonOper.showFailureMsg('页面加载数据失败：' + res.message);
		});
	},
	_getSavingData: function _getSavingData() {
		var data;

		if (this._datas.globe.treeKey) {
			//this.updateGlobalData();
			data = angular.copy(this._datas.globe);
		} else {
			data = angular.copy(GM.Form.formScope.result);
		}

		data = JSON.stringify(data);
		return data;
	},
	/**
  * 将作用域中数据列表的临时数据更新到数据列表配置对象数据项中
  *
  * @param {Object} ds       数据列表别名
  */
	_setGridDataWithTempData: function _setGridDataWithTempData(ds) {
		if (!GM.Form.formScope[ds + 'Option']) {
			return;
		}

		var prefix = 'list$' + ds;

		for (var key in GM.Form.formScope) {
			if (key.indexOf(prefix) < 0) {
				continue;
			}

			if (!GM.Form.formScope[ds + 'Option']['data'] || GM.Form.formScope[ds + 'Option']['data'].length === 0) {
				GM.Form.formScope[ds + 'Option']['data'] = [];
			}

			for (var i = 0; i < GM.Form.formScope[key].length; i++) {
				var dgId = GM.Form.formScope[key][i]['$$dgid'];
				var jIndex;

				for (var j = 0; j < GM.Form.formScope[ds + 'Option']['data'].length; j++) {
					if (GM.Form.formScope[ds + 'Option']['data'][j]['$$dgid'] == dgId) {
						jIndex = j;
						break;
					}
				}

				if (jIndex || jIndex === 0) {
					for (var subKey in GM.Form.formScope[key][i]) {
						GM.Form.formScope[ds + 'Option']['data'][jIndex][subKey] = GM.Form.formScope[key][i][subKey];
					}

					jIndex = undefined;
				} else {
					GM.Form.formScope[ds + 'Option']['data'].push(angular.copy(GM.Form.formScope[key][i]));
				}
			}
		}

		GM.Form.parameters.dataGridNewFlag = false;
	},
	_saveDialog: function _saveDialog(ds) {
		if (!GM.Form.formScope.dialogParameter.formid) {
			return;
		}

		var _this = this;

		//如果业务号为空，则数据列表上添加的数据应该先不直接入库
		if (!GM.Form.parameters.urlParams.ywh) {
			//获取对话框表单内的数据对象
			var serverUrl = window.FORM_REST_URL.workflow.getAnalyzedParam + '?formid=' + GM.Form.formScope.dialogParameter.formid;
			GM.Form.CommonOper.syncAjaxGet(serverUrl, function (res) {
				if (res.success === true) {
					var $dgid, bHas;

					for (var i = 0; i < GM.Form.parameters.formAlias.length; i++) {
						if (GM.Form.parameters.formAlias[i].formid == GM.Form.formScope.dialogParameter.formid) {
							GM.Form.parameters.formAlias[i].alias = angular.copy(res.data);
							bHas = true;
							break;
						}
					}

					if (!bHas) {
						GM.Form.parameters.formAlias.push({
							formid: GM.Form.formScope.dialogParameter.formid,
							alias: angular.copy(res.data)
						});
					}

					for (var key in res.data) {
						if (!GM.Form.formScope.result[key]) {
							continue;
						}

						var lkey = 'list$' + ds + '$' + key;

						if (GM.Form.parameters.dataGridNewFlag || !GM.Form.formScope.result[key]['$$dgid']) {
							if (!$dgid) {
								GM.Form.CommonOper.syncAjaxGet(window.FORM_REST_URL.workflow.getUUID, function (r) {
									$dgid = r.data;
								}, function (r) {});
							}

							GM.Form.formScope.result[key]['$$dgid'] = $dgid;

							if (!GM.Form.formScope[lkey]) {
								GM.Form.formScope[lkey] = [];
							}

							if (!GM.Form.formScope.result[key]['ROW_ID']) {
								GM.Form.formScope.result[key]['ROW_ID'] = GM.Form.formScope[lkey].length + 1;
							}

							GM.Form.formScope[lkey].push(angular.copy(GM.Form.formScope.result[key]));
							_this._setGridDataWithTempData(ds);
						} else {
							for (var i = 0; i < GM.Form.formScope[lkey].length; i++) {
								if (GM.Form.formScope[lkey][i]['$$dgid'] == GM.Form.formScope.result[key]['$$dgid']) {
									GM.Form.formScope[lkey][i] = angular.copy(GM.Form.formScope.result[key]);
									_this._setGridDataWithTempData(ds);
									break;
								}
							}
						}
					}
				}
			}, function (res) {});
		} else {
			var paras = {};
			angular.copy(GM.Form.parameters.urlParams, paras);

			if (GM.Form.formScope.dialogParameter.params) {
				for (var key in GM.Form.formScope.dialogParameter.params) {
					paras[key] = GM.Form.formScope.dialogParameter.params[key];
				}
			}

			var param = {
				formids: GM.Form.formScope.dialogParameter.formid,
				urlParameters: JSON.stringify(paras),
				keys: JSON.stringify(_this._datas.saveKeys),
				data: _this._getSavingData()
			};

			GM.Form.CommonOper.syncAjaxPost(GM.Form.parameters.servers.saveDataServer, function (res) {
				if (res.success === true) {
					GM.Form.CommonOper.showSuccessMsg('保存成功');
				} else {
					GM.Form.CommonOper.showFailureMsg(res.message);
				}
			}, function (res) {
				GM.Form.CommonOper.showFailureMsg(res.message);
			}, param);
		}
	},
	/**
  * 将数据列表的临时数据赋予待保存对象中
  */
	_pretreatedSavingData: function _pretreatedSavingData() {
		for (var key in GM.Form.formScope) {
			if (key.indexOf('list$') < 0) {
				continue;
			}

			var arr = key.split('$');

			if (arr.length < 3) {
				continue;
			}

			var alia = arr[2];
			var data = GM.Form.formScope[key];

			for (var subKey in GM.Form.formScope.result) {
				if (alia == subKey) {
					if (!(GM.Form.formScope.result[subKey] instanceof Array)) {
						GM.Form.formScope.result[subKey] = [];
					}

					for (var i = 0; i < data.length; i++) {
						var jIndex = -1;

						for (var j = 0; j < GM.Form.formScope.result[subKey].length; j++) {
							if (GM.Form.formScope.result[subKey][j]['$$dgid'] && GM.Form.formScope.result[subKey][j]['$$dgid'] == data[i]['$$dgid']) {
								jIndex = j;
								break;
							}
						}

						if (jIndex > -1) {
							GM.Form.formScope.result[subKey][j] = data[i];
						} else {
							GM.Form.formScope.result[subKey].push(data[i]);
						}
					}

					break;
				}
			}
		}
	},
	/**
  * 保存数据
  *
  * @param {Boolean} autoHandOver        保存成功之后是否自动移交
  * @param {Function} successCallback    保存成功之后回调
  * @param {Function} failureCallback    保存失败之后回调
  */
	_saveData: function _saveData(autoHandOver, successCallback, failureCallback) {
		var _this = this;

		if (GM.Form.parameters.urlParams.startFlag == '1') {
			_this._pretreatedSavingData();
		}

		var param = {
			urlParameters: JSON.stringify(GM.Form.parameters.urlParams),
			keys: JSON.stringify(_this._datas.saveKeys),
			data: _this._getSavingData()
		};

		if (GM.Form.parameters.savingFormIds && GM.Form.parameters.savingFormIds.length > 0) {
			param.formids = GM.Form.parameters.savingFormIds.join(',');
		} else {
			param.formids = GM.Form.parameters.formIds.join(',');
		}

		GM.Form.CommonOper.syncAjaxPost(GM.Form.parameters.servers.saveDataServer, function (res) {
			if (res.success === true) {
				if (successCallback) {
					successCallback();
					return;
				}

				if (autoHandOver === true) {
					GM.Form.formScope.handOver();
				} else {
					GM.Form.CommonOper.showSuccessMsg('保存成功');
				}
			} else {
				if (failureCallback) {
					failureCallback();
				}

				GM.Form.CommonOper.showFailureMsg(res.message);
			}
		}, function (res) {
			if (failureCallback) {
				failureCallback();
			}

			GM.Form.CommonOper.showFailureMsg(res.message);
		}, param);
	}
};

'use strict';

require('formCommonApi');

require('formWorkflow');

require('formData');

require('formWebgis');

GM.Form.formScope = undefined; //angular当前SCOPE
GM.Form.restful = undefined; //RESTful服务调用对象
GM.Form.validationObject = undefined; //页面
GM.Form.ngDialog = undefined; //对话框组件
GM.Form.gmTable = undefined; //数据列表控件
GM.Form.filter = undefined; //过滤器对象
GM.Form.timeout = undefined; //定时器对象
//表单参数
GM.Form.parameters = {
    appId: undefined, //APP ID
    serviceAppKey: undefined, //APP标识，由前文传入，非当前登录信息所述
    urlParams: undefined, //页面参数
    urlTempParams: [], //在页面弹出的对话框上传入的临时参数列表
    userInfo: {
        realName: top.GM.userInfo == null ? '' : top.GM.userInfo.realName,
        loginName: top.GM.userInfo == null ? '' : top.GM.userInfo.loginName
    }, //用户登录信息
    dataLoaded: false, //数据是否已加载
    formValid: true, //数据验证是否通过
    formIds: [], //表单ID列表
    savingFormIds: [], //待保存表单ID
    saveLayerName: undefined, //不为空，则在保存之前保存图形坐标数据
    servers: {
        getDataServer: undefined, //获取数据服务接口地址
        saveDataServer: undefined, //保存数据服务接口地址
        getHandOverVarServer: undefined, //移交判断服务接口地址
        updateBizFlowInfoServer: undefined, //在业务库中保存流程流转信息的拷贝
        getSingleDataGridServer: undefined //获取单个数据列表数据源
    },
    formDataSourceNames: [], //当前表单及所涉及（如弹出框）的所有表单涉及到的所有数据源别名
    dataGridNewFlag: false, //表单内数据列表是否正在新增记录
    formAlias: [] //表单ID与表单别名的存储
};

/**
 * 流程表单引擎类
 * @class GM.Form.FormEngine
 */
GM.Form.FormEngine = {
    _functions: {
        beforeGetData: [], //提取数据之前
        afterGetData: [], //获取数据之后
        pageLoaded: [] //页面加载之后
    }, //各阶段额外执行的操作
    _initScopeVar: function _initScopeVar() {
        var _this = this;

        GM.Form.formScope.settings = {
            layoutUrl: undefined, //表单组织形式
            forms: [], //表单
            tabs: [], //表单配置
            prints: [], //打印列表
            buttons: [], //底部按钮列表
            topUrl: undefined, //头部功能区Url
            showBottom: false, //底部功能区是否展示
            loadWebGIS: false //是否加载地块管理等webgis功能
        }; //表单整体框架设置

        GM.Form.formScope.draftInfo = {
            key: undefined
        }; //草稿信息

        GM.Form.formScope.dataLoaded = false;
        GM.Form.formScope.hasInitCalcs = false;
    },
    /**
     * 入口。初始化全局变量，scope上下文等
     * @param {Object} scope    表单作用域
     * @param {Object} r        调用RESTful服务对象
     * @param {Object} v        控件验证对象
     * @param {Object} d        对话框组件
     * @param {Object} g        数据列表组件
     */
    init: function init(scope, r, v, f, d, g, t) {
        var _this = this;
        GM.Form.parameters.urlParams = GM.LayoutOper.getURLParams(window.location.href);
        GM.Form.parameters.appId = GM.Form.parameters.urlParams.appId;
        GM.Form.parameters.serviceAppKey = GM.Form.parameters.urlParams.serviceAppKey;

        if (GM.Form.parameters.urlParams.serviceAppKey) {
            for (var key in GM.Form.parameters.servers) {
                GM.Form.parameters.servers[key] = _this._getFormServiceUrl(GM.Form.parameters.urlParams.serviceAppKey) + FORM_REST_URL.form[key];
            }
        }

        GM.Form.formScope = scope;
        GM.Form.restful = r;
        GM.Form.validationObject = v;
        GM.Form.filter = f;
        GM.Form.ngDialog = d;
        GM.Form.gmTable = g;
        GM.Form.timeout = t;

        this._initScopeVar();

        if (!GM.Form.formScope) {
            this.showFailureMsg('无法初始化上下文！');
            return;
        }

        if (!top.GM.userInfo) {
            this.showFailureMsg('无法获取登录用户信息！');
            return;
        }

        this._loadPage();
        this._bindEvents();
        this._doAfterLoadPage(GM.Form.formScope);
    },
    getAppServerUrl: function getAppServerUrl(appKey) {
        var url = this._getFormServiceUrl(appKey || GM.Form.parameters.urlParams.serviceAppKey);

        if (url) {
            if (url.substring(url.length - 1, url.length) == '/') {
                url = url.substring(0, url.length - 1);
            }

            return url.substring(0, url.lastIndexOf('/'));
        }

        return undefined;
    },
    clearTemporaryUrlParameters: function clearTemporaryUrlParameters() {
        if (GM.Form.parameters.urlTempParams.length > 0) {
            for (var i = 0; i < GM.Form.parameters.urlTempParams.length; i++) {
                if (GM.Form.parameters.urlParams[GM.Form.parameters.urlTempParams[i]]) {
                    GM.Form.parameters.urlParams[GM.Form.parameters.urlTempParams[i]] = undefined;
                }
            }
        }
    },
    /**
     * 在0.5秒后将重命名所有切换页，防止重名导致错误
     */
    _doAfterLoadPage: function _doAfterLoadPage(scope) {
        var _this = this;

        GM.Form.timeout(function () {
            $('.nav li a[data-target]').each(function () {
                var tabId = $(this).attr('data-target').replace('#', '');
                var newTabId = GM.Form.CommonOper.getUUID();
                var tabDiv = $(this).parent('li').parent('ul').parent('div').next('.div-content').find("div[id='" + tabId + "']");

                if (tabDiv.length > 0) {
                    tabDiv.attr('id', newTabId);
                    $(this).attr('data-target', '#' + newTabId);
                }
            });

            if (scope.hasInitCalcs === false && angular.element('[calc-exp]').length > 0) {
                _this._watchCalcExp(scope);
                scope.hasInitCalcs = true;
            }
        }, 500);
    },
    _getFormServiceUrl: function _getFormServiceUrl(appKey) {
        var result = null;

        //然后取前端配置
        if (result == null) {
            try {
                for (var ii = 0; ii < CONFIG_JSON.length; ii++) {
                    if (appKey === CONFIG_JSON[ii].app) {
                        result = CONFIG_JSON[ii].value + 'form/';
                        break;
                    }
                }
            } catch (ex) {
                //调试环境取调试配置
                result = FORM_SERVER;
                console.log(ex);
            }
        }

        return result;
    },
    /**
     * 绑定ng事件
     */
    _bindEvents: function _bindEvents() {
        var _this = this;

        GM.Form.formScope.dateAdd = function (dateStr, span) {
            if (isNaN(span)) {
                return '';
            }

            span = parseFloat(span);

            try {
                var a = new Date(dateStr);
                a.setDate(a.getDate() + span);
                var year = a.getFullYear().toString();
                var month = a.getMonth() + 1;

                if (month < 10) {
                    month = '0' + month;
                } else {
                    month = month.toString();
                }

                var day = a.getDate();

                if (day < 10) {
                    day = '0' + day;
                } else {
                    day = day.toString();
                }

                return year + '-' + month + '-' + day;
            } catch (ex) {}

            return '';
        };

        GM.Form.formScope.dateDiff = function (date1, date2) {
            try {
                var a = new Date(date1),
                    b = new Date(date2);
                return (b - a) / (1000 * 3600 * 24);
            } catch (ex) {}
        };

        GM.Form.formScope.invokePlugin = function (validScope, name) {
            _this._clickEvent(validScope, name);
        };

        GM.Form.formScope.treeConfig = {
            'activeNode': 'treeEvent'
        };

        GM.Form.formScope.treeEvent = function (node) {
            _this._treeConfig(GM.Form.formScope, node);
        };

        GM.Form.formScope.goBack = function () {
            top.goPendingPage(GM.Form.parameters.urlParams.box);
        };

        //打印
        GM.Form.formScope.print = function (code, para) {
            _this._print(para || GM.Form.formScope.taskInfo.bizKey, code, top.GM.AppInfo.id);
        };

        //弹出对话框
        GM.Form.formScope.showFormDialog = function (url, formid, name, buttons, clearParams) {
            if (clearParams) {
                _this.clearTemporaryUrlParameters();
            }
            _this._showFormDialog(GM.Form.formScope, url, formid, name, buttons);
        };

        GM.Form.formScope.closeFormDialog = function () {
            GM.Form.Dialog.close();
        };

        GM.Form.formScope.formLoaded = function () {
            _this._formLoaded(GM.Form.formScope);
        };

        GM.Form.formScope.isCbChecked = function (a, b) {
            return _this._isCheckBoxChecked(GM.Form.formScope, a, b);
        };

        GM.Form.formScope.cbCheck = function (e, a, b) {
            _this._cbCheck(GM.Form.formScope, e, a, b);
        };

        GM.Form.formScope.dialogButtonClick = function (s) {
            var arr = s.split(';');

            for (var i = 0; i < arr.length; i++) {
                if (!arr[i]) {
                    continue;
                }

                var ss = arr[i];
                var c = ss.substring(1, 1);

                if (c != ';' && c != "'" && c != '"') {
                    c = 'GM.Form.formScope.';
                }

                ss = c + ss;
                ss = ss.replace(/RDS_L_PAR/g, '(').replace(/RDS_R_PAR/g, ')').replace(/RDS_COLON/g, ':').replace(/RDS_COMMA/g, ',').replace(/RDS_S_QUOTATION/g, "'").replace(/RDS_D_QUOTATION/g, '"');

                eval(ss);
            }
        };

        //自动计算
        GM.Form.formScope.$watch('dataLoaded', function () {
            if (GM.Form.formScope.dataLoaded === true && GM.Form.formScope.hasInitCalcs === false && angular.element('[calc-exp]').length > 0) {
                _this._watchCalcExp(GM.Form.formScope);
                GM.Form.formScope.hasInitCalcs = true;
            }
        });
    },
    _watchCalcExp: function _watchCalcExp(scope) {
        var _this = this;
        //当前用户名
        angular.element(":text[calc-exp='user'][ng-model],textarea[calc-exp='user'][ng-model]").each(function () {
            var sUserNgModel = $(this).attr('ng-model');

            scope.$watch(sUserNgModel, function () {
                var vCur;

                try {
                    vCur = eval('GM.Form.formScope.' + sUserNgModel);
                } catch (e) {}

                if (!vCur || vCur == '') {
                    GM.Form.CommonOper.setObjectAttrVal(scope, sUserNgModel, 0, top.GM.userInfo.realName);
                }
            });
        });

        //当前用户登录名
        angular.element(":text[calc-exp='userid'][ng-model],textarea[calc-exp='userid'][ng-model]").each(function () {
            var sUserNgModel = $(this).attr('ng-model');

            scope.$watch(sUserNgModel, function () {
                var vCur;

                try {
                    vCur = eval('GM.Form.formScope.' + sUserNgModel);
                } catch (e) {}

                if (!vCur || vCur == '') {
                    GM.Form.CommonOper.setObjectAttrVal(scope, sUserNgModel, 0, top.GM.userInfo.loginName);
                }
            });
        });

        //当前用户登录名
        angular.element(":text[calc-exp='org'][ng-model],textarea[calc-exp='org'][ng-model]").each(function () {
            var sUserNgModel = $(this).attr('ng-model');

            scope.$watch(sUserNgModel, function () {
                var vCur;

                try {
                    vCur = eval('GM.Form.formScope.' + sUserNgModel);
                } catch (e) {}

                if (!vCur || vCur == '') {
                    if (top.GM.userInfo.organizations.length > 0) {
                        var orgs = [];

                        for (var i = 0; i < top.GM.userInfo.organizations.length; i++) {
                            orgs.push(top.GM.userInfo.organizations[i].name);
                        }
                    }
                    GM.Form.CommonOper.setObjectAttrVal(scope, sUserNgModel, 0, orgs.join(','));
                }
            });
        });

        //当日日期
        angular.element(":text[calc-exp='sysdate'][ng-model],textarea[calc-exp='sysdate'][ng-model]").each(function () {
            var sUserNgModel = $(this).attr('ng-model');

            scope.$watch(sUserNgModel, function () {
                var vCur;

                try {
                    vCur = eval('GM.Form.formScope.' + sUserNgModel);
                } catch (e) {}

                if (!vCur || vCur == '') {
                    var dt = new Date();
                    var vYear = dt.getFullYear();
                    var vMonth = (dt.getMonth() < 9 ? '0' : '') + (dt.getMonth() + 1);
                    var vDate = (dt.getDate() < 10 ? '0' : '') + dt.getDate();
                    var vSysDate = vYear + '-' + vMonth + '-' + vDate;

                    GM.Form.CommonOper.setObjectAttrVal(scope, sUserNgModel, 0, vSysDate);
                }
            });
        });

        if (angular.element("[calc-exp][calc-exp!='user'][calc-exp!='userid'][calc-exp!='sysdate']").length > 0) {
            var arrCalcOpers = ['+', '-', '*', '/', '^', '%', '(', ')', ','];
            angular.element("[calc-exp][calc-exp!='user'][calc-exp!='userid'][calc-exp!='sysdate']").each(function () {
                var sCalcExp = $(this).attr('calc-exp');
                var isCn = sCalcExp.indexOf('cn(') === 0;

                if (isCn) {
                    sCalcExp = sCalcExp.substring(3, sCalcExp.length - 1);
                }

                var sModel = $(this).attr('ng-model');
                var sDigits = $(this).attr('digits');
                var arrEleBinds = [];
                var curEleBind = '';
                var sNewCalcExp = '';

                for (var i = 0; i < sCalcExp.length; i++) {
                    if (arrCalcOpers.containsObj(sCalcExp[i]) || i === sCalcExp.length - 1) {
                        if (curEleBind != '') {
                            if (i === sCalcExp.length - 1) {
                                curEleBind += sCalcExp[i];
                            }

                            arrEleBinds.push(curEleBind.replace(')', ''));
                            curEleBind = '';
                        }

                        sNewCalcExp += sCalcExp[i];

                        if (i < sCalcExp.length - 1 && !arrCalcOpers.containsObj(sCalcExp[i + 1]) && (sCalcExp[i + 1] < '0' || sCalcExp[i + 1] > '9')) {
                            sNewCalcExp += 'GM.Form.formScope.';
                        }
                    } else {
                        if (i === 0 && (sCalcExp[i] < '0' || sCalcExp[i] > '9')) {
                            sNewCalcExp = 'GM.Form.formScope.';
                        }

                        curEleBind += sCalcExp[i];
                        sNewCalcExp += sCalcExp[i];
                    }
                }

                for (var i = 0; i < arrEleBinds.length; i++) {
                    scope.$watch(arrEleBinds[i], function () {
                        var curVal;
                        var curSign = '';
                        var sLastExp = '';
                        var isString = false;

                        if (sNewCalcExp.indexOf('GM.Form.formScope.dateAdd') === 0 || sNewCalcExp.indexOf('GM.Form.formScope.dateDiff') === 0) {
                            isString = true;
                            sLastExp = sNewCalcExp;
                        } else {
                            for (var uu = 0; uu < sNewCalcExp.length; uu++) {
                                if (arrCalcOpers.containsObj(sNewCalcExp[uu]) || uu === sNewCalcExp.length - 1) {
                                    if (uu === sNewCalcExp.length - 1) {
                                        curSign += sNewCalcExp[uu];
                                    }

                                    if (curSign != '') {
                                        try {
                                            curVal = eval(curSign);
                                        } catch (e) {
                                            curVal = undefined;
                                        }

                                        var vLastOper = uu === sNewCalcExp.length - 1 ? '' : sNewCalcExp[uu];

                                        if (curVal) {
                                            if (!isNaN(curVal)) {
                                                sLastExp += parseFloat(curVal).toFixed(6) + vLastOper;
                                            } else {
                                                sLastExp += curVal;

                                                if (isString === false) {
                                                    isString = true;
                                                }
                                            }
                                        } else {
                                            sLastExp += '0' + vLastOper;
                                        }

                                        curSign = '';
                                    } else {
                                        sLastExp += sNewCalcExp[uu];
                                    }
                                } else {
                                    curSign += sNewCalcExp[uu];
                                }
                            }
                        }

                        try {
                            if (isString === true) {
                                if (sLastExp.indexOf('GM.Form.formScope.dateAdd') === 0 || sLastExp.indexOf('GM.Form.formScope.dateDiff') === 0) {
                                    GM.Form.CommonOper.setObjectAttrVal(scope, sModel, 0, eval(sLastExp));
                                } else {
                                    GM.Form.CommonOper.setObjectAttrVal(scope, sModel, 0, sLastExp);
                                }
                            } else {
                                if (sDigits && sDigits != '') {
                                    sLastExp = '(' + sLastExp + ').toFixed(' + sDigits + ').trimNumber()';
                                }

                                if (isCn) {
                                    eval('GM.Form.formScope.' + sModel + "='" + formCommonOper.getCnFormatMoney(eval(sLastExp)) + "'");
                                } else {
                                    try {
                                        var vCalResult = eval(sLastExp);

                                        if (vCalResult == 'NaN') {
                                            vCalResult = '';
                                        }

                                        GM.Form.CommonOper.setObjectAttrVal(scope, sModel, 0, vCalResult);
                                    } catch (e) {}
                                }
                            }
                        } catch (ee) {}
                    });
                }
            });
        }
    },
    _cbCheck: function _cbCheck(scope, e, a, b) {
        if (!b || b == '') {
            return;
        }

        var bChecked = angular.element(e.target).prop('checked');

        var arr = a.split('.');
        var obj = scope;

        for (var i = 0; i < arr.length; i++) {
            if (bChecked && !obj[arr[i]]) {
                obj[arr[i]] = i < arr.length - 1 ? {} : b;
            } else {
                if (i == arr.length - 1) {
                    var result = obj[arr[i]].split(',');

                    if (bChecked && !GM.CommonOper.isArrayContainsEle(result, b)) {
                        result.push(b);
                    } else if (!bChecked && GM.CommonOper.isArrayContainsEle(result, b)) {
                        GM.CommonOper.removeItemFromArray(result, b);
                    }

                    obj[arr[i]] = result.join(',');
                }
            }

            obj = obj[arr[i]];
        }
    },
    /**
     * 表单在页面渲染之后执行的操作
     *
     * @param {Object} scope
     */
    _formLoaded: function _formLoaded(scope) {
        //初始化表单上各个数据列表的表头
        var arrDataGridDataSources = [];
        angular.element('[ui-grid]').each(function () {
            arrDataGridDataSources.push(angular.element(this).attr('ui-grid').replace('Option', ''));
        });

        GM.Form.FormData._initOptions(scope, arrDataGridDataSources);
    },
    /**
     * 判断checkbox列表中的单个checkbox是否被选中
     *
     * @param {String} a
     * @param {String} b
     */
    _isCheckBoxChecked: function _isCheckBoxChecked(scope, a, b) {
        if (!a || a == '' || !b || b == '') {
            return false;
        }

        var arr = a.split('.');
        var obj = scope;

        for (var i = 0; i < arr.length; i++) {
            if (!obj[arr[i]]) {
                return false;
            }

            obj = obj[arr[i]];
        }

        if (!obj || obj == '') {
            return false;
        }

        var arrVals = obj.split(',');
        return GM.CommonOper.isArrayContainsEle(arrVals, b);
    },
    /**
     * 在表单框架内弹出对话框
     * @param {String} scope    父scope
     * @param {String} url      对话框内表单的URL
     * @param {String} formid   对话框内表单的ID
     * @param {String} name     对话框内表单的名称
     * @param {String} buttons  对话框内表单的底部按钮
     */
    _showFormDialog: function _showFormDialog(scope, url, formid, name, buttons) {
        if (!scope.initedDlgFormIds) {
            scope.initedDlgFormIds = [];
        }

        if (!GM.CommonOper.isArrayContainsEle(scope.initedDlgFormIds, formid)) {
            scope.initDataGridOptions(formid);
        } else {
            scope.initedDlgFormIds.push(formid);
        }

        if (!scope.formInfos) {
            scope.formInfos = [];
        }

        if (!url) {
            for (var i = 0; i < scope.formInfos.length; i++) {
                if (scope.formInfos[i].formid == formid) {
                    url = scope.formInfos[i].url;
                    break;
                }
            }

            if (!url) {
                GM.Form.CommonOper.syncAjaxGet(window.FORM_REST_URL.workflow.getFormInfoById + '?formid=' + formid, function (res) {
                    if (res.success === true && res.data) {
                        url = 'views/forms/' + res.data.dir;
                        scope.formInfos.push({
                            formid: formid,
                            url: url
                        });
                    }
                }, function (res) {});
            }
        }

        if (!!url && url.toLowerCase().indexOf('views/forms') < 0) {
            url = 'views/forms/' + url;
        }

        var dialogPara = {
            formid: formid,
            title: name,
            url: url
        };

        if (buttons) {
            var arrBtns = buttons.split(',');
            var formBtns = [];

            for (var i = 0; i < arrBtns.length; i++) {
                var arrSBtn = arrBtns[i].split(':');
                formBtns.push({
                    name: arrSBtn[0],
                    click: arrSBtn[1],
                    id: arrSBtn[2]
                });
            }

            dialogPara.buttons = formBtns;
        }

        scope.dialogParameter = dialogPara;

        GM.Form.Dialog = GM.Form.ngDialog.open({
            template: require('../views/model/dialog.html'),
            scope: scope,
            plain: true
        });
    },
    /**
     * 打印
     * @param {String} param    打印参数（默认案卷编号）
     * @param {String} code     FastReport模板在WCF配置中对应的code
     * @param {String} appId    业务系统ID
     */
    _print: function _print(param, code, appId) {
        var printCfgUrl = window.FORM_REST_URL.workflow.printConfigUrl + '?appid=' + appId;
        GM.Form.CommonOper.syncAjaxGet(printCfgUrl, function (res) {
            if (res.success === true && res.data && res.data.length > 0) {
                printCfgUrl = 'http://' + res.data[0].ftpHost + ':' + res.data[0].ftpPort + '/' + res.data[0].ftpApp + '/';
            }
        }, function (res) {});

        var params = {
            code: code,
            appid: appId,
            param: param
        };

        var url = window.FORM_REST_URL.workflow.print;

        GM.Form.restful.post(url, params).then(function (res) {
            if (res.success === true && res.data) {
                var results = res.data.split(';');

                for (var i = 0; i < results.length; i++) {
                    if (results[i] !== '') {
                        var r = results[i].replace(/\\/g, '/');

                        if (r.indexOf('./') === 0) {
                            r = r.substring(1, r.length);
                        } else if (r.indexOf('/') !== 0) {
                            r = '/' + r;
                        }

                        top.window.open(printCfgUrl + r);
                    }
                }
            }
        });
    },
    _treeConfig: function _treeConfig(scope, node) {
        scope.treeFormKey = pNode.formid + ';' + pNode.sxh + ';' + pNode.subtype;
    },
    _clickEvent: function _clickEvent(validScope, name) {
        var _this = this;
        var names = name.split(',');

        if (name == 'goBack') {
            _this._doClick(names);
        } else {
            top.GM.LayoutOper.validateForm(GM.Form.validationObject, validScope.allForms, function () {
                GM.Form.formScope.settings.formValid = true;
                _this._doClick(names);
            }, function () {
                GM.Form.formScope.settings.formValid = false;
                GM.Form.CommonOper.showFailureMsg('表单验证不通过，请先根据页面提示进行修改');
            });
        }
    },
    _doClick: function _doClick(names) {
        if (names.length === 0) {
            return;
        }

        eval('GM.Form.formScope.' + names[0] + '()');
        names.splice(0, 1);
        this._doClick(names);
    },
    _pushFormId: function _pushFormId(formid) {
        GM.CommonOper.addUniqueEle(GM.Form.parameters.formIds, formid);
    },
    _pushSavingFormId: function _pushSavingFormId(formid) {
        GM.CommonOper.addUniqueEle(GM.Form.parameters.savingFormIds, formid);
    },
    _getPages: function _getPages(tabs) {
        var pages = [];
        this._retrievePages(pages, tabs);
        return pages;
    },
    _retrievePages: function _retrievePages(pages, tabs) {
        for (var kk = 0; kk < tabs.length; kk++) {
            if (tabs[kk].children) {
                this._retrievePages(pages, tabs[kk].children);
            } else {
                if (!!tabs[kk].formid && tabs[kk].formid != 'map') {
                    this._pushFormId(tabs[kk].formid);

                    if (tabs[kk].save === true || GM.LayoutOper.getParamsValue(tabs[kk].url, 'readonly') !== 'true') {
                        this._pushSavingFormId(tabs[kk].formid);
                    }
                }

                pages.push(tabs[kk]);
            }
        }
    },
    _getLayoutUrl: function _getLayoutUrl(pstyle) {
        pstyle = pstyle || 'tab';

        if (pstyle.indexOf('url:') === 0) {
            return pstyle.substring(4, pstyle.length);
        }

        return STATIC_RESOURCES_PATH + 'dist/core-views/layout/' + pstyle + '.html';
    },
    _loadPage: function _loadPage() {
        var _this = this;
        GM.Form.Workflow.init();

        if (GM.Form.parameters.urlParams.formid) {
            GM.Form.formScope.settings.layoutUrl = _this._getLayoutUrl('raw');
            _this._pushFormId(GM.Form.parameters.urlParams.formid);
            GM.Form.parameters.dataLoaded = true;
        } else {
            var sIndexJsonUrl = window.FORM_REST_URL.workflow.getTaskConfig + "?processDefId=" + GM.Form.parameters.urlParams.processDefId + "&taskKey=";

            //获取节点定义json文件路径
            if (!GM.Form.formScope.taskInfo.bizKey) {
                sIndexJsonUrl += encodeURIComponent("起草");
                GM.Form.formScope.settings.showBottom = true;
            } else {
                GM.Form.formScope.settings.topUrl = STATIC_RESOURCES_PATH + '/dist/core-views/layout/top.html';
                sIndexJsonUrl += encodeURIComponent(GM.Form.formScope.taskInfo.taskKey);
                GM.Form.formScope.settings.showBottom = GM.Form.formScope.taskInfo.name != '查看';
            }

            GM.Form.CommonOper.syncAjaxGet(sIndexJsonUrl, function (res) {
                if (res.success === true && res.data) {
                    GM.Form.formScope.settings.layoutUrl = _this._getLayoutUrl(!!res.data.style ? res.data.style : 'tab');
                    GM.Form.formScope.settings.tabs = res.data.forms;
                    GM.Form.formScope.settings.forms = _this._getPages(res.data.forms);
                    GM.Form.formScope.settings.buttons = res.data.buttons;
                    GM.Form.formScope.settings.prints = res.data.prints;

                    if (res.data.webgis) {
                        GM.Form.formScope.settings.loadWebGIS = !!res.data.webgis;
                    }

                    if (GM.Form.formScope.settings.forms && GM.Form.formScope.settings.forms.length > 0) {
                        for (var i = 0; i < GM.Form.formScope.settings.forms.length; i++) {
                            if (GM.Form.formScope.settings.forms[i].formid) {
                                _this._pushFormId(GM.Form.formScope.settings.forms[i].formid);

                                if (GM.Form.formScope.settings.forms[i].save === true) {
                                    _this._pushSavingFormId(GM.Form.formScope.settings.forms[i].formid);
                                }
                            }
                        }
                    }
                }
            }, function (res) {});
        }
    }
};

'use strict';

window.formWebgis = {
    init: function init() {
        GM.Form.formScope.graphStat = {};
    },
    savePlots: function savePlots(ywh, payload, asDraft) {
        var strData = JSON.stringify(payload);
        var saveData = {
            ywh: ywh,
            strParams: strData,
            asDraft: asDraft === true ? '1' : '0'
        };

        return GM.Form.restful.post(RESTURL.graph.savePlots, null, saveData);
    },
    saveGraphStatics: function saveGraphStatics() {
        if (!GM.Form.parameters.saveLayerName) {
            return;
        }

        var polygon = [],
            savePolygon = [];
        var param = {
            layerName: GM.Form.parameters.saveLayerName
        };

        var status = false;
        var strObjectName = null;

        if (GM.Form.formScope.graphStat.hasOwnProperty('polygon')) {
            polygon = GM.Form.formScope.graphStat.polygon;
            status = GM.GIS.ExternalApi.saveJXPolygons(polygon, GM.Form.formScope);
            strObjectName = GM.GIS.ExternalApi.getJhObjectIDName();
        }

        if (status === false) {
            return false;
        }

        if (GM.Form.formScope.graphStat.statistics) {
            param.statistics = GM.Form.formScope.graphStat.statistics;
        }

        for (var p = 0; p < polygon.length; p++) {
            var vObjectId = polygon[p].feature && polygon[p].feature.get(strObjectName) ? polygon[p].feature.get(strObjectName) : undefined;

            vObjectId = vObjectId && vObjectId != '' ? vObjectId : polygon[p].objectid;

            savePolygon.push({
                polygonNum: polygon[p].polygonNum,
                area: polygon[p].area,
                name: polygon[p].name,
                strGeom: polygon[p].strGeom,
                objectid: vObjectId
            });
        }

        param.polygon = savePolygon;
        GM.Form.formScope.saveGraphParam = param;

        return true;
    }
};

'use strict';

require('formCommonApi');

require('agencyService');

/**
 * 流程扭转类
 * @class GM.Form.FormData
 */
GM.Form.Workflow = {
    init: function init() {
        var _this = this;

        GM.Form.formScope.taskInfo = {
            bizKey: GM.Form.parameters.urlParams.ywh,
            draftBizKey: GM.Form.parameters.urlParams.did || GM.Form.parameters.urlParams.duuid, //草稿业务号
            processDefId: GM.Form.parameters.urlParams.processDefId,
            processKey: GM.Form.parameters.urlParams.processKey, //流程标识
            processName: GM.Form.parameters.urlParams.processName,
            taskKey: GM.Form.parameters.urlParams.taskKey, //节点名称
            isClaimed: false, //是否已被签收
            bizName: GM.Form.parameters.urlParams.caseTitle, //业务名称
            startTime: undefined, //接收时间
            forbid: GM.Form.parameters.urlParams.forbid == '1', //是否已被禁止
            boxName: GM.Form.parameters.urlParams.box || 'agency'
        }; //当前节点信息

        if (GM.Form.parameters.urlParams.ywh) {
            var url = window.FORM_REST_URL.workflow.getProcessInstanceByBizKey + '?bizKey=' + GM.Form.parameters.urlParams.ywh;
            GM.Form.restful.get(url).then(function (res) {
                if (res.success === true && res.data) {
                    GM.Form.formScope.taskInfo.startTime = res.data.startTime;
                }
            }, function (res) {});
        }

        GM.Form.formScope.handOver = function () {
            window.Dialog = GM.Form.ngDialog.open({
                template: STATIC_RESOURCES_PATH + '/dist/core-views/handOver.html',
                scope: GM.Form.formScope
            });
        };

        GM.Form.formScope.startProcessAndHandOver = function () {
            GM.Form.formScope.startProcess(true, true);
        };

        GM.Form.formScope.startProcess = function (autoHandOver, notTurnToFirst) {
            _this._startProcess(autoHandOver, notTurnToFirst);
        };

        //签收或放弃签收
        GM.Form.formScope.claimOrUnclaim = function (status) {
            _this._claimOrUnclaim(status);
        };

        //查看流程图
        GM.Form.formScope.showWfGraph = function () {
            GM.Form.formScope.blankTitle = '流程图';
            GM.Form.formScope.htmlContent = '<img src="' + FORM_REST_URL.workflow.getHighLightTask + '?bizKey=' + GM.Form.formScope.taskInfo.bizKey + '" style="margin-top:30px;"/>';
            //GM.Form.formScope.htmlContent = '<img src="http://192.168.5.27:8080/activiti-web/usertask/getHighLightTask?bizKey=Z201803230005"/>';
            Dialog = GM.Form.ngDialog.open({
                template: STATIC_RESOURCES_PATH + '/dist/core-views/model/blank.html',
                scope: GM.Form.formScope
            });
        };

        _this._isTaskClaimed();
        _this._initBizFlowDetails();
    },
    _claimOrUnclaim: function _claimOrUnclaim(status) {
        var alertMessage, myUrl, erroeMessage;
        if (status === 'claim') {
            alertMessage = '确认签收？';
            myUrl = FORM_REST_URL.workflow.claimTask;
            erroeMessage = '签收失败';
        } else {
            alertMessage = '确认放弃？';
            myUrl = FORM_REST_URL.workflow.unclaimTask;
            erroeMessage = '放弃签收失败';
        }
        GM.Alert.show(alertMessage, '', null, function () {
            GM.Form.restful.get(myUrl, {
                bizKey: GM.Form.formScope.taskInfo.bizKey,
                taskKey: GM.Form.formScope.taskInfo.taskKey,
                userId: GM.Form.parameters.userInfo.loginName
            }).then(function (res) {
                if (res.success === true) {
                    var str = '';
                    for (var key in GM.Form.parameters.urlParams) {
                        str += '&' + key + '=' + GM.Form.parameters.urlParams[key];
                    }
                    window.location = str.replace(/&/, '?') + '&taskKey=' + GM.Form.formScope.taskInfo.taskKey;
                } else {
                    GM.Infomation.show(erroeMessage, {
                        type: 'warning'
                    });
                }
            }, function (error) {
                GM.Infomation.show(error.message, {
                    type: 'warning'
                });
            });
        });
    },
    _startProcess: function _startProcess(autoHandOver, notTurnToFirst) {
        var _this = this;
        var ctrlCaseTitle = $("[data-role='bizname']");

        if (!ctrlCaseTitle || ctrlCaseTitle.length === 0) {
            GM.Form.CommonOper.showFailureMsg('缺少用于案卷标题的控件！');
            return;
        }

        ctrlCaseTitle = ctrlCaseTitle.val();

        if (!ctrlCaseTitle || ctrlCaseTitle == '') {
            GM.Form.CommonOper.showFailureMsg('案卷标题不能为空！');
            return;
        }

        var param = {
            appId: GM.Form.parameters.appId,
            userId: GM.Form.parameters.userInfo.loginName,
            processDefId: GM.Form.parameters.urlParams.processDefId,
            title: ctrlCaseTitle
        };

        if (GM.Form.formScope.taskInfo.draftBizKey) {
            param.draftKey = GM.Form.formScope.taskInfo.draftBizKey;
        }

        //设置业务箱内的附加的业务数据
        var ctrlExtraBizDatas = angular.element("[data-role='biz-data']");
        var extraBizData;

        if (ctrlExtraBizDatas.length > 0) {
            for (var y = 0; y < ctrlExtraBizDatas.length; y++) {
                var sModel = $(ctrlExtraBizDatas[y].attr('ng-model'));

                if (sModel) {
                    var arr = sModel.split('.');

                    if (arr.length > 0) {
                        if (!extraBizData) {
                            extraBizData = {};
                        }

                        extraBizData[arr[arr.length - 1]] = eval('GM.Form.formScope.' + sModel);
                    }
                }
            }
        }

        if (extraBizData) {
            param.extraBizData = extraBizData;
        }

        GM.Form.restful.get(FORM_REST_URL.workflow.startProcess, param).then(function (res) {
            if (res.success === true) {
                //保存数据
                GM.Form.parameters.urlParams.ywh = res.data.bizKey;
                GM.Form.formScope.taskInfo.taskKey = res.data.currentTaskId;
                GM.Form.formScope.taskInfo.bizKey = res.data.bizKey;
                GM.Form.formScope.taskInfo.bizName = res.data.caseTitle;
                GM.Form.formScope.taskInfo.startTime = res.data.startTime;
                GM.Form.formScope.taskInfo.isClaimed = true;

                if (autoHandOver === true) {
                    GM.Form.formScope.saveAndHandOver();
                } else {
                    GM.Form.formScope.save();
                }

                _this.saveBizFlowInfo('', res.data.bizKey);
                if (!notTurnToFirst) {
                    var str = '';
                    for (var key in GM.Form.parameters.urlParams) {
                        str += '&' + key + '=' + GM.Form.parameters.urlParams[key];
                    }
                    window.location = str.replace(/&/, '?') + '&taskKey=' + GM.Form.formScope.taskInfo.taskKey;
                }
            }
        }, function (res) {});
    },
    _initBizFlowDetails: function _initBizFlowDetails() {
        var _this = this;
        //流转详情
        GM.Form.formScope.pendingPageLz = {
            currentPage: 1
        };

        GM.Form.formScope.$watch('pendingPageLz.currentPage', function () {
            if (GM.Form.formScope.pendingPageLz.currentPage > 0) {
                _this._getLzData();
            }
        });

        //表格
        GM.Form.formScope.lzGridOptions = {};

        //定义操作
        var strCellTemplate = '<span style="display:block;padding:5px;color:#3280fc">正常</span>';

        GM.Form.formScope.lzGridOptions.columnDefs = [{
