require('fileService');

angular.module('frameApp').controller('fileCtrl', ['$scope', '$location', '$fileService', '$captureService', 'ngDialog', '$validation', function ($scope, $location, $fileService, $captureService, ngDialog, $validation) {
    var urlParams = formBase.getUrlParamters($location.absUrl());
    //业务号
    var strYwh = urlParams.ywh;
    //节点名称
    var strStepName = urlParams.blbz;
    //是否签收
    var bCliam = true;

    $scope.fileTreeConfig = {
        activeNode: 'activeNode',
        name: 'filename'
    };

    //点击节点
    $scope.activeNode = function (pNode) {
        if (pNode != null) {
            if (pNode.processDefId != null) {
                //文件夹点击
                $scope.activeFolder = true;
            } else {
                //文件点击
                $scope.activeFolder = false;
            }
        }
        $scope.pActiveNode = pNode;
    };

    //上传材料
    $scope.uploadFile = function () {
        $('#file_upload').click();
        $('#file_upload').unbind().change(function () {
            var formData = new FormData();
            formData.append('multipartFile', this.files[0]);
            //formData.append("ywh", strYwh);
            formData.append('id', $scope.pActiveNode.id);
            formData.append('dir', strYwh + '\\' + $scope.pActiveNode.filename);
            formData.append('isDraft', !!urlParams.duuid || !!urlParams.did ? '1' : '0');
            var response = GM.CommonOper.ajaxRequest(RESTURL.file.uploadFile, formData, {
                type: 'POST',
                cache: false,
                processData: false,
                contentType: false
            });
            if ($scope.pActiveNode.children == null) {
                $scope.pActiveNode.children = [];
            }
            if (response.data != null && response.data.children != null && response.data.children.length != 0) {
                var pData = response.data.children[0];
                pData.filename = pData.name;
                //pData.name = pData.fileName;
                $scope.pActiveNode.children.push(pData);
                $scope.$apply($scope.pActiveNode);
                GM.Infomation.show('上传成功！');
            }
            $('#file_upload').val('');
            $scope.fileTree = $fileService.formatTreeData($scope.fileTree, strStepName, bCliam);
        });
    };

    //编辑材料
    $scope.editFolder = function () {
        $scope.copyFocusItem = angular.copy($scope.pActiveNode);
    };

    //删除材料
    $scope.deleteFolder = function () {
        GM.Alert.show('提示？', '是否确认删除材料？', {
            showCancelButton: true
        }, function () {
            $fileService.removeFolder($scope.pActiveNode.id).then(function (response) {
                GM.CommonOper.removeItemFromArray($scope.fileTree, $scope.pActiveNode, 'id');
                GM.Infomation.show('删除成功！');
            }, function (error) {
                GM.Infomation.show('删除失败！');
                $log.error(error);
            });
        });
    };

    //删除文件
    $scope.deleteFile = function () {
        GM.Alert.show('提示？', '是否确认删除文件？', {
            showCancelButton: true
        }, function () {
            $fileService.removeFile($scope.pActiveNode.id, $scope.pActiveNode.fileId).then(function (response) {
                GM.TreeOper.removeNode($scope.fileTree, $scope.pActiveNode);
                GM.Infomation.show('删除成功！');
            }, function (error) {
                GM.Infomation.show('删除失败！');
                $log.error(error);
            });
        });
    };

    //编辑文件
    $scope.editFile = function () {
        $scope.copyFocusItem = angular.copy($scope.pActiveNode);
    };

    //查看文件
    $scope.viewFile = function () {
        //查看文件
        var strUrl = $scope.pActiveNode.url;
        var strFileType = GM.FilePath.getFileType(strUrl);
        if (strFileType === 'img') {
            $scope.imgUrl = strUrl;
        } else {
            window.open(strUrl);
        }
    };

    //保存文件
    $scope.saveFile = function () {
        angular.copy($scope.pActiveNode, $scope.copyFocusItem);
        $fileService.saveFile($scope.copyFocusItem).then(function (response) {
            angular.copy($scope.copyFocusItem, $scope.pActiveNode);
            GM.Infomation.show('修改成功！');
            $('#fileModal').modal('hide');
        }, function (error) {
            GM.Infomation.show('修改失败！');
            $log.error(error);
        });
    };
    //新建材料
    $scope.addFolder = function () {
        GM.Form.Dialog = ngDialog.open({
            template: 'folderModal.html',
            scope: $scope
        });
    };
    //关闭弹窗
    $scope.closeDialog = function () {
        if (ngDialog) {
            ngDialog.close();
        }
    };

    //保存材料
    $scope.saveFolder = function (dislogScope) {
        GM.LayoutOper.validateForm($validation, dislogScope.folderForm, function () {
            $fileService.saveFolder($scope.copyFocusItem).then(function (response) {
                if ($scope.copyFocusItem.id == null) {
                    //添加
                    $scope.fileTree.push(response.data);
                    GM.Infomation.show('添加成功！');
                } else {
                    //修改
                    angular.copy($scope.copyFocusItem, $scope.pActiveNode);
                    GM.Infomation.show('修改成功！');
                }
                ngDialog.close();
            }, function (error) {
                if ($scope.focusItem == null) {
                    GM.Infomation.show('添加失败！');
                } else {
                    GM.Infomation.show('修改失败！');
                }
                $log.error(error);
            });
        }, function () {});
    };

    //扫描文件
    $scope.captureImg = function () {
        $captureService.showCapture($scope);
    };
}]);

