angular.module('frameApp').controller('indexCtrl', ['$scope', '$http', '$gmRestful', function ($scope, $http, $gmRestful) {
    GM.token = top.GM.token;
    var pRestful = new $gmRestful();
    GM.FrameManager.prepareData($scope, pRestful);
    //获取所有的数据列表数据源，用于初始化数据列表控件
    var bizKey = GM.LayoutOper.getParamsValue(window.location.href, 'ywh');
    var taskKey = GM.LayoutOper.getParamsValue(window.location.href, 'taskKey');
    var processKey = GM.LayoutOper.getParamsValue(window.location.href, 'processKey');
    var processDefId = GM.LayoutOper.getParamsValue(window.location.href, 'processDefId');

    var setUrl = function setUrl() {
        var view = GM.LayoutOper.getParamsValue(window.location.href, 'view');

        if (view) {
            $scope.subUrl = 'views/' + view + '.html?' + window.location.href.split('?')[1];
        } else {
            $scope.subUrl = STATIC_RESOURCES_PATH + 'dist/core-views/layout/formIndex.html';
        }
    };

    var loadPage = function loadPage(res) {
        var formIds = '';

        if (res.forms && res.forms.length > 0) {
            var formIdList = [];
            for (var i = 0; i < res.forms.length; i++) {
                formIdList.push(res.forms[i].formid);
            }

            formIds = formIdList.join(',');
        }
        //存储起草时需要用到的formid
        $scope.formIds = formIds;
        if (formIds) {
            $http.get(window.FORM_REST_URL.workflow.getDataGridDataSourceNames + '?formids=' + formIds).then(function (res) {
                if (res.data.success === true) {
                    GM.Form.parameters.formDataSourceNames = res.data.data;
                }

                setUrl();
            }, function (res) {
                console.log(res);
            });
        } else {
            GM.LayoutOper.getParamsValue(window.location.href, 'view');
            setUrl();
        }
    };
    //只查看业务详情
    if (!!bizKey && !processKey) {
        var getInfoUrl = window.FORM_REST_URL.workflow.getProcessInstanceByBizKey + '?bizKey=' + encodeURIComponent(bizKey);
        var box = GM.LayoutOper.getParamsValue(window.location.href, 'box');

        if (!taskKey) {
            taskKey = '查看';
        }

        GM.Form.CommonOper.syncAjaxGet(getInfoUrl, function (r) {
            if (r && r.success === true && r.data) {
                var res = r.data;
                if (res.processDef && res.processDef.processDefinitionId) {
                    processKey = res.processDef.processDefinitionId.split(':')[0];
                    getInfoUrl = 'index.html?appId=' + GM.AppInfo.id + '&serviceAppKey=' + res.processDef.serviceAppKey + '&box=' + box + '&ywh=' + bizKey + '&processKey=' + processKey + '&processDefId=' + res.processDefId + '&taskKey=' + encodeURIComponent(taskKey) + '&processName=' + encodeURIComponent(res.processDef.processName) + '&caseTitle=' + encodeURIComponent(res.caseTitle);

                    if (taskKey === '查看') {
                        getInfoUrl += '&forbid=1';
                    }

                    window.location = getInfoUrl;
                }
            }
        }, function (res) {
            document.write('无法获取业务详情。');
        });
    } else {
        //获取案卷信息
        var url = window.FORM_REST_URL.workflow.getTaskConfig + '?processDefId=' + processDefId + '&taskKey=';
        url += encodeURIComponent(taskKey || '起草');

        GM.Form.CommonOper.syncAjaxGet(url, function (res) {
            if (res.success === true && res.data) {
                loadPage(res.data);
            } else {
                document.write('请求数据失败。');
            }
        }, function (res) {
            document.write('请求数据失败。');
        });
    }
    $scope.closeDlg = function () {
        $('button.close[data-dismiss]', window.parent.document).click();
    };
}]);
