angular.module('frameApp').controller('protocolCtrl', ['$scope', '$rootScope', '$protocolService', '$agencyService', '$log', function ($scope, $rootScope, $protocolService, $agencyService, $log) {
    //起草箱
    //top.GM.AppInfo.id = "aa836f906aa545f6ba32a065a32c4e8a";
    //top.GM.AppInfo.name = "自然资源统一确权登记";

    //用户权限
    if (!top.GM.userInfo || top.GM.userInfo.permissions.length === 0) {
        GM.Infomation.show('无法获取当前用户权限！', {
            type: 'danger',
            placement: 'center'
        });

        return;
    }

    $protocolService.queryDraft(top.GM.AppInfo.id, top.GM.userInfo.permissions.join(',')).then(function (response) {
        $scope.draftData = response.data;
    }, function (error) {
        $log.error(error);
    });

    //起草按钮
    $scope.draftProcess = function (processData) {
        $protocolService.getUUID(null).then(function (res) {
            if (res && res.success === true) {
                goToDraft(processData, res.data);
            } else {
                goToDraft(processData, 'unknown');
            }
        }, function (res) {
            goToDraft(processData, 'unknown');
        });
    };

    //跳转到待办页面
    $scope.goPending = function () {
        $scope.activeNaviFromPath('/protocol', true);
    };

    var goToDraft = function goToDraft(processData, uuid) {
        var processKey = processData.processDefinitionId.split(':')[0];
        $rootScope.processUrl = $agencyService.getHtmlPath() + '?startFlag=1&duuid=' + uuid + '&box=protocol&appId=' + top.GM.AppInfo.id + '&serviceAppKey=' + processData.serviceAppKey + '&processKey=' + processKey + '&processDefId=' + processData.id + '&processName=' + encodeURIComponent(processData.processName);
        $("[ng-controller='protocolCtrl'] *").hide();
        $("[ng-controller='protocolCtrl']").append('<iframe frameborder="0" height="100%" width="100%" name ="protocolCtrl"></iframe>');
        window['protocolCtrl'].location.href = $rootScope.processUrl;
    };
}]);

