 * @author LP
 */
var frameApp = angular.module('frameApp', ['ui.router', 'ngAnimate', 'gm.ngCustom.control', 'ngResource', 'ngSanitize', 'ngCookies']);

GM.FrameManager.configModule(frameApp);

/**
 * @class NgModule.frame2.frameCtrl 框架控制器
 * @extends NgModule.frame.frameCtrl
 * @author LP
 */
frameApp.controller('frameCtrl', ['$scope', '$location', '$gmRestful', '$cookies', '$gmTable', function ($scope, $location, $gmRestful, $cookies, $gmTable) {
    $scope.frameData = GLOBAL_FRAME_CONFIG;
    //获取登出路径
    var strPath = GM.LayoutOper.getURLWithoutParams($location.absUrl());
    $scope.frameData.loginUrl = LOGIN_PATH + '?service=' + strPath + '&logout=1';
    $scope.frameData.indexUrl = MAIN_PAGE_PATH;
    $scope.naviData = GLOBAL_FRAME_CONFIG.navi;
    $scope.naviHide = false; //是否隐藏大导航框
    $scope.activeChildItem = null; //处于焦点的二级子节点
    $scope.threeNaviHide = true; //三级菜单是否隐藏
    $scope.fullScreen = $cookies.getObject('fullScreen');
    $scope.bMenuShow = false; //在全屏模式下时候显示菜单
    $scope.themeName = $cookies.get('themeName') || 'blue'; //系统主题
    $scope.tabWrapper = true; //开启标签模式
    var $pGmTable = new $gmTable();

    //主题切换
    $scope.themeData = {
        blue: {
            name: '蓝色主题',
            color: '#1E5FCA',
            class: 'blue'
        },
        red: {
            name: '红色主题',
            color: '#e01f54',
            class: 'red'
        },
        third: {
            name: '不动产主题',
            color: '#1A82E8',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-estate.css'
        },
        theme1st: {
            name: 'theme1st',
            color: '#25C6DA',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-1st.css'
        },
        theme2nd: {
            name: 'theme2nd',
            color: '#3D4dAd',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-2nd.css'
        },
        theme3rd: {
            name: 'theme3rd',
            color: '#49B04C',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-3rd.css'
        },
        theme4th: {
            name: 'theme4th',
            color: '#66BBF1',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-4th.css'
        },
        theme5th: {
            name: 'theme5th',
            color: '#FDB092',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-5th.css'
        },
        theme6th: {
            name: 'theme6th',
            color: '#FDEECE',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-6th.css'
        },
        theme7th: {
            name: 'theme7th',
            color: '#8E9BBB',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-7th.css'
        },
        theme8th: {
            name: 'theme8th',
            color: '#DCD2ED',
            path: STATIC_RESOURCES_PATH + 'custom/frame/frame2/theme-8th.css'
        }
    };

    //数据准备
    var pRestful = new $gmRestful();
    GM.FrameManager.prepareData($scope, pRestful);

    //添加公共接口
    GM.FrameManager.addCommonApi($scope);

    //添加通知内容
    // GM.FrameManager.addNotify($scope);

    /**
     * @member NgModule.frame2.frameCtrl
     * @method activeNaviFromPath 使菜单处于焦点
     * @param  {String} strPath 路由地址
     * @param  {Boolean} bReload 是否重新加载页面
     * @author LP
     */
    $scope.activeNaviFromPath = function (strPath, bReload) {
        if (bReload === true) {
            //需要从新加载页面
            $scope.refreshPage(strPath);
        }
        Util.activeNaviFromPath(strPath);
        $scope.goPath(strPath);
    };

    /**
     * @member NgModule.frame2.frameCtrl
     * @method refreshPage 刷新路由页面
     * @param  {String} strPath 路由地址
     * @author LP
     */
    $scope.refreshPage = function (strPath) {
        var strName = strPath.substr(1);
        var pEle = GM.CommonOper.getArrayEleByAtt($scope.tabData.tabs, 'name', strName);
        if (pEle) {
            var strUrl = GM.LayoutOper.getURLWithoutParams(pEle.url);
            var strTime = new Date().getTime();
            strUrl = GM.LayoutOper.addURLParam(strUrl, 'timestamp', strTime);
            pEle.url = strUrl;
        }
    };

    /**
     * @member NgModule.frame2.frameCtrl
     * @method deletePage 删除路由页面
     * @param  {String} strPath 路由地址
     * @return {Boolean} 是否删除成功
     */
    $scope.deletePage = function (strPath) {
        var bRt = false;
        if (!GM.CommonOper.isStrNullOrEmpty(strPath)) {
            var strName = strPath.substr(1);
            var pEle = GM.CommonOper.getArrayEleByAtt($scope.tabData.tabs, 'name', strName);
            if (pEle) {
                GM.CommonOper.removeItemFromArray($scope.tabData.tabs, pEle);
                bRt = true;
            }
        }
        return bRt;
    };

    /**
     * @member NgModule.frame2.frameCtrl
     * @method goHomePage 返回首页
     * @author LP
     */
    $scope.goHomePage = function () {
        $scope.goPath($scope.frameData.homePage.path);

        //焦点置空
        var pActiveItem = Util.getActiveChildItem();
        if (pActiveItem != null) {
            pActiveItem.active = false;
        }
        $scope.activeChildItem = null;
    };

    //设置用户头像
    $scope.userImg = {
        background: 'url(' + require('./img/user.png') + ') no-repeat 0 0 / contain'
    };

    //函数定义
    var Util = {
        //获取处于焦点的二级子节点
        getActiveChildItem: function getActiveChildItem() {
            var pNaviData = $scope.naviData;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                for (var jj = 0; jj < oneItem.children.length; jj++) {
                    var twoItem = oneItem.children[jj];
                    if (twoItem.active === true) {
                        return twoItem;
                    }
                }
            }
        },
        //根据路由路径激活不同的导航项
        activeNaviFromPath: function activeNaviFromPath(strPath) {
            Util.initNaviData();
            var pNaviData = $scope.naviData;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                for (var jj = 0; jj < oneItem.children.length; jj++) {
                    var twoItem = oneItem.children[jj];
                    if (twoItem.path === strPath) {
                        twoItem.active = true;
                        oneItem.down = true;
                        $scope.activeChildItem = twoItem;
                        return;
                    }
                    if (twoItem.children) {
                        for (var kk = 0; kk < twoItem.children.length; kk++) {
                            var threeItem = twoItem.children[kk];
                            if (threeItem.path === strPath) {
                                threeItem.active = true;
                                twoItem.active = true;
                                oneItem.down = true;
                                $scope.threeNaviHide = false;
                                $scope.activeChildItem = twoItem;
                                return;
                            }
                        }
                    }
                }
            }
        },
        //初始化导航数据
        initNaviData: function initNaviData() {
            var pNaviData = $scope.naviData;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                oneItem.down = false;
                for (var jj = 0; jj < oneItem.children.length; jj++) {
                    var twoItem = oneItem.children[jj];
                    twoItem.active = false;
                    if (twoItem.children) {
                        for (var kk = 0; kk < twoItem.children.length; kk++) {
                            var threeItem = twoItem.children[kk];
                            threeItem.active = false;
                        }
                    }
                }
            }
        },
        setTheme: function setTheme(strThemeName) {
            for (var key in $scope.themeData) {
                $('html').removeClass(key);
            }
            $('html').addClass(strThemeName);
        }
    };

    if ($scope.tabWrapper === false) {
        //url中如果有path则按照path重新激活导航
        Util.activeNaviFromPath($location.path());
    }

    $scope.$on('$locationChangeSuccess', function (angularEvent, newUrl, oldUrl) {
        Util.activeNaviFromPath($location.path());
    });

    $scope.logout = function ($event) {
        GM.FrameManager.logout('0');
        $event.preventDefault();
    };

    $scope.changePassword = function ($event) {
        GM.FrameManager.logout('1');
        $event.preventDefault();
    };

    //设置导航模式
    $scope.setNaviMode = function () {
        $scope.naviHide = !$scope.naviHide;
    };

    //导航条父级节点点击事件
    $scope.clickParentItem = function (parentItem) {
        var bDown = !parentItem.down;
        var pNaviData = $scope.naviData;

        for (var ii = 0; ii < pNaviData.length; ii++) {
            if (!angular.equals(pNaviData[ii], parentItem)) {
                pNaviData[ii].down = false;
            }
        }
        parentItem.down = bDown;
    };

    //导航条子级节点击事件
    $scope.clickChildItem = function (childItem) {
        //设置焦点节点
        var pNaviData = $scope.naviData;
        for (var ii = 0; ii < pNaviData.length; ii++) {
            var oneItem = pNaviData[ii];
            for (var jj = 0; jj < oneItem.children.length; jj++) {
                var twoItem = oneItem.children[jj];
                twoItem.active = false;
            }
        }
        childItem.active = true;
        $scope.activeChildItem = childItem;
        $scope.goPath(childItem.path);
    };

    //通过事件路由到具体页面
    $scope.goPathFromFrame = function (event) {
        if (event != null) {
            var strPath = event.target.getAttribute('path');
            $scope.goPath(strPath, true);
        }
    };

    //路由到具体页面
    $scope.goPath = function (strPath, bActiveItem) {
        bActiveItem = bActiveItem == null ? false : bActiveItem;
        if (strPath != null && strPath !== '') {
            //处理导航条焦点
            if (bActiveItem === true) {
                Util.activeNaviFromPath(strPath);
            }
            if ($scope.tabWrapper === true) {
                //标签模式
                var strTabName = strPath.substr(1);
                var pTabItem = GM.CommonOper.getArrayEleByAtt($scope.tabData.tabs, 'name', strTabName);
                if (pTabItem != null) {
                    //已经存在，则焦点之
                } else {
                    //不存在，则添加之
                    $scope.tabData.tabs.push({
                        name: strTabName,
                        caption: $scope.activeChildItem.name,
                        url: $scope.activeChildItem.template
                    });
                }
                $scope.tabData.focusTab = strTabName;
            } else {
                //路由模式
                $location.path(strPath).replace();
            }
        }
    };

    //全图
    $scope.setFullScreen = function () {
        $scope.fullScreen = !$scope.fullScreen;
        $cookies.put('fullScreen', $scope.fullScreen);
        $scope.$broadcast('fullScreen', $scope.fullScreen);
        //设置样式
        $timeout(function () {
            if (window.pMap != null && window.pMap.map != null) {
                window.pMap.map.updateSize();
            }
        }, 100);
    };

    //设置菜单是否可见
    $scope.showMenu = function () {
        $scope.bMenuShow = !$scope.bMenuShow;
    };

    //修改主题
    $scope.changeTheme = function (strThemeName) {
        $scope.themeName = strThemeName;
        $cookies.put('themeName', $scope.themeName);
        Util.setTheme($scope.themeName);
        // require(['css!' + $scope.themeData[$scope.themeName].path + '?' + (new Date()).getTime()]);
    };
    Util.setTheme($scope.themeName);
    //---------------tab模式---------------
    $scope.tabData = {
        tabs: [],
        focusTab: '',
        clickTab: 'clickTab'
    };
    //选择主菜单
    $scope.$watch('tabData.focusTab', function () {
        $pGmTable.updateSize();
    });

    if ($scope.frameData.homePage) {
        var strPermission = $scope.frameData.homePage.permission;
        if (strPermission != null && $scope.hasPermission(strPermission) === false) {
            //没有首页权限
        } else {
            var strTabName = $scope.frameData.homePage.path.substr(1);
            $scope.tabData.tabs.push({
                name: strTabName,
                caption: $scope.frameData.homePage.name,
                url: $scope.frameData.homePage.template
            });
            $scope.tabData.focusTab = strTabName;
        }
    }

    //默认展示标签
    if ($scope.defaultViewTabs) {
        for (var ii = 0; ii < $scope.defaultViewTabs.length; ii++) {
            $scope.activeNaviFromPath($scope.defaultViewTabs[ii]);
        }
    }

    //跳转到待办箱
    $scope.goPending = function () {
        //删除当前标签页
        $scope.deletePage('/' + $scope.tabData.focusTab);

        //焦点待办箱
        $scope.activeNaviFromPath('/agency', true);
    };
    //---------------查询表单---------------
}]);

//验证token
