'use strict';

require('../../frame/frame.js');

require('./login.less');

var app = angular.module('loginApp', []);
app.controller('loginCtrl', ['$scope', '$timeout', function ($scope, $timeout) {
    var result;
    $scope.loginInfo = loginInfo;
    $scope.userImg = require('./img/user.png');
    $scope.passwordImg = require('./img/password.png');
    $scope.codeImg = require('./img/verificationCode.png');

    var params = GM.LayoutOper.getURLParams(window.location.href);
    var service = params.service;
    var appKey = params.appKey == null ? 'mainApp' : params.appKey;

    if (params.changePassword === '1') {
        $scope.changePassword = true;
    } else {
        //默认隐藏修改密码界面
        $scope.changePassword = false;
    }

    if (GM.CommonOper.isStrNullOrEmpty(service)) {
        service = MAIN_PAGE_PATH;
    }
    var strLogout = params.logout;

    if (strLogout === '1') {
        //登出
        result = GM.CommonOper.ajaxRequest(RESTURL.logout);
        if (result && result.success === true) {
            //登出成功
            GM.LayoutOper.clearCookie();
        } else {
            //登出失败
            GM.Alert.show('提示', '登出失败！');
        }
    }

    //如果有cookies则自动登录
    var GMLOGIN_SERVER_EC = GM.CommonOper.getCookie('clientServerToken');
    if (!GM.CommonOper.isStrNullOrEmpty(GMLOGIN_SERVER_EC)) {
        //尝试登录
        var strUrl = GM.LayoutOper.addURLParam(RESTURL.login, 'GMSSO_SERVER_EC', GMLOGIN_SERVER_EC);
        strUrl = GM.LayoutOper.addURLParam(strUrl, 'service', service);
        strUrl = GM.LayoutOper.addURLParam(strUrl, 'appKey', appKey);
        result = GM.CommonOper.ajaxRequest(strUrl);
        if (result && result.success === true && result.data != null) {
            //登录成功,跳转页面
            goService(service, result.data.gmsso_cli_ec_key);
            return true;
        }
    }
    if (loginInfo.swiperImgs) {
        var timer = $timeout(function () {
            var mySwiper = new Swiper('.swiper-container', {
                loop: true,
                speed: 700,
                autoplay: 5000,
                pagination: '.swiper-pagination',
                onTransitionStart: function onTransitionStart(swiper) {
                    $('.tips').removeClass('showTips');
                },
                onTransitionEnd: function onTransitionEnd(swiper) {
                    $('.swiper-wrapper').find('.swiper-slide').eq(swiper.activeIndex).find('.tips').addClass('showTips');
                }
            });
            mySwiper.onResize();
            clearTimeout(timer);
        }, 50);
    }

    //登录按钮
    $scope.loginData = {};
    $scope.submit = function () {
        //验证表单
        var loginData = $scope.loginData;
        loginData.base64Code = GM.verifyCode;
        if (GM.CommonOper.isStrNullOrEmpty(loginData.username)) {
            GM.Alert.show('提示', '请输入用户名！');
            return;
        }
        if (GM.CommonOper.isStrNullOrEmpty(loginData.password)) {
            GM.Alert.show('提示', '请输入密码！');
            return;
        }
        if (GM.CommonOper.isStrNullOrEmpty(loginData.code)) {
            GM.Alert.show('提示', '请输入验证码！');
            return;
        }
        if (loginData.code.toLowerCase() !== loginData.base64Code.toLowerCase()) {
            GM.Alert.show('提示', '验证码错误，请重新输入！');
            return;
        }
        loginData.service = service;
        loginData.appKey = appKey;
        var strUrl = RESTURL.login;
        var result = GM.CommonOper.ajaxRequest(strUrl, loginData, {
            type: 'get'
        });
        if (result && result.success === true && result.data != null) {
            //登录成功
            GM.CommonOper.addCookie('clientServerToken', result.data.gmsso_ser_ec_key, 1);
            GMLOGIN_SERVER_EC = result.data.gmsso_ser_ec_key;
            if (result.data.gmsso_cli_ec_key) {
                goService(service, result.data.gmsso_cli_ec_key);
            } else {
                //没有配置子系统
                GM.Alert.show('提示', '当前子系统路径无法识别！');
            }
        } else {
            //登录失败
            var resultMessage = result.message;
            if (resultMessage.search(/该用户超过半年没有登录/g) >= 0) {
                GM.Alert.show('提示', result.message, null, function () {
                    //该用户超过半年没有登录
                    $scope.changePassword = true;
                    $scope.$apply();
                });
            } else {
                GM.Alert.show('提示', result.message);
            }
        }
    };
    //修改密码
    $scope.changePasswordData = {};
    var patt = /[A-Za-z].*[0-9]|[0-9].*[A-Za-z]/;
    $scope.confirmChangePassword = function () {
        if (GM.CommonOper.isStrNullOrEmpty($scope.changePasswordData.loginName)) {
            GM.Alert.show('提示', '请输入用户名！');
            return;
        }
        if (GM.CommonOper.isStrNullOrEmpty($scope.changePasswordData.oriPassword)) {
            GM.Alert.show('提示', '请输入原始密码！');
            return;
        }
        if (GM.CommonOper.isStrNullOrEmpty($scope.changePasswordData.newPassword)) {
            GM.Alert.show('提示', '请输入新密码！');
            return;
        }
        if (GM.CommonOper.isStrNullOrEmpty($scope.changePasswordData.newPasswordAgain)) {
            GM.Alert.show('提示', '请再次输入新密码！');
            return;
        }
        if (!patt.test($scope.changePasswordData.newPassword) || !patt.test($scope.changePasswordData.newPasswordAgain)) {
            GM.Alert.show('提示', '密码输入不符合规范！密码须包含数字、字母');
            return;
        }
        if ($scope.changePasswordData.newPassword !== $scope.changePasswordData.newPasswordAgain) {
            GM.Infomation.show('两次输入密码不一致', { type: 'warning' });
            return;
        }
        var changePasswordUrl = UUMS_SERVER + 'user/updatePassword';
        var changePasswordResult = GM.CommonOper.ajaxRequest(changePasswordUrl, $scope.changePasswordData, {
            type: 'post'
        });
        if (changePasswordResult && changePasswordResult.success === true) {
            GM.Infomation.show('密码修改成功', { type: 'success' });
            $scope.changePassword = false;
            $scope.loginData.password = '';
        } else {
            GM.Alert.show('提示', changePasswordResult.message);
        }
    };

    //重新加载验证码
    $scope.reloadCode = function () {
        var result = GM.CommonOper.ajaxRequest(LOGIN_SERVER + 'getCode?' + Math.random(), null, {
            type: 'get'
        });
        if (result && result.success === true) {
            $('#imgCode').attr('src', 'data:image/png;base64,' + result.data);
            $('#wwimgCode').attr('src', 'data:image/png;base64,' + result.data);
            GM.verifyCode = result.message;
        }
    };

    //注册点击事件
    $(document.body).keyup(function (event) {
        if (event.keyCode === 13) {
            $scope.submit();
        }
    });

    //初始化验证码
    $scope.reloadCode();

    //登录后页面跳转
    function goService(strService, strToken) {
        var strUrl = GM.LayoutOper.addURLParam(strService, 'GMSSO_CLIENT_EC', strToken);
        strUrl = GM.LayoutOper.addURLParam(strUrl, 'GMLOGIN_SERVER_EC', GMLOGIN_SERVER_EC);
        window.location.href = strUrl;
    }
}]);
app.filter('to_trusted', ['$sce', function ($sce) {
    return function (text) {
        return $sce.trustAsHtml(text);
    };
}]);
app.run(['$templateCache', function ($templateCache) {
    var strTem = require('./login.html');
    $templateCache.put('login.tpl.html', strTem);
}]);

