                 */
                _getHDimentionNumber: function _getHDimentionNumber(nHH, nUnitNum) {
                    var result = 0;
                    for (var ii = 0; ii < nUnitNum; ii++) {
                        if (this.ArrayUnitHouseCount[ii]) {
                            result += this.ArrayUnitHouseCount[ii];
                        }
                    }
                    result += nHH;
                    if (result > 0) {
                        result = result - 1;
                    }
                    return result;
                },
                /**
                 * @description 格式化数据
                 * @method _formatHouseTable
                 * @param {Array} pArrayHouse
                 * @private
                 */
                _formatHouseTable: function _formatHouseTable(pArrayHouse) {
                    for (var ii = 0; ii < pArrayHouse.length; ii++) {
                        var pHouse = pArrayHouse[ii];

                        //记录原始户号
                        pHouse.originHh = pHouse.hh;

                        //室号部位为空的设置为空字符串
                        if (pHouse.shbw == null) {
                            pHouse.shbw = '';
                        }

                        //获取层号
                        pHouse.sjc = this._getChNumber(pHouse.sjc);
                    }

                    //获取是否重新编写户号，默认不编
                    var bReNumber = true;
                    this.maintainHh(pArrayHouse, bReNumber);
                },
                /**
                 * @description 维护逻辑幢中的户号
                 * @method maintainHh
                 * @param {Array} pArrayHouse
                 * @param {Boolean} bReNumber 是否重新编写户号，默认不编
                 */
                maintainHh: function maintainHh(pArrayHouse, bReNumber) {
                    //区分有无单元号数据
                    this.unitHouseData = [];
                    this.noUnitHouseData = [];
                    var ii;
                    var pHouse;
                    for (ii = 0; ii < pArrayHouse.length; ii++) {
                        pHouse = pArrayHouse[ii];
                        if (pHouse.dyh == null || pHouse.dyh === 0) {
                            //没有单元号的数据
                            this.noUnitHouseData.push(pHouse);
                        } else {
                            //有单元号的数据
                            this.unitHouseData.push(pHouse);
                        }
                    }
                    if (bReNumber === true) {
                        //重新编号
                        this._maintainHh(this.unitHouseData, true);
                        this._maintainHh(this.noUnitHouseData, false);
                    } else {
                        //使用数据中的户号
                        for (ii = 0; ii < pArrayHouse.length; ii++) {
                            pHouse = pArrayHouse[ii];
                            pHouse.hh = pHouse.originHh;
                        }
                    }
                },
                /**
                 * @description 通过层号里面的值获取相应的数值信息
                 * @method _getChNumber
                 * @param strCh {String} 层号
                 * @return {Number} 相应的数值信息
                 */
                _getChNumber: function _getChNumber(strCh) {
                    //维护层号
                    if (!strCh) {
                        return;
                    }
                    var arrayMatches = strCh.match(/^-\d+/g);
                    if (arrayMatches == null || arrayMatches.length === 0) {
                        arrayMatches = strCh.match(/\d+/g);
                    }
                    if (arrayMatches != null && arrayMatches.length > 0) {
                        strCh = arrayMatches[0];
                    }
                    return strCh;
                },
                /**
                 * @description 维护数据的户号
                 * @method _maintainHh
                 * @param arrayHouseData {Array} 户信息数组
                 * @param bHasDyh {Boolean} 是否有单元号
                 */
                _maintainHh: function _maintainHh(arrayHouseData, bHasDyh) {
                    var arrayHouseFloor = {};
                    var pHouseData;
                    var ii;
                    var key;
                    if (bHasDyh === false) {
                        for (ii = 0; ii < arrayHouseData.length; ii++) {
                            pHouseData = arrayHouseData[ii];
                            if (arrayHouseFloor[pHouseData.sjc] == null) {
                                arrayHouseFloor[pHouseData.sjc] = [];
                            }
                            arrayHouseFloor[pHouseData.sjc].push(pHouseData);
                        }
                        for (key in arrayHouseFloor) {
                            this._miantainHhFromFloor(arrayHouseFloor[key]);
                        }
                    } else {
                        for (ii = 0; ii < arrayHouseData.length; ii++) {
                            pHouseData = arrayHouseData[ii];
                            var strKey = pHouseData.sjc + 'T' + pHouseData.dyh;
                            if (arrayHouseFloor[strKey] == null) {
                                arrayHouseFloor[strKey] = [];
                            }
                            arrayHouseFloor[strKey].push(pHouseData);
                        }
                        for (key in arrayHouseFloor) {
                            this._miantainHhFromFloor(arrayHouseFloor[key]);
                        }
                    }
                },
                /**
                 * @description 维护每一层的户号
                 * @method _miantainHhFromFloor
                 * @param {Array} arrayHouseInfo
                 */
                _miantainHhFromFloor: function _miantainHhFromFloor(arrayHouseInfo) {
                    if (arrayHouseInfo == null) {
                        return;
                    }
                    arrayHouseInfo = arrayHouseInfo.sort(function (ele1, ele2) {
                        var arrayMatch1 = ele1.shbw.match(/\d+/g);
                        var arrayMatch2 = ele2.shbw.match(/\d+/g);
                        if (arrayMatch1 == null) {
                            arrayMatch1 = [0];
                        }
                        if (arrayMatch2 == null) {
                            arrayMatch2 = [0];
                        }
                        var nNum1 = Number(arrayMatch1[arrayMatch1.length - 1]);
                        var nNum2 = Number(arrayMatch2[arrayMatch2.length - 1]);
                        return nNum1 - nNum2;
                    });
                    for (var ii = 0; ii < arrayHouseInfo.length; ii++) {
                        var pHouseInfo = arrayHouseInfo[ii];
                        //自动维护户号，不再使用原始数据的户号
                        pHouseInfo.hh = ii + 1;
                    }
                }
            };
            GM.OA.Housetable.maintainData(scope.arrayhouse);
            scope.legendData = GM.OA.Housetable.legendData;

            //添加监听
            scope.$watch('arrayhouse', function (newValue, oldValue, scope) {
                GM.OA.Housetable.scope = scope;
                GM.OA.Housetable.maintainData(scope.arrayhouse);
            });
        }
    };
}]);

