function ($layoutTag) {
    //查询表单中标签指令
    return {
        restrict: 'ECA',
        template: require('./tag.tpl.html'),
        scope: {
            tagdata: '='
        },
        link: function link(scope, element, attr, transclution) {
            var pLayoutTag = new $layoutTag();
            var nMaxTagNum = Number(attr['maxTagNum']);
            var bMulti = attr['multi'] !== 'false'; //默认多选
            scope.bMulti = bMulti;

            //element.hide();
            if (Number.isNaN(nMaxTagNum) || nMaxTagNum === 0) {
                //计算可以存放的最大个数
                GM.CommonOper.delayExcute(function (params) {
                    var arrayItems = params.element.find('.tag-item');
                    if (arrayItems.length === 0 || $(arrayItems[0]).width() <= 0 || params.element.width() <= 0) {
                        return false;
                    } else {
                        //减去更多选择的按钮宽度
                        var nRemainWidth = params.element.width() - 50;
                        //多选状态需要减去 不限 按钮的宽度
                        if (bMulti === true) {
                            nRemainWidth -= 52;
                        }
                        for (var ii = 0; ii < arrayItems.length; ii++) {
                            var $pItem = $(arrayItems[ii]);
                            nRemainWidth -= $pItem.outerWidth() + 10;
                            if (nRemainWidth <= 0) {
                                params.scope.maxTagNum = ii;
                                params.scope.$apply('maxTagNum');
                                break;
                            }
                        }
                        //params.element.show();
                        return true;
                    }
                }, {
                    scope: scope,
                    element: element
                });
            } else {
                scope.maxTagNum = nMaxTagNum;
            }

            //查看额外的标签
            scope.clickExtraTool = function () {
                scope.extraDown = !scope.extraDown;
            };

            //选中tag标签
            scope.checkTag = function (item) {
                if (bMulti) {
                    //多选
                    item.checked = !item.checked;
                    if (angular.isFunction(scope.$parent[scope.tagdata.checkTag])) {
                        var arrayCheckedNodes = pLayoutTag.getCheckedTags(scope.tagdata.tags);
                        if (GM.CommonOper.isFunction(scope.$parent[scope.tagdata.checkTag])) {
                            scope.$parent[scope.tagdata.checkTag](arrayCheckedNodes);
                        }
                    }
                } else {
                    //单选
                    if (item.checked === true) {
                        //已经选中,再次点击，则取消选中
                        item.checked = false;
                        if (GM.CommonOper.isFunction(scope.$parent[scope.tagdata.checkTag])) {
                            scope.$parent[scope.tagdata.checkTag](null);
