                if (scope.left + nDivWidth < nSpanWidth) {
                    //先横向滚动
                    scope.left += nDivWidth - 10;
                } else {
                    //横向滚动结束，开始竖向滚动
                    if (scope.notifyData != null && scope.notifyData.length > 0) {
                        scope.nSwapIndex++;
                        var nIndex = scope.nSwapIndex % scope.notifyData.length;
                        scope.message = scope.notifyData[nIndex];
                    }
                    scope.left = 0;
                }
                scope.spanStyle.left = '-' + scope.left + 'px';
            }, nInterval);

            if (scope.notifyData) {
                scope.message = scope.notifyData[scope.nSwapIndex];
            }
        }
    };
