/**
 * 统计图表服务
 * @class NgModule.statistics.$gmStatisticsTable
 * @alias $gmStatisticsTable
 * @author LP
 */
function () {
    this.$get = [function () {
        function factory() {
            var $gmStatisticsTable = {};
            $gmStatisticsTable.options = {
                nThHeigth: 37,
                nThPadding: 8
            };

            /**
             * 格式化数据
             * @member NgModule.statistics.$gmStatisticsTable
             * @method formatConfig
             * @param {Object} pConfig 配置项
             */
            $gmStatisticsTable.formatConfig = function (pConfig) {
                if (pConfig == null) {
                    return null;
                }
                var ii;
                this.treeData = pConfig.form;
                var nMaxLevel = $gmStatisticsTable._setParent(pConfig.form, null);

                //维护所有叶子节点的信息
                var arrayNames = [];
                var arrayLeafNodes = this._getNodesByParams('isParent', false);
                for (ii = 0; ii < arrayLeafNodes.length; ii++) {
                    var pLeafNode = arrayLeafNodes[ii];
                    pLeafNode.colspan = 1;
                    arrayNames.push(pLeafNode.name);
                }
                pConfig.names = arrayNames;

                //设置具体样式
                var pFormData = [];
                for (ii = nMaxLevel; ii > 0; ii--) {
                    var arrayNodes = this._getNodesByParams('level', ii);
                    //维护节点的样式
                    for (var jj = 0; jj < arrayNodes.length; jj++) {
                        this._maintainNodeStyle(arrayNodes[jj]);
                    }
                    pFormData.unshift(arrayNodes);
                }
                pConfig.formatForm = pFormData;
                return pConfig;
            };

            /**
             * @description 设置节点的父节点
             * @member NgModule.statistics
             * @method _setParent
             * @param arrayChildren {Array} 子节点集合
             * @param pParent   {Object} 父节点
             * @return {int} 返回最大的level值
             * @private
             */
            $gmStatisticsTable._setParent = function (arrayChildren, pParent) {
                if (arrayChildren == null) {
                    if (pParent != null) {
                        return pParent.level;
                    } else {
                        return 1;
                    }
                }
                var result = 1;
                for (var ii = 0; ii < arrayChildren.length; ii++) {
                    var pItem = arrayChildren[ii];
                    pItem.isParent = false;
                    pItem.parent = pParent;
                    if (pParent != null) {
                        pItem.level = pItem.parent.level + 1;
                        pItem.parent.isParent = true;
                    } else {
                        pItem.level = 1;
                    }
                    var nLevel = $gmStatisticsTable._setParent(pItem.children, pItem);
                    if (nLevel > result) {
                        result = nLevel;
                    }
                }
                return result;
            };

            /**
             * @description 根据参数获取节点集合
             * @member NgModule.statistics
             * @method _getNodesByParams
             * @param {String} key 查找名称
             * @param {String} value 查找值
             * @param {Object} parentNode 父节点
             * @return {Array} 返回的节点集合
             * @private
             */
            $gmStatisticsTable._getNodesByParams = function (key, value, parentNode) {
                var result = [];
                var arrayNodes = null;
                if (parentNode != null) {
                    arrayNodes = parentNode.children;
                } else {
                    arrayNodes = this.treeData;
                }
                if (arrayNodes != null) {
                    for (var ii = 0; ii < arrayNodes.length; ii++) {
                        var pNode = arrayNodes[ii];
                        if (pNode[key] === value) {
                            result.push(pNode);
                        }
                        var arrayTempNodes = this._getNodesByParams(key, value, pNode);
                        result = result.concat(arrayTempNodes);
                    }
                }
                return result;
            };

            /**
             * @description 维护th元素的高度和宽度
             * @member NgModule.statistics
             * @method _maintainNodeStyle
             * @param pNode {Object} 节点信息
             * @private
             */
            $gmStatisticsTable._maintainNodeStyle = function (pNode) {
                if (pNode == null) {
                    return;
                }
                if (pNode.isParent === false) {
                    pNode.rowspan = pNode.rowspan == null ? 1 : pNode.rowspan;
                    pNode.colspan = pNode.colspan == null ? 1 : pNode.colspan;
                    if (pNode.nWidth == null) {
                        pNode.nWidth = (pNode.caption.length * 20 + this.options.nThPadding * 2) * pNode.colspan;
                    }
                } else {
                    var nColspan = 0;
                    var nWidth = 0;
                    for (var ii = 0; ii < pNode.children.length; ii++) {
                        var pSubNode = pNode.children[ii];
                        nColspan += pSubNode.colspan;
                        nWidth += pSubNode.nWidth;
                    }
                    pNode.rowspan = pNode.rowspan == null ? 1 : pNode.rowspan;
                    pNode.colspan = nColspan;
                    pNode.nWidth = nWidth;
                }
                if (pNode.nLineHeight == null) {
                    pNode.nLineHeight = this.options.nThHeigth * pNode.rowspan - this.options.nThPadding * 2;
                }

                //设置style
                pNode.style = GM.CommonOper.formatString('line-height: {0}px;width:{1}px', [pNode.nLineHeight, pNode.nWidth]);
            };

            return $gmStatisticsTable;
        }
        return factory;
    }];
}).directive('gmStatisticsTable', ['$gmStatisticsTable',
/**
