'use strict';

require('api');

/**
 * 框架管理器
 * @class GM.FrameManager
 * @author LP
 */
GM.FrameManager = {
    /**
     * @description 初始化路由
     * @method initRouter
     * @param $stateProvider
     */
    initRouter: function initRouter($stateProvider) {
        try {
            var stateName;
            if (GLOBAL_FRAME_CONFIG) {
                //配置首页
                if (GLOBAL_FRAME_CONFIG.homePage) {
                    stateName = GLOBAL_FRAME_CONFIG.homePage.path.substr(1);
                    $stateProvider.state(stateName, {
                        url: GLOBAL_FRAME_CONFIG.homePage.path,
                        templateUrl: GLOBAL_FRAME_CONFIG.homePage.template
                    });
                }
                //配置其他项
                for (var ii = 0; ii < GLOBAL_FRAME_CONFIG.navi.length; ii++) {
                    var oneItem = GLOBAL_FRAME_CONFIG.navi[ii];
                    if (oneItem.path != null) {
                        stateName = oneItem.path.substr(1);
                        if (oneItem.template) {
                            $stateProvider.state(stateName, {
                                url: oneItem.path,
                                templateUrl: oneItem.template
                            });
                        }
                    } else {
                        for (var jj = 0; jj < oneItem.children.length; jj++) {
                            var twoItem = oneItem.children[jj];
                            stateName = twoItem.path.substr(1);
                            if (twoItem.template) {
                                $stateProvider.state(stateName, {
                                    url: twoItem.path,
                                    templateUrl: twoItem.template
                                });
                            }
                        }
                    }
                }
            }
        } catch (ex) {
            console.warn('框架配置变量未定义');
        }
    },
    /**
     * @description 模块配置
     * @method configModule
     * @param {Object} app
     */
    configModule: function configModule(app) {
        if (app == null) {
            return;
        }
        //模块配置
        app.config(['$stateProvider', '$urlRouterProvider', 'uiSelectConfig', '$qProvider', 'cfpLoadingBarProvider', 'ngDialogProvider', '$validationProvider', 'mePageLoadingProvider', '$sceDelegateProvider', function ($stateProvider, $urlRouterProvider, uiSelectConfig, $qProvider, cfpLoadingBarProvider, ngDialogProvider, $validationProvider, mePageLoadingProvider, $sceDelegateProvider) {
            //$urlRouterProvider.when("",GLOBAL_FRAME_CONFIG.homePage.path);//设置首页为默认页面
            GM.FrameManager.initRouter($stateProvider);

            //配置下拉框选择器
            uiSelectConfig.theme = 'bootstrap';
            uiSelectConfig.resetSearchInput = true;
            uiSelectConfig.appendToBody = false;

            //默认配置
            $qProvider.errorOnUnhandledRejections(false);

            //进度条
            cfpLoadingBarProvider.includeSpinner = true;
            cfpLoadingBarProvider.parentSelector = '#loading-container';

            //对话框
            ngDialogProvider.setDefaults({
                className: 'ngdialog-theme-default custom',
                plain: false,
                showClose: true,
                closeByDocument: false,
                closeByEscape: false,
                appendTo: false,
                trapFocus: false,
                preCloseCallback: function preCloseCallback() {
                    //console.log('default pre-close callback');
                }
            });

            //表单验证
            //$validationProvider.setValidMethod("blur");

            //弹出层进度条插件
            mePageLoadingProvider.container = '.frame-content>.content';

            $sceDelegateProvider.resourceUrlWhitelist([
            // Allow same origin resource loads.
            'self',
            // Allow loading from our assets domain.  Notice the difference between * and **.
            '**']);
        }]);
    },
    /**
     * @description 添加token验证
     * @method addToken
     * @param {Object}  app ng中的应用对象
     * @param {Boolean} bLogout 是否自动登出
     */
    addToken: function addToken(app, bLogout) {
        if (app == null) {
            return;
        }
        //验证token
        app.run(['$gmRestful', '$location', '$http', function ($gmRestful, $location, $http) {
            $(document.body).css({
                visibility: 'hidden'
            });
            var pRestful = new $gmRestful();
            if (RESTURL.token == null) {
                GM.token = 'admin';
                $(document.body).css({
                    visibility: 'visible'
                });
                return;
            }
            var bValid = pRestful.isTokenValid(RESTURL.token);
            if (bValid === false && bLogout !== false) {
                GM.FrameManager.logout();
            }
            $(document.body).css({
                visibility: 'visible'
            });
        }]);
    },
    /**
     * @description 基础数据准备
     * @method prepareData
     * @param {Object} $scope
     * @param {Object} pRestful
     */
    prepareData: function prepareData($scope, pRestful) {
        //用户信息
        this.prepareUserData($scope, pRestful);

        //数据字典信息
        this.prepareDicData();
    },
    /**
     * @description 用户信息获取
     * @method prepareUserData
     * @param {Object} $scope
     * @param {Object} pRestful
     */
    prepareUserData: function prepareUserData($scope, pRestful) {
        if (UUMS_SERVER) {
            var pUserData = pRestful.analysisToken();
            if (pUserData) {
                GM.UserOper.getUserInfo(pUserData.userId, UUMS_SERVER + 'user/get', UUMS_SERVER + 'area/tree', UUMS_SERVER + 'role/get', UUMS_SERVER + 'organization/tree');
                //获取用户对应的辖区信息
                $scope.user = {
                    caption: GM.userInfo.realName,
                    name: GM.userInfo.loginName,
                    id: GM.userInfo.id
                };
            }
        }
    },
    /**
     * @description 数据字典数据获取
     * @method prepareDicData
     */
    prepareDicData: function prepareDicData() {
        //获取当前系统的appid
        var appResponse = GM.CommonOper.ajaxRequest(UUMS_SERVER + 'app/list', { size: 1000 }, {
            type: 'get'
        });
        if (appResponse && appResponse.data) {
            var records = appResponse.data.records;
            GM.AppInfos = records;
            for (var ii = 0; ii < records.length; ii++) {
                if (records[ii].name === APP_KEY) {
                    GM.AppInfo = records[ii];
                    break;
                }
            }
        }

        //数据字典数据
        if (UUMS_SERVER && GM.AppInfo) {
            GM.SysDicOper.getSysdic(UUMS_SERVER + 'dict/tree', GM.AppInfo.id);
        }
    },
    /**
     * @description 是否拥有权限
     * @method hasPermission
     * @param {String} strPermission 权限标识符，以逗号隔开
     * @param {Array} returnValues 返回值
     * @return {boolean} 是否拥有权限
     */
    hasPermission: function hasPermission(strPermission, returnValues) {
        if (returnValues == null || returnValues.length < 2) {
            returnValues = [true, false];
        }
        if (GM.CommonOper.isStrNullOrEmpty(strPermission)) {
            return returnValues[0];
        }
        if (GM.userInfo == null) {
            return returnValues[1];
        }
        var arrayUserPermisson = GM.userInfo.permissions;
        if (arrayUserPermisson == null) {
            return returnValues[1];
        }
        var arrayPermission = strPermission.split(',');
        for (var ii = 0; ii < arrayPermission.length; ii++) {
            if (GM.CommonOper.isArrayContainsEle(arrayUserPermisson, arrayPermission[ii])) {
                return returnValues[0];
            }
        }
        return returnValues[1];
    },
    /**
     * 登出操作
     * @method logout
     * @param changePassword {String} 是否为修改密码 0否 1是
     */
    logout: function logout(changePassword) {
        var strAbsUrl = window.location.href;
        var strPath = GM.LayoutOper.getURLWithoutParams(strAbsUrl);
        var strUrl = LOGIN_PATH + '?service=' + strPath + '&logout=1' + '&appKey=' + APP_KEY + '&changePassword=' + changePassword;

        //跳转之前先删除cookie值
        GM.LayoutOper.clearCookie();

        window.location.href = strUrl;
    },
    addNotify: function addNotify($scope) {
        $scope.notifyData = ['数据字典表单验证问题；日期控件表单验证样式问题', '我是消息'];
    },
    /**
     * 作用域上添加公共API
     * @method addCommonApi
     * @param $scope {scope} 作用域
     */
    addCommonApi: function addCommonApi($scope) {
        /**
         * @description 用户是否拥有功能权限
         * @member NgModule.frame.frameCtrl
         * @param strPermission {String} 权限字符串
         * @param returnValues {Array} 返回值，默认为 true和false
         * @returns {*}
         */
        $scope.hasPermission = function (strPermission, returnValues) {
            return GM.FrameManager.hasPermission(strPermission, returnValues);
        };

        /**
         * @description 获取辖区名称
         * @member NgModule.frame.frameCtrl
         * @param strCode
         */
        $scope.getRegionCaption = function (strCode) {
            return GM.RegionOper.getCaption(strCode);
        };

        /**
         * 获取字典显示值
         * @method getDicLable
         * @member NgModule.frame.frameCtrl
         * @param {String} value 字典值
         * @param {String} type 字典类型
         * @return {String} 字典显示值
         */
        $scope.getDicLable = function (value, type) {
            var strLable = GM.SysDicOper.getLabel(type, value);
            return strLable;
        };

        /**
         * 树状表格插件中获取样式的方法
         * @ignore
         * @method getStyleForTreeTable
         * @param {Number} nPx 像素
         * @return {Object}
         */
        $scope.getStyleForTreeTable = function (nPx) {
            if (nPx === 0) {
                return {};
            } else {
                return {
                    'padding-left': nPx + 'px'
                };
            }
        };

        /**
         * 表格插件中复杂表头的宽度计算函数
         * @ignore
         * @method _getCategoryWidth
         * @param {Array} columns 列集合
         * @param {Object} category 类型
         * @param {'subcategory'|'category'} type 类型
         * @returns {category} category
         */
        $scope._getCategoryWidth = function (colContainer, category, type) {
            var dw = 0;
            var columns = colContainer.renderedColumns;
            if (columns) {
                for (var i = 0; i < columns.length; i++) {
                    if (columns[i].colDef[type] === category.name) {
                        dw += columns[i].drawnWidth;
                    }
                }
            }
            var width = dw.toString() + 'px';
            var res = {
                'max-width': width,
                'min-width': width
            };
            if (category && category.name) {
                res['border-bottom'] = 'solid 1px #cecece';
            }
            return res;
        };
    }
};

