/**
 * @description 主页通用操作API
 * @class GM.CenterCommon
 * @author LP
 */
GM.CenterCommon = {
    /**
     * @description 打开子系统页面
     * @method openSystem
     * @param {String} strUrl 子系统地址
     */
    openSystem: function openSystem(strUrl) {
        var strServerToken = GM.CommonOper.getParamFromUrlOrCookie('GMLOGIN_SERVER_EC', 'clientServerToken');
        //var strResultUrl = GM.LayoutOper.addURLParam(strUrl,"GMSSO_CLIENT_EC",GM.token);
        var strResultUrl = GM.LayoutOper.addURLParam(strUrl, 'GMLOGIN_SERVER_EC', strServerToken);
        strResultUrl = GM.LayoutOper.addURLParam(strResultUrl, 'userName', GM.userInfo.loginName);

        //为防止token超时，每次都获取新的token值
        var requestData = {
            GMSSO_SERVER_EC: strServerToken,
            appKey: APP_KEY
        };
        var strUrl = LOGIN_SERVER + 'login';
        var response = GM.CommonOper.ajaxRequest(strUrl, requestData, {
            type: 'get'
        }, false);
        if (response && response.success === true && response.data != null) {
            if (response && response.data && response.data.gmsso_cli_ec_key) {
                GM.token = response.data.gmsso_cli_ec_key;
            }
        }
        strResultUrl = GM.LayoutOper.addURLParam(strResultUrl, 'token', GM.token);
        strResultUrl = GM.LayoutOper.addURLParam(strResultUrl, 'appKey', APP_KEY);
        window.open(strResultUrl);
    },
    /**
     * @description 获取列表中拥有权限的列表数据
     * @method getPermissionList
     * @param {Array} arrayItems 子系统列表集合
     * @return {Array} 未拥有权限的子系统下标
     */
    getPermissionList: function getPermissionList(arrayItems) {
        if (arrayItems == null) {
            return null;
        }
        var result = [];
        var index = [];
        for (var ii = 0; ii < arrayItems.length; ii++) {
            var pItem = arrayItems[ii];
            if (GM.FrameManager.hasPermission(pItem.permission) !== false) {
                result.push(pItem);
                //如果有子节点则判断子节点的权限
                if (pItem.children) {
                    var tempArray = [];
                    for (var jj = 0; jj < pItem.children.length; jj++) {
                        if (GM.FrameManager.hasPermission(pItem.children[jj].permission) !== false) {
                            tempArray.push(pItem.children[jj]);
                        }
                    }
                    pItem.children = tempArray;
                }
            } else {
                index.push(ii);
            }
        }
        return index;
    }
};

