 ---------------------*/
.gm-combotree-div {
  width: 100%;
}
.gm-combotree-div .input-group-btn {
  position: absolute;
  width: 100%;
}
.gm-combotree-div .input-group-btn > ul {
  min-width: 100%;
  padding: 5px;
}
.gm-combotree-div .input-group-btn button {
  opacity: 0;
  width: 100%;
  z-index: 999;
}
.gm-combotree-div > input {
  text-align: left;
}
.gm-combotree-div .icon {
  position: absolute;
  margin: 6px 0 0 -17px;
  z-index: 999;
}
/*--------------------
       可切换标签
 ---------------------*/
.tab-wrapper {
  width: 100%;
  height: 44px;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}
.tab-wrapper .tab-tool {
  color: #85b4ec;
  width: 30px;
  text-align: center;
  cursor: pointer;
  position: relative;
  background: #1b5fcc;
  z-index: 2;
}
.tab-wrapper .tab-tool i {
  line-height: 44px;
  font-size: 15px;
}
.tab-wrapper .tab-tool:hover {
  background-color: #0251ae;
}
.tab-wrapper .tab-tool.disabled {
  pointer-events: none;
  visibility: hidden;
}
.tab-wrapper .tab-tool.item-hide {
  visibility: hidden;
}
.tab-wrapper .tab-tabs {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  position: relative;
  z-index: 1;
}
.tab-wrapper .tab-tabs > ul {
  list-style-type: none;
  padding: 0;
  margin: 0;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  position: absolute;
  -webkit-transition: all 300ms;
  transition: all 300ms;
  z-index: 1;
  width: 5000px;
}
.tab-wrapper .tab-tabs > ul > li {
  height: 40px;
  margin: 4px 10px 0 0;
  background: #0e6deb;
  color: white;
  width: 130px;
  text-align: center;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  padding: 0 5px;
  cursor: pointer;
}
.tab-wrapper .tab-tabs > ul > li.active {
  background: #0251ae;
  border-top: 2px solid #02e101;
}
.tab-wrapper .tab-tabs > ul > li > .title {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  font-weight: bold;
  line-height: 40px;
}
.tab-wrapper .tab-tabs > ul > li > .close-button {
  -ms-flex-item-align: center;
      align-self: center;
  width: 20px;
  font-size: 20px;
  height: 20px;
  line-height: 20px;
  border-radius: 20px;
  -webkit-transition: all 300ms;
  transition: all 300ms;
}
.tab-wrapper .tab-tabs > ul > li > .close-button:hover {
  color: #fff;
  -webkit-transform: rotate(360deg);
  transform: rotate(360deg);
  background: rgba(0, 0, 0, .35);
}
/*--------------------
    页面通用小控件模块
 ---------------------*/
.tag-container {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}
.tag-container > button {
  min-width: auto;
  margin: 5px 10px 0 0;
  height: 21px;
}
.tag-container .tag-item {
  height: 30px;
  border: 1px solid #e7e7e7;
  margin-right: 10px;
  cursor: pointer;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  padding: 5px 10px;
  position: relative;
}
.tag-container .tag-item:hover {
  border-color: #037ae0;
}
.tag-container .tag-item.checked {
  border-color: #037ae0;
}
.tag-container .tag-item.checked > span {
  color: #037ae0;
}
.tag-container .tag-item.checked .angle {
  border-bottom-color: #037ae0;
}
.tag-container .tag-item > span {
  display: inline-block;
  text-align: center;
}
.tag-container .tag-item .angle {
  border-bottom: 15px solid #e7e7e7;
  border-left: 15px solid transparent;
  height: 0;
  width: 0;
  position: absolute;
  margin-top: 8px;
  margin-left: -5px;
}
.tag-container .tag-item .icon {
  position: absolute;
  margin-top: 13px;
  font-size: 12px;
  -webkit-transform: scale(0.9);
          transform: scale(0.9);
  color: white;
  margin-left: -2px;
}
.tag-container .extra-tags-tool {
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
  height: 30px;
  padding: 5px;
  cursor: pointer;
  white-space: nowrap;
}
.tag-container .extra-tags-tool > span.rotate {
  -webkit-transform: rotate(180deg);
  transform: rotate(180deg);
  -webkit-transition: all 300ms;
  transition: all 300ms;
}
.tag-container .extra-tags-tool .extra-tags {
  position: absolute;
  width: 420px;
  margin-top: 10px;
  margin-left: -170px;
  -webkit-box-shadow: 0 2px 5px rgba(0, 0, 0, .15);
          box-shadow: 0 2px 5px rgba(0, 0, 0, .15);
  background: white;
  padding: 20px 10px 10px 20px;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-flow: wrap;
      flex-flow: wrap;
  z-index: 1;
}
.date-choose-div {
  position: relative;
}
.date-choose-div .icon {
  position: absolute;
  right: 5px;
  top: 9px;
}
/*--------------------
页面通用小控件模块-轮播通知
 ---------------------*/
.carousel-container {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  padding: 5px;
}
.carousel-container .icon-div {
  background: url(" + escape(require("./img/公告.png")) + ");
  height: 18px;
  width: 20px;
  margin: 0 10px 0 0;
}
.carousel-container .text-div {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  height: 18px;
  width: 10px;
  position: relative;
  overflow: hidden;
}
.carousel-container .text-div .swap-animation {
  font-size: 14px;
  line-height: 18px;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  color: white;
}
.carousel-container .text-div .swap-animation span {
  word-break: keep-all;
  white-space: nowrap;
  position: absolute;
  -webkit-transition: 0.5s linear all;
  transition: 0.5s linear all;
}
.carousel-container .text-div .swap-animation.ng-enter {
  top: -50px;
}
.carousel-container .text-div .swap-animation.ng-enter-active {
  top: 0;
}
.carousel-container .text-div .swap-animation.ng-leave {
  top: 0;
}
.carousel-container .text-div .swap-animation.ng-leave-active {
  top: 50px;
}
/*--------------------
       轮播插件
 ---------------------*/
body .rn-carousel-control {
  font-size: 60px;
  color: wheat;
}
/*--------------------
       滑块插件
 ---------------------*/
.ui-slider-tick {
  position: absolute;
  background-color: #aaa;
  width: 2px;
  height: 8px;
  top: 16px;
}
.ui-slider {
  position: relative;
}
.ui-slider-out-of-bounds {
  position: absolute;
  background-color: #EEE;
  height: 100%;
}
.ui-slider-tip {
  position: absolute;
  top: 2px;
  left: 0px;
  width: 21px;
  text-align: center;
  font-size: 0.8em;
}
/*--------------------
       拖拽事件
 ---------------------*/
[ng-drag="true"] {
  cursor: move;
}
