 ---------------------*/
.tab-wrapper {
  width: 100%;
  height: 44px;
  display: flex;
}
.tab-wrapper .tab-tool {
  color: #85b4ec;
  width: 30px;
  text-align: center;
  cursor: pointer;
  position: relative;
  background: #1b5fcc;
  z-index: 2;
}
.tab-wrapper .tab-tool i {
  line-height: 44px;
  font-size: 15px;
}
.tab-wrapper .tab-tool:hover {
  background-color: #0251ae;
}
.tab-wrapper .tab-tool.disabled {
  pointer-events: none;
  visibility: hidden;
}
.tab-wrapper .tab-tool.item-hide {
  visibility: hidden;
}
.tab-wrapper .tab-tabs {
  flex: 1;
  position: relative;
  z-index: 1;
}
.tab-wrapper .tab-tabs > ul {
  list-style-type: none;
  padding: 0;
  margin: 0;
  display: flex;
  position: absolute;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
  z-index: 1;
  width: 5000px;
}
.tab-wrapper .tab-tabs > ul > li {
  height: 40px;
  margin: 4px 10px 0 0;
  background: #0e6deb;
  color: white;
  width: 130px;
  text-align: center;
  display: flex;
  padding: 0 5px;
  cursor: pointer;
}
.tab-wrapper .tab-tabs > ul > li.active {
  background: #0251ae;
  border-top: 2px solid #02e101;
}
.tab-wrapper .tab-tabs > ul > li > .title {
  flex: 1;
  font-weight: bold;
  line-height: 40px;
}
.tab-wrapper .tab-tabs > ul > li > .close-button {
  align-self: center;
  width: 20px;
  font-size: 20px;
  height: 20px;
  line-height: 20px;
  border-radius: 20px;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
.tab-wrapper .tab-tabs > ul > li > .close-button:hover {
  color: #fff;
  -webkit-transform: rotate(360deg);
  -ms-transform: rotate(360deg);
  -o-transform: rotate(360deg);
  transform: rotate(360deg);
  background: rgba(0, 0, 0, 0.35);
}
/*--------------------
