}
.tag-container > button {
  min-width: auto;
  margin: 5px 10px 0 0;
  height: 21px;
}
.tag-container .tag-item {
  height: 30px;
  border: 1px solid #e7e7e7;
  margin-right: 10px;
  cursor: pointer;
  display: flex;
  padding: 5px 10px;
  position: relative;
}
.tag-container .tag-item:hover {
  border-color: #037ae0;
}
.tag-container .tag-item.checked {
  border-color: #037ae0;
}
