        margin: 4px 2px 0 0;
    }
    .icont-folder-open {
        .tree-icon;
        vertical-align: top;
        background-image: url("./img/folder-open.png");
        .tree-new-icon;
        margin: 3px 0 0 0;
    }
    .icont-folder-close {
        .tree-icon;
        background-image: url("./img/folder-close.png");
        vertical-align: top;
        .tree-new-icon;
        margin: 3px 0 0 0;
    }
    .icont-file {
        .tree-icon;
        vertical-align: top;
        background-image: url("./img/file.png");
        .tree-new-icon;
        margin: 3px 0 0 0;
    }
    .tree-a {
        padding-right: 3px;
        margin: 0;
        cursor: pointer;
        height: 21px;
        color: #333;
        background-color: transparent;
        text-decoration: none;
        vertical-align: top;
        display: inline-block;
        font-size: 12px;
        font-family: Verdana, Arial, Helvetica, AppleGothic, sans-serif;
        .tree-span-text {
            line-height: 21px;
            margin-right: 2px;
        }
        .tree-span-text.node-disabled {
            color: grey;
        }
        .tree-span-text.node-error {
            color: red;
        }
        &:hover {
            background-color: #e5e5e5;
        }
    }
    .node-active {
        background-color: #e5e5e5;
    }
    .angular-ui-tree-node .tree-node {
        display: flex;
        >.tree-a {
            text-overflow: ellipsis;
            overflow: hidden;
            white-space: nowrap;
            flex: 1;
        }
    }
    .angular-ui-tree>input {
        margin-bottom: 5px;
        width: 100%;
    }
    .tree-show-icon {
        font-size: 12px !important;
        font-family: ZenIcon !important;
        color: #2A93F7;
        position: absolute;
        right: 0px;
        top: 5px;
    }
    .tree-add-icon {
        font-size: 12px !important;
        font-family: ZenIcon !important;
        color: #2A93F7;
        position: absolute;
        right: 0px;
        top: 5px;
    }
    .tree-delete-icon {
        font-size: 12px !important;
        font-family: ZenIcon !important;
        color: #2A93F7;
        position: absolute;
        right: 15px;
        top: 5px;
    }
    .icon-building,.icon-globe,.icon-table,.icon-home{
        color: #2fa9c7;
    }
}


/*--------------------
       下拉树控件
 ---------------------*/

.gm-combotree-div {
    width: 100%;
    .input-group-btn {
        position: absolute;
        width: 100%;
        >ul {
            min-width: 100%;
            padding: 5px;
        }
        button {
            opacity: 0;
            width: 100%;
            z-index: 999;
        }
    }
    >input {
        text-align: left;
    }
    .icon {
        position: absolute;
        margin: 6px 0 0 -17px;
        z-index: 999;
    }
}


/*--------------------
       可切换标签
 ---------------------*/

.tab-wrapper {
    width: 100%;
    height: 44px;
    display: flex;
    .tab-tool {
        color: rgb(133, 180, 236);
        width: 30px;
        text-align: center;
        cursor: pointer;
        position: relative;
        background: #1b5fcc;
        z-index: 2;
        i {
            line-height: 44px;
            font-size: 15px;
        }
        &:hover {
            background-color: rgb(2, 81, 174);
        }
        &.disabled {
            pointer-events: none;
            visibility: hidden;
        }
        &.item-hide {
            visibility: hidden;
        }
    }
    .tab-tabs {
        flex: 1;
        position: relative;
        z-index: 1;
        >ul {
            list-style-type: none;
            padding: 0;
            margin: 0;
            display: flex;
            position: absolute;
            -webkit-transition: all 300ms;
            -moz-transition: all 300ms;
            -ms-transition: all 300ms;
            -o-transition: all 300ms;
            transition: all 300ms;
            z-index: 1;
            width: 5000px;
            >li {
                height: 40px;
                margin: 4px 10px 0 0;
                background: #0e6deb;
                color: white;
                width: 130px;
                text-align: center;
                display: flex;
                padding: 0 5px;
                cursor: pointer;
                &.active {
                    background: rgb(2, 81, 174);
                    border-top: 2px solid rgb(2, 225, 1);
                }
                >.title {
                    flex: 1;
                    font-weight: bold;
                    line-height: 40px;
                }
                >.close-button {
                    align-self: center;
                    width: 20px;
                    font-size: 20px;
                    height: 20px;
                    line-height: 20px;
                    border-radius: 20px;
                    -webkit-transition: all 300ms;
                    -moz-transition: all 300ms;
                    -ms-transition: all 300ms;
                    -o-transition: all 300ms;
                    transition: all 300ms;
                    &:hover {
                        color: #fff;
                        -webkit-transform: rotate(360deg);
                        -ms-transform: rotate(360deg);
                        -o-transform: rotate(360deg);
                        transform: rotate(360deg);
                        background: rgba(0, 0, 0, .35);
                    }
                }
            }
        }
    }
}


/*--------------------
    页面通用小控件模块
 ---------------------*/

.tag-container {
    display: flex;
    >button {
        min-width: auto;
        margin: 5px 10px 0 0;
        height: 21px;
    }
    .tag-item {
        height: 30px;
        border: 1px solid rgb(231, 231, 231);
        margin-right: 10px;
        cursor: pointer;
        display: flex;
        padding: 5px 10px;
        position: relative;
        &:hover {
            border-color: rgb(3, 122, 224);
        }
        &.checked {
            border-color: rgb(3, 122, 224);
            >span {
                color: rgb(3, 122, 224);
            }
            .angle {
                border-bottom-color: rgb(3, 122, 224);
            }
        }
        >span {
            display: inline-block;
            text-align: center;
        }
        .angle {
            border-bottom: 15px solid #e7e7e7;
            border-left: 15px solid transparent;
            height: 0;
            width: 0;
            position: absolute;
            margin-top: 8px;
            margin-left: -5px;
        }
        .icon {
            position: absolute;
            margin-top: 13px;
            font-size: 12px;
            transform: scale(0.9);
            color: white;
            margin-left: -2px;
        }
    }
    .extra-tags-tool {
        user-select: none;
        height: 30px;
        padding: 5px;
        cursor: pointer;
        white-space: nowrap;
        &>span.rotate {
            -webkit-transform: rotate(180deg);
            -ms-transform: rotate(180deg);
            -o-transform: rotate(180deg);
            transform: rotate(180deg);
            -webkit-transition: all 300ms;
            -moz-transition: all 300ms;
            -ms-transition: all 300ms;
            -o-transition: all 300ms;
            transition: all 300ms;
        }
        .extra-tags {
            position: absolute;
            width: 420px;
            margin-top: 10px;
            margin-left: -170px;
            box-shadow: 0 2px 5px rgba(0, 0, 0, 0.15);
            background: white;
            padding: 20px 10px 10px 20px;
            display: flex;
            flex-flow: wrap;
            z-index: 1;
        }
    }
}

.date-choose-div {
    position: relative;
    .icon {
        position: absolute;
        right: 5px;
        top: 9px;
    }
}


/*--------------------
页面通用小控件模块-轮播通知
 ---------------------*/

.carousel-container {
    display: flex;
    padding: 5px;
    .icon-div {
        background: url(img/公告.png);
        height: 18px;
        width: 20px;
        margin: 0 10px 0 0;
    }
    .text-div {
        flex: 1;
        height: 18px;
        width: 10px;
        position: relative;
        overflow: hidden;
        .swap-animation {
            font-size: 14px;
            line-height: 18px;
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            color: white;
            span {
                word-break: keep-all;
                white-space: nowrap;
                position: absolute;
                transition: 0.5s linear all;
            }
        }
        .swap-animation.ng-enter,
        .swap-animation.ng-leave {
            //transition:0.5s linear all;
        }
        .swap-animation.ng-enter {
            top: -50px;
        }
        .swap-animation.ng-enter-active {
            top: 0;
        }
        .swap-animation.ng-leave {
            top: 0;
        }
        .swap-animation.ng-leave-active {
            top: 50px;
        }
    }
}


/*--------------------
       轮播插件
 ---------------------*/

body .rn-carousel-control {
    font-size: 60px;
    color: wheat;
}


/*--------------------
       滑块插件
 ---------------------*/

.ui-slider-tick {
    position: absolute;
    background-color: #aaa;
    width: 2px;
    height: 8px;
    top: 16px;
}

.ui-slider {
    position: relative;
}

.ui-slider-out-of-bounds {
    position: absolute;
    background-color: #EEE;
    height: 100%;
}

.ui-slider-tip {
    position: absolute;
    top: 2px;
    left: 0px;
    width: 21px;
    text-align: center;
    font-size: 0.8em;
}

/*--------------------
       拖拽事件
 ---------------------*/
[ng-drag="true"]{
    cursor: move;
