.ui-grid-cell-contents.operate a {
  padding: 0 5px;
}
.grid.ui-grid {
  border: none;
}
/*----------------------------
        向导插件样式
 -----------------------------*/
.ui-tour-backdrop {
  background-color: rgba(0, 0, 0, 0.5);
  fill: rgba(0, 0, 0, 0.5);
}
