    padding-top: 12px !important;
  }
  .query-form header h4 {
    padding: 10px 0px 10px 10px !important;
  }
  .query-form .query-content {
    padding: 0px 8px !important;
  }
  .query-form .query-content .tool-bar {
    height: 44px !important;
  }
  .query-form .query-content .query-condition {
    padding: 8px 0px 0px 0px!important;
  }
  .query-form .query-content .query-condition .query-tool {
    padding-left: 40px !important;
  }
  .query-form .query-content .tool-bar .tool-tag .tag-container {
    margin: auto !important;
  }
  .query-form .query-content .tool-buttons .btn-link {
    font-size: 14px !important;
    border: none !important;
  }
  .query-form .query-content .content-table table > tbody > tr:nth-child(even) > td,
  .query-form .query-content .content-table table > tbody > tr:nth-child(even) > th {
    background-color: #f9f9f9;
  }
  .query-form .query-content .content-table .table-striped > tbody > tr:nth-child(odd) > td,
  .query-form .query-content .content-table .table-striped > tbody > tr:nth-child(odd) > th {
    background-color: #ffffff !important;
  }
  .query-form .query-content .app-content ul li > span > div > div > div.title {
    height: 25px !important;
    line-height: 25px !important;
  }
  .query-form .query-content .app-content ul li > span > div > div > div.image {
    width: 40% !important;
    padding-top: 40% !important;
  }
  .query-form .query-content .app-content ul li > span > div > div > div.image:hover {
    width: 45% !important;
    padding-top: 45% !important;
  }
  .ngdialog.ngdialog-theme-default.custom.sm .ngdialog-content {
    width: 570px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content {
    width: 900px !important;
    height: 450px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-header {
    padding: 10px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-content {
    height: 300px !important;
    padding: 0px 30px 10px 30px!important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .nav-tabs {
    margin: 0px 10px;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .gm-pagination .pager-loose {
    margin: 10px 0px !important;
    line-height: 1em;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .gm-pagination .pager-loose > li > a {
    padding: 3px 9px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .gm-pagination .page-tool {
    margin: 13px 0 0 10px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-label {
    padding: 13px 8px 0px 8px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-label label {
    margin-bottom: 0px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value {
    padding: 13px 50px 0px 8px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value .form-control {
    height: 25px !important;
    line-height: 1 !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value .gm-tree-div {
    min-height: 50px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value .gm-tree-div .tree-a {
    font-size: 13px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple tr.catagory span {
    margin-top: 10px !important;
  }
}
.theme-choosed {
  background: #145ccd;
}
.theme-choosed > a {
  color: #ffffff !important;
}
