            框架样式
 -----------------------------*/
html {
  font-family: "Microsoft Yahei", "Droid Sans Fallback", "Arial", "Helvetica", "sans-serif", "宋体";
  font-size: 14px;
  outline: none;
  color: #373d46;
  background-color: #fafafa;
}
.frame2 {
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}
.frame2 > header {
  height: 124px;
  z-index: 1;
  user-select: none;
}
.frame2 > header.full-screen {
  height: 44px;
}
.frame2 .frame-content {
  display: flex;
  flex: 1 1 auto;
  height: 1px;
  z-index: 0;
}
.frame2 > footer {
  height: 0;
}
.frame2 .notify {
  position: absolute;
  right: 400px;
  width: 300px;
  align-self: center;
  top: 25px;
}
.frame2 .notify.full-screen {
  top: 6px;
}
.header-up {
  height: 80px;
  display: flex;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
.header-up.full-screen {
  height: 0;
}
.header-up.full-screen * {
  display: none;
}
.header-up .system-info {
  color: #ffffff;
  display: flex;
}
.header-up .system-info .logo img {
  height: 100%;
  width: 80px;
}
.header-up .system-info .system-name {
  padding: 16px 0 0 10px;
}
.header-up .system-info .system-name > div:first-child {
  font-size: 20px;
  font-weight: bold;
}
.header-up .tools {
  flex: 1;
}
.header-up .tools .theme-option {
  height: 100%;
  color: white;
  text-align: right;
  padding-right: 30px;
  display: flex;
}
.header-up .tools .theme-option .dropdown .dropdown-toggle {
  line-height: 80px;
  margin-left: 10px;
  flex: 1;
  padding: 0 10px;
  cursor: pointer;
  height: 80px;
}
.header-up .tools .theme-option .dropdown ul li .theme-item {
  float: left;
  background: red;
  border-radius: 50px;
  padding: 2px;
  border: 2px solid white;
  margin-right: 5px;
  height: 20px;
  width: 20px;
  align-self: center;
  text-align: center;
  cursor: pointer;
}
.header-up .tools .theme-option .dropdown ul li .theme-item i {
  position: relative;
  top: -3px;
}
.header-up .user-info {
  width: 180px;
  color: white;
}
.header-up .user-info > .btn-group {
  display: flex;
}
.header-up .user-info > .btn-group .user-img {
  height: 60px;
  width: 60px;
  background: aliceblue;
  border-radius: 30px;
  margin-top: 10px;
}
.header-up .user-info > .btn-group .user-img > div {
  height: 54px;
  width: 54px;
  background: red;
  border-radius: 30px;
  margin: 3px;
}
.header-up .user-info > .btn-group .dropdown-toggle {
  line-height: 80px;
  margin-left: 10px;
  flex: 1;
  padding: 0 10px;
  cursor: pointer;
  height: 80px;
}
.header-up .user-info > .btn-group .dropdown-toggle > span:first-child {
  max-width: 65px;
  display: inline-block;
  line-height: 80px;
}
.header-up .user-info > .btn-group .dropdown-toggle > span:last-child {
  font-size: 12px;
  padding-left: 5px;
  display: inline-block;
  position: absolute;
  top: 33px;
}
.header-down {
  height: 44px;
  background: #1b5fcc;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.15);
  display: flex;
}
.header-down .menu-header {
  width: 255px;
  background-color: #2d3846;
  text-align: center;
  text-transform: uppercase;
  color: #ffffff;
  padding-top: 13px;
  position: relative;
  z-index: 2;
  pointer-events: none;
}
.header-down .menu-header.full-screen {
  width: 100px;
  cursor: pointer;
  pointer-events: initial;
}
.header-down .menu-header.full-screen:hover {
  background-color: #047adf;
}
.header-down .tab-header {
  flex: 1;
  display: flex;
}
.header-down .tab-header .tool-item {
  color: #85b4ec;
  width: 30px;
  text-align: center;
  cursor: pointer;
  background: #1b5fcc;
  position: relative;
  z-index: 1;
}
.header-down .tab-header .tool-item i {
  line-height: 44px;
  font-size: 15px;
}
.header-down .tab-header .tool-item:hover {
  background-color: #0251ae;
}
.header-down .tab-header .tool-tab {
  flex: 1;
}
.frame-content .menu {
  height: 100%;
  width: 255px;
  cursor: pointer;
  user-select: none;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
  overflow-y: auto;
  overflow-x: hidden;
}
.frame-content .menu.full-screen {
  position: absolute;
  z-index: 999;
  top: 44px;
}
.frame-content .menu.hidden1 {
  transform: translateY(-120%);
}
.frame-content .menu .menu-item > .nav {
  height: 63px;
  font-size: 14px;
  padding: 20px 0 0 30px;
}
.frame-content .menu .menu-item > .nav > i {
  font-size: 20px;
}
.frame-content .menu .menu-item > .nav > .nav-icon {
  height: 20px;
  margin-top: -6px;
}
.frame-content .menu .menu-item > .nav > .title {
  padding-left: 10px;
}
.frame-content .menu .menu-item > .nav > span.icon {
  float: right;
  position: relative;
  top: 5px;
  right: 30px;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
.frame-content .menu .menu-item > .nav > span.icon.click-down {
  -webkit-transform: rotate(180deg);
  -ms-transform: rotate(180deg);
  -o-transform: rotate(180deg);
  transform: rotate(180deg);
}
.frame-content .menu .menu-item .sub-item {
  list-style-type: none;
  margin: 0;
  padding: 0;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
.frame-content .menu .menu-item .sub-item.hidden1 {
  height: 0 !important;
}
.frame-content .menu .menu-item .sub-item.hidden1 li {
  display: none;
}
.frame-content .menu .menu-item .sub-item > li {
  width: 255px;
  height: 55px;
  display: flex;
  padding-top: 18px;
}
.frame-content .menu .menu-item .sub-item > li .subitem-icon {
  padding-left: 50px;
}
.frame-content .menu .menu-item .sub-item > li .subitem-icon .nav-icon {
  height: 15px;
}
.frame-content .menu .menu-item .sub-item > li .subitem-title {
  flex: 1;
  padding-left: 10px;
}
.frame-content .menu .menu-item .sub-item > li:hover {
  background-color: #047adf;
}
.frame-content .menu .menu-item .sub-item > li.active {
  background-color: #047adf;
}
.frame-content .content {
  margin: 10px;
  -webkit-box-shadow: 0 0 5px #eaeaea;
  -moz-box-shadow: 0 0 5px #eaeaea;
  box-shadow: 0 0 5px #eaeaea;
  flex: 1;
  border: 1px solid #dfdfdf;
  height: calc(100% - 15px);
  width: 500px;
  background: white;
  position: relative;
}
.frame-content .content .view-content {
  height: 100%;
  overflow-y: auto;
  overflow-x: hidden;
}
.frame-content .content .view-content > div {
  height: 100%;
}
/*----------------------------
            常用页面样式
 -----------------------------*/
.under-design {
  height: 100%;
  background: #a6c3de;
  text-align: center;
  display: flex;
}
.under-design .page-content {
  margin: auto;
}
.under-design i {
  font-size: 120px;
  color: rgba(0, 0, 0, 0.2);
  align-self: center;
}
.under-design .title {
  color: #FFF;
  font-size: 60px;
  text-align: center;
}
/*----------------------------
            通用样式
 -----------------------------*/
.btn {
  border-radius: 0;
  min-width: 90px;
}
.ui-grid-top-panel {
  background: #f5f5f5 !important;
}
/*----------------------------
            插件补充样式
 -----------------------------*/
#loading-bar .bar {
  top: auto !important;
  left: auto !important;
  background-color: red;
}
#loading-bar-spinner {
  color: red;
  top: auto !important;
  left: auto !important;
}
.fr-box.fr-basic .fr-element {
  white-space: normal;
  word-break: break-all;
}
/*----------------------------
            兼容低分屏样式
 -----------------------------*/
@media screen and (min-width: 1366px) and (max-width: 1600px) {
  .query-form > header > h4 {
    padding: 10px;
  }
  .ngdialog.ngdialog-theme-default {
    padding-top: 80px;
    padding-bottom: 80px;
  }
}
.pageload-overlay {
  position: absolute;
}
.pageload-overlay::after,
.pageload-overlay::before {
  position: absolute;
}
@media screen and (max-width: 1366px) {
  .frame2 > header {
    height: 114px !important;
  }
  .frame2 > header.full-screen {
    height: 44px !important;
  }
  .header-up {
    height: 70px !important;
  }
  .header-up .system-info .logo img {
    width: 70px !important;
  }
  .header-up .tools .theme-option > span {
    line-height: 70px !important;
  }
