html {
  font-family: "Microsoft Yahei", "Droid Sans Fallback", "Arial", "Helvetica", "sans-serif", "宋体";
  font-size: 14px;
  outline: none;
  color: #373d46;
  background-color: #fafafa;
}
.frame3 {
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}
.frame3 > header {
  height: 80px;
  z-index: 1;
  user-select: none;
}
.frame3 > header.full-screen {
  height: 0;
}
.frame3 .frame-content {
  display: flex;
  flex: 1 1 auto;
  height: 1px;
  z-index: 0;
}
.frame3 > footer {
  height: 0;
