  color: white;
  position: relative;
  z-index: 9999;
  background: #0e6fea;
  padding: 0 0 0 50px;
}
.frame3-header .user-info > .btn-group {
  display: flex;
}
.frame3-header .user-info > .btn-group .user-img {
  height: 60px;
  width: 60px;
  background: aliceblue;
  border-radius: 30px;
  margin-top: 10px;
}
.frame3-header .user-info > .btn-group .user-img > div {
  height: 54px;
  width: 54px;
  background: red;
  border-radius: 30px;
  margin: 3px;
}
