  width: 100%;
  height: 100%;
  overflow: hidden;
}
a{
    text-decoration: none;
}
a:hover{
    text-decoration: none;
}

[ng-controller="systemCenterCtrl"]{
  height: 100%;
}
//登录页样式
#myBanner{
    display: block;
    width: 100%;
    height: calc(~"100% - 60px");
    .backImg{
        width: 100%;
        height: 100%;
    }
    .title{
        position: absolute;
        z-index: 100;
        color: #fff;
        width: 100%;
        height: 125px;
        top: 5%;
        text-align: center;
        .logBox{
            display: inline-block;
            width:120px;
            height: 120px;
            img{
                display: block;
                width: 100%;
                height: auto;
                padding-top: 26px;
            }
        }
        .titleText{
            display: inline-block;
            p:nth-of-type(1){
                font: .4rem/.5rem "微软雅黑";
                text-shadow: 0 2px 5px #000;
                margin: 0 0 5px 0;
            }
            p:nth-of-type(2){
                font: .16rem/.2rem "微软雅黑";
                opacity: .6;
                filter: alpha(opacity=60);
            }
        }
    }
    .logOut{
        position: absolute;
        right: 0;
        top: 5px;
        z-index: 100;
        cursor: pointer;
        .userImg{
            width: 35px;
            height: 35px;
            margin-right: 18px;
        }
        .userName{
            margin-right: 6px; 
            color: #fff;
        }
        .icon-chevron-down{
            color: #fff;
            margin-right: 10px;
        }
        .message{
            padding-left: 0;
            margin-top: 10px;
            list-style: none;
            li{
                line-height: 20px;
                text-align: center;
            }
        }
    }
}
#metroBox,#moreMetroBox{
    position: absolute;
    width: 60%;
    height: 55%;
    top: 28%;
    left: 50%;
    margin-left: -28.5%;
    .subBox{
        float: left;
        width: 23%;
        height: 33%;
        margin: .7% 2% .7% 0;
        box-sizing: border-box;
        padding: 5px 10px;
        cursor: pointer;
        transition: all .3s ease-in-out; 
        -webkit-transition: all .3s ease-in-out;
        -ms-transition: all .3s ease-in-out;
        .imgBox{
            width: 100%;
            height: 80%;
            line-height: 80%;
            justify-content:center;
            align-items:center;
            display:flex;
        }
        .txtBox{
            text-align: center;
            span{
                color: #fff;
            }
        }
    }
    .width2{
        width: 48% !important;
    }
    .width3{
        width: 73% !important;
    }
    .width4{
        width: 98% !important;
    }
    .height2{
        height: 69% !important;
    }
    .height3{
        height: 105.5% !important;
    }
    .subBox:hover{
        transform: scale(1.002);
        -webkit-transform: scale(1.002);
        -ms-transform: scale(1.002);
        box-shadow: 0 0 20px rgba(0,0,0,.5);
        font-stretch:normal;
    }
    .disabled{
        cursor: initial;
        background: rgba(150,150,150,.7) !important;
        .txtBox{
            span{
                color: #EAEAEA;
            }
        }
    }
    .disabled:hover{
        transform: scale(1) !important;
        -webkit-transform: scale(1) !important;
        -ms-transform: scale(1) !important;
        box-shadow:none !important;
    }
    .subBox:nth-of-type(1){
        background: rgb(255,183,107); /* Old browsers */
        background: rgb(203,96,179); /* Old browsers */
        background: -moz-linear-gradient(-45deg, rgba(203,96,179,1) 0%, rgba(193,70,161,1) 9%, rgba(168,0,119,1) 64%, rgba(219,54,164,1) 100%); /* FF3.6-15 */
        background: -webkit-linear-gradient(-45deg, rgba(203,96,179,1) 0%,rgba(193,70,161,1) 9%,rgba(168,0,119,1) 64%,rgba(219,54,164,1) 100%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(203,96,179,1) 0%,rgba(193,70,161,1) 9%,rgba(168,0,119,1) 64%,rgba(219,54,164,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#cb60b3', endColorstr='#db36a4',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
    .subBox:nth-of-type(2){
        background: rgb(0,140,0); /* Old browsers */
        background: -moz-linear-gradient(-45deg, rgba(0,140,0,1) 0%, rgba(1,151,0,1) 49%, rgba(1,163,0,1) 100%); /* FF3.6-15 */
        background: -webkit-linear-gradient(-45deg, rgba(0,140,0,1) 0%,rgba(1,151,0,1) 49%,rgba(1,163,0,1) 100%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(0,140,0,1) 0%,rgba(1,151,0,1) 49%,rgba(1,163,0,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#008c00', endColorstr='#01a300',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
    .subBox:nth-of-type(3){
        background: rgb(252,234,187); /* Old browsers */
        background: -moz-linear-gradient(-45deg, rgba(252,234,187,1) 0%, rgba(248,181,0,1) 0%, rgba(248,181,0,1) 2%, rgba(252,205,77,1) 100%, rgba(251,223,147,1) 100%, rgba(252,205,77,1) 101%); /* FF3.6-15 */
        background: -webkit-linear-gradient(-45deg, rgba(252,234,187,1) 0%,rgba(248,181,0,1) 0%,rgba(248,181,0,1) 2%,rgba(252,205,77,1) 100%,rgba(251,223,147,1) 100%,rgba(252,205,77,1) 101%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(252,234,187,1) 0%,rgba(248,181,0,1) 0%,rgba(248,181,0,1) 2%,rgba(252,205,77,1) 100%,rgba(251,223,147,1) 100%,rgba(252,205,77,1) 101%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#fceabb', endColorstr='#fccd4d',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
    .subBox:nth-of-type(4){
        background: rgb(143,0,150); /* Old browsers */
        background: -moz-linear-gradient(-45deg, rgba(143,0,150,1) 0%, rgba(154,0,161,1) 50%, rgba(162,0,171,1) 100%); /* FF3.6-15 */
        background: -webkit-linear-gradient(-45deg, rgba(143,0,150,1) 0%,rgba(154,0,161,1) 50%,rgba(162,0,171,1) 100%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(143,0,150,1) 0%,rgba(154,0,161,1) 50%,rgba(162,0,171,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#8f0096', endColorstr='#a200ab',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
    .subBox:nth-of-type(5){
        background: rgb(254,187,187); /* Old browsers */
        background: -moz-linear-gradient(-45deg, rgba(254,187,187,1) 0%, rgba(254,144,144,1) 19%, rgba(255,92,92,1) 48%); /* FF3.6-15 */
        background: -webkit-linear-gradient(-45deg, rgba(254,187,187,1) 0%,rgba(254,144,144,1) 19%,rgba(255,92,92,1) 48%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(254,187,187,1) 0%,rgba(254,144,144,1) 19%,rgba(255,92,92,1) 48%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#febbbb', endColorstr='#ff5c5c',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
    .subBox:nth-of-type(6){
        background: rgb(81,52,171); /* Old browsers */
        background: -moz-linear-gradient(-45deg, rgba(81,52,171,1) 0%, rgba(93,58,184,1) 50%, rgba(99,62,191,1) 100%); /* FF3.6-15 */
        background: -webkit-linear-gradient(-45deg, rgba(81,52,171,1) 0%,rgba(93,58,184,1) 50%,rgba(99,62,191,1) 100%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(135deg, rgba(81,52,171,1) 0%,rgba(93,58,184,1) 50%,rgba(99,62,191,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#5134ab', endColorstr='#633ebf',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
