    /**
     * @description 判断两个对象是否一致，只判断值
     * @param {object} pObj1 对象1
     * @param {object} pObj2 对象2
     * @return {Boolean} 是否相同
     */
    compareObject: function compareObject(pObj1, pObj2) {
        var pResult = true;
        pResult = this._equalObject(pObj1, pObj2);
        if (pResult === true) {
            pResult = this._equalObject(pObj2, pObj1);
        }
        return pResult;
    },
    /**
     * @description 判断两个对象是否一致，只判断值
     * @param {Object} pObj1 比较对象1
     * @param {Object} pObj2
     * @return {boolean}
     */
    _equalObject: function _equalObject(pObj1, pObj2) {
        if (!this.isObject(pObj1) || !this.isObject(pObj2)) {
            return false;
        }
        var bRt = true;
        for (var key in pObj1) {
            if (this.isObject(pObj1[key])) {
                bRt = this._isSameObject(pObj1[key], pObj2[key]);
                if (bRt === false) {
                    bRt = false;
                    return bRt;
                }
            } else {
                if (pObj1[key] !== pObj2[key]) {
                    return false;
                }
            }
        }
        return bRt;
    },
    /**
     * @description 判断字符串是否为空
     * @method isStrNullOrEmpty
     * @param {String} str 字符串
     * @return {Boolean} 是否为空
     */
    isStrNullOrEmpty: function isStrNullOrEmpty(str) {
        if (str == null) {
            return true;
        }
        if (!this.isString(str)) {
            return false;
        }
        var bEmpty = true;
        var pTempStr = GM.CommonOper.trimStr(str);
        for (var ii = 0; ii < pTempStr.length; ii++) {
            var pTempChar = pTempStr[ii];
            if (pTempChar !== ' ' && pTempChar !== '↵') {
                bEmpty = false;
                break;
            }
        }
        return bEmpty;
    },
    /**
     * @description 去除字符串的前后空格，包含回车字符
     * @method trimStr
     * @param {String} str 字符串
     * @return {String} 处理后的字符串
     */
    trimStr: function trimStr(str) {
        if (str == null || !this.isString(str)) {
            return str;
        }

        var pTempStr = str.replace(/(^\r\n*)|(\r\n*$)/g, '');
        pTempStr = pTempStr.replace(/(^\n*)|(\n*$)/g, '');
        pTempStr = pTempStr.replace(/(^\s*)|(\s*$)/g, '');
        pTempStr = pTempStr.replace(/(^\r*)|(\r*$)/g, '');
        return pTempStr;
    },
    /**
     * 去除字符串前后指定字符
     * @method trimStrWithChar
     * @param {String} strValue 要处理的字符串
     * @param {String} removeChar 要删除的指定字符
     * @return {String} 处理后的字符串
     */
    trimStrWithChar: function trimStrWithChar(strValue, removeChar) {
        if (strValue == null || strValue.length < 1 || removeChar == null) {
            return strValue;
        }
        for (var ii = 0; ii < strValue.length; ii++) {
            if (strValue[ii] === removeChar) {
                strValue = strValue.substring(1);
            } else {
                break;
            }
        }
        for (var jj = strValue.length - 1; jj >= 0; jj--) {
            if (strValue[jj] === removeChar) {
                strValue = strValue.substring(0, strValue.length - 1);
            } else {
                break;
            }
        }
        return strValue;
    },
    /**
     * 裁剪字符串前面匹配字符串
     * @param {String} str 被裁剪字符串
     * @param {String} c 裁剪字符串
     * @returns {String} 结果字符串
     */
    trimStart: function trimStart(str, c) {
        if (!c || c == null || c === '') {
            return str;
        } else {
            var rg = new RegExp(c);
            var i = -1;
            while (rg.test(str.charAt(++i))) {}
            return str.slice(i, this.length - i);
        }
    },
    /**
     * 裁剪字符串后面匹配字符串
     * @param {String} str 被裁剪字符串
     * @param {String} c 裁剪字符串
     * @returns {String} 结果字符串
     */
    trimEnd: function trimEnd(str, c) {
        if (!c || c == null || c === '') {
            return c;
        } else {
            var rg = new RegExp(c);
            var i = str.length;
            while (rg.test(str.charAt(--i))) {}
            return str.slice(0, i + 1);
        }
    },
    /**
     * @description 截取字符串数组中的字符串元素
     * @method trimArrayStr
     * @param {Array} strArray 字符串数组
     */
    trimArrayStr: function trimArrayStr(strArray) {
        if (GM.CommonOper.isArray(strArray)) {
            for (var ii = 0; ii < strArray.length; ii++) {
                strArray[ii] = GM.CommonOper.trimStr(strArray[ii]);
            }
        }
    },
    /**
     * @description 简化数组，将数组元素中的数组拆分开
     * @method simplifyArray
     * @param {Array} pArray 数组
     * @return {Array} 简化后的数组
     */
    simplifyArray: function simplifyArray(pArray) {
        if (pArray == null) {
            return null;
        }
        var pResult = [];
        for (var ii = 0; ii < pArray.length; ii++) {
            var pItem = pArray[ii];
            var bArray = GM.CommonOper.isArray(pItem);
            if (bArray === true) {
                var pTempResult = GM.CommonOper.simplifyArray(pItem);
                if (pTempResult) {
                    pResult = pResult.concat(pTempResult);
                }
            } else {
                if (pItem) {
                    pResult.push(pItem);
                }
            }
        }
        return pResult;
    },
