    /**
     * @description 按顺序获取字典键数组
     * @method getSortDic
     * @param {Array} 要处理的数组
     * @return {Array} 处理好的数组
     */
    getSortDic: function getSortDic(pDic) {
        var pResult = [];
        if (pDic) {
            for (var key in pDic) {
                pResult.push(key);
            }
        } else {
            return null;
        }
        return pResult.sort(function (ele1, ele2) {
            try {
                if (!GM.CommonOper.isStartsWith(ele1, '0') && GM.CommonOper.isDigital(ele1) && !GM.CommonOper.isStartsWith(ele2, '0') && GM.CommonOper.isDigital(ele2)) {
                    return Number(ele1) - Number(ele2);
                } else {
                    return ele1 - ele2;
                }
            } catch (ex) {
                return -1;
            }
        });
