        var arrayNodes = GM.TreeOper.getAllNodes(this.region, 'childrens');
        var result = null;
        if (arrayNodes) {
            for (var ii = 0; ii < arrayNodes.length; ii++) {
                var pNode = arrayNodes[ii];
                if (pNode.code === strCode) {
                    result = pNode.title;
                    break;
                }
            }
        }
