/**
 * 楼盘表模块
 * @class NgModule.houseTable
 * @alias gm.ngCustom.houseTable
 * @author LP
 */
angular.module('gm.ngCustom.houseTable', []).directive('gmHousetable', [
/**
 *
 * ```
 * <gm-housetable arrayhouse="houseTable" viewInfoFun = "viewHouseInfo" zddm="zddm" buildid="buildId"></gm-housetable>
 * ```
 *
 * @method gmHousetable 楼盘表指令
 * @member NgModule.houseTable
 * @param {Array} arrayhouse 作用域传值，楼盘表数据集合
 * @param {String} viewinfofun 作用域传值，查看户信息函数名称
 * @param {String} zddm 作用域传值，宗地代码值
 * @param {String} buildid 作用域传值，幢ID值
 */
function () {
    return {
        restrict: 'ECA',
        template: require('./house-table.tpl.html'),
        scope: {
            arrayhouse: '=',
            viewinfofun: '=',
            zddm: '=',
            buildid: '='
        },
        link: function link(scope, element, attr, transclution) {
            /**
             * @class GM.OA.Housetable
             * @ignore
             */
            GM.OA.Housetable = {
                maintainData: function maintainData(pArrayHouse) {
                    if (pArrayHouse == null) {
                        return;
                    }
                    this._formatHouseTable(pArrayHouse);
                    this._initTwoDimensionArray();
                    this._initNoUnitData();
                },
                /**
                 * @description 图例数据
                 */
                legendData: [{
                    name: '查封',
                    color: '#CC3232'
                }, {
                    name: '异议',
                    color: '#FFCB17'
                }, {
                    name: '预告',
                    color: '#9967CA'
                }, {
                    name: '抵押',
                    color: '#3566CC'
                }, {
                    name: '地役权',
                    color: '#48DDE0'
                }, {
                    name: '所有权',
                    color: '#329737'
                }, {
                    name: '公共部位',
                    color: '#B2A056'
                }],
                /**
                 * @description 初始化数据到二维表中
                 */
                _initTwoDimensionArray: function _initTwoDimensionArray() {
                    var arrayHouse = this.unitHouseData;
                    var ii;
                    var nMaxSumHuhao = 0;
                    var pHouseInfo;

                    if (arrayHouse == null || arrayHouse.length === 0) {
                        return;
                    }
                    this.nMaxCenHao = this.nMinCenhao = Number(arrayHouse[0].sjc);
                    //获取最大层号和单元号
                    this.ArrayUnitHouseCount = [];
                    for (ii = 0; ii < arrayHouse.length; ii++) {
                        pHouseInfo = arrayHouse[ii];
                        var nTempCNum = Number(pHouseInfo.sjc);
                        if (nTempCNum < this.nMinCenhao) {
                            this.nMinCenhao = nTempCNum;
                        } else if (nTempCNum > this.nMaxCenHao) {
                            this.nMaxCenHao = nTempCNum;
                        }
                        var nUnitNum = pHouseInfo.dyh;
                        if (!this.ArrayUnitHouseCount[nUnitNum] || this.ArrayUnitHouseCount[nUnitNum] < pHouseInfo.hh) {
                            this.ArrayUnitHouseCount[nUnitNum] = pHouseInfo.hh;
                        }
                        pHouseInfo.unit = nUnitNum;
                        //获取房屋的类型
                        pHouseInfo.houseLegend = this._getHouseLegend(pHouseInfo);
                    }

                    for (ii = 0; ii < this.ArrayUnitHouseCount.length; ii++) {
                        if (this.ArrayUnitHouseCount[ii]) {
                            nMaxSumHuhao += this.ArrayUnitHouseCount[ii];
                        }
                    }
                    //初始化二维数组
                    var nFloorCount = this.nMaxCenHao - this.nMinCenhao;
                    if (nFloorCount === 0) {
                        nFloorCount = 1;
                    } else if (this.nMinCenhao > 0 || this.nMaxCenHao < 0) {
                        nFloorCount += 1;
                    }
                    this.unitHouseData = {
                        nMaxFloorNum: this.nMaxCenHao,
                        nMinFloorNum: this.nMinCenhao
                    };
                    for (ii = 0; ii < arrayHouse.length; ii++) {
                        pHouseInfo = arrayHouse[ii];
                        var nHouseCh = Number(pHouseInfo.sjc);
                        if (this.unitHouseData[nHouseCh] == null) {
                            this.unitHouseData[nHouseCh] = new Array(nMaxSumHuhao);
                        }

                        var nTempY = this._getHDimentionNumber(pHouseInfo.hh, pHouseInfo.unit);
                        this.unitHouseData[nHouseCh][nTempY] = pHouseInfo;
                    }

                    //维护scope
                    //填充空数据
                    var pResult = [];
                    for (ii = this.nMaxCenHao; ii <= this.nMaxCenHao; ii++) {
                        if (this.unitHouseData[ii]) {
                            this.unitHouseData[ii].sjc = ii;
                            pResult.push(this.unitHouseData[ii]);
                        } else if (ii !== 0) {
                            var tempData = new Array(nMaxSumHuhao);
                            tempData.sjc = ii;
                            pResult.push(tempData);
                        }
                    }
                    this.scope.unitHouseData = pResult;
                    this.scope.ArrayUnitHouseCount = this.ArrayUnitHouseCount;
                },
                /**
                 * @description 初始化没有单元号的数据
                 * @method _initNoUnitData
                 */
                _initNoUnitData: function _initNoUnitData() {
                    var arrayHouse = this.noUnitHouseData;
                    var ii;
                    if (arrayHouse == null || arrayHouse.length === 0) {
                        return;
                    }
                    var nMaxFloorNum = Number(arrayHouse[0].sjc);
                    var nMinFloorNum = Number(arrayHouse[0].sjc);
                    var nMaxHh = 0;
                    for (ii = 0; ii < arrayHouse.length; ii++) {
                        var pHouseData = arrayHouse[ii];
                        var nCh = Number(pHouseData.sjc);
                        if (nCh > nMaxFloorNum) {
                            nMaxFloorNum = nCh;
                        } else if (nCh < nMinFloorNum) {
                            nMinFloorNum = nCh;
                        }
                        if (nMaxHh < pHouseData.hh) {
                            nMaxHh = pHouseData.hh;
                        }
                        pHouseData.houseLegend = this._getHouseLegend(pHouseData);
                    }

                    //初始化二维数组
                    var nFloorCount = nMaxFloorNum - nMinFloorNum;
                    if (nFloorCount === 0) {
                        nFloorCount = 1;
                    } else if (nMinFloorNum > 0 || nMaxFloorNum < 0) {
                        nFloorCount += 1;
                    }
                    this.noUnitHouseData = {
                        nMaxFloorNum: nMaxFloorNum,
                        nMinFloorNum: nMinFloorNum
                    };
                    for (ii = 0; ii < arrayHouse.length; ii++) {
                        var pHouseInfo = arrayHouse[ii];
                        var nHouseCh = Number(pHouseInfo.sjc);
                        if (this.noUnitHouseData[nHouseCh] == null) {
                            this.noUnitHouseData[nHouseCh] = new Array(nMaxHh);
                        }
                        var nTempY = pHouseInfo.hh - 1;
                        this.noUnitHouseData[nHouseCh][nTempY] = pHouseInfo;
                    }

                    //维护scope
                    //填充空数据
                    var pResult = [];
                    for (ii = nMinFloorNum; ii <= nMaxFloorNum; ii++) {
                        if (this.noUnitHouseData[ii]) {
                            this.noUnitHouseData[ii].sjc = ii;
                            pResult.push(this.noUnitHouseData[ii]);
                        } else if (ii !== 0) {
                            var tempData = new Array(nMaxHh);
                            tempData.sjc = ii;
                            pResult.push(tempData);
                        }
                    }
                    this.scope.noUnitHouseData = pResult;
                    if (this.noUnitHouseData != null) {
                        for (var key in this.noUnitHouseData) {
                            if (key !== 'nMaxFloorNum' && key !== 'nMinFloorNum') {
                                this.scope.noUnitColspan = this.noUnitHouseData[key].length;
                                break;
                            }
                        }
                    }
                },
                /**
                 * @description 获取户对应的登记类型
                 * @method _getHouseLegend
                 * @param pHouseInfo
                 * @return {String} 登记类型
                 */
                _getHouseLegend: function _getHouseLegend(pHouseInfo) {
                    if (pHouseInfo == null) {
                        return null;
                    }
                    var strKey = '';
                    if (pHouseInfo.ggbw != null && pHouseInfo.ggbw !== '' && pHouseInfo.ggbw !== '0') {
                        //不动产单元为公共部位
                        strKey = '公共部位';
                    } else {
                        if (pHouseInfo.cfcount > 0) {
                            strKey = '查封';
                        } else if (pHouseInfo.yycount > 0) {
                            strKey = '异议';
                        } else if (pHouseInfo.ygcount > 0) {
                            strKey = '预告';
                        } else if (pHouseInfo.dyacount > 0) {
                            strKey = '抵押';
                        } else if (pHouseInfo.dyicount > 0) {
                            strKey = '地役权';
                        } else if (pHouseInfo.bdcqcount > 0) {
                            strKey = '所有权';
                        }
                    }

                    var result = null;
                    for (var ii = 0; ii < this.legendData.length; ii++) {
                        var pLegend = this.legendData[ii];
                        if (pLegend.name === strKey) {
                            result = pLegend;
                        }
                    }
                    return result;
                },
                /**
                 * @description 获取2维表中第一维的位置
                 * @method _getHDimentionNumber
                 * @param {Number} nHH
                 * @param {Number} nUnitNum
                 * @return {Number} 对应的位置
                 */
                _getHDimentionNumber: function _getHDimentionNumber(nHH, nUnitNum) {
                    var result = 0;
                    for (var ii = 0; ii < nUnitNum; ii++) {
                        if (this.ArrayUnitHouseCount[ii]) {
                            result += this.ArrayUnitHouseCount[ii];
                        }
                    }
                    result += nHH;
                    if (result > 0) {
                        result = result - 1;
                    }
                    return result;
                },
                /**
                 * @description 格式化数据
                 * @method _formatHouseTable
                 * @param {Array} pArrayHouse
                 * @private
                 */
                _formatHouseTable: function _formatHouseTable(pArrayHouse) {
                    for (var ii = 0; ii < pArrayHouse.length; ii++) {
                        var pHouse = pArrayHouse[ii];

                        //记录原始户号
                        pHouse.originHh = pHouse.hh;

                        //室号部位为空的设置为空字符串
                        if (pHouse.shbw == null) {
                            pHouse.shbw = '';
                        }

                        //获取层号
                        pHouse.sjc = this._getChNumber(pHouse.sjc);
                    }

                    //获取是否重新编写户号，默认不编
                    var bReNumber = true;
                    this.maintainHh(pArrayHouse, bReNumber);
                },
                /**
                 * @description 维护逻辑幢中的户号
                 * @method maintainHh
                 * @param {Array} pArrayHouse
                 * @param {Boolean} bReNumber 是否重新编写户号，默认不编
                 */
                maintainHh: function maintainHh(pArrayHouse, bReNumber) {
                    //区分有无单元号数据
                    this.unitHouseData = [];
                    this.noUnitHouseData = [];
                    var ii;
                    var pHouse;
                    for (ii = 0; ii < pArrayHouse.length; ii++) {
                        pHouse = pArrayHouse[ii];
                        if (pHouse.dyh == null || pHouse.dyh === 0) {
                            //没有单元号的数据
                            this.noUnitHouseData.push(pHouse);
                        } else {
                            //有单元号的数据
                            this.unitHouseData.push(pHouse);
                        }
                    }
                    if (bReNumber === true) {
                        //重新编号
                        this._maintainHh(this.unitHouseData, true);
                        this._maintainHh(this.noUnitHouseData, false);
                    } else {
                        //使用数据中的户号
                        for (ii = 0; ii < pArrayHouse.length; ii++) {
                            pHouse = pArrayHouse[ii];
                            pHouse.hh = pHouse.originHh;
                        }
                    }
                },
                /**
                 * @description 通过层号里面的值获取相应的数值信息
                 * @method _getChNumber
                 * @param strCh {String} 层号
                 * @return {Number} 相应的数值信息
                 */
                _getChNumber: function _getChNumber(strCh) {
                    //维护层号
                    if (!strCh) {
                        return;
                    }
                    var arrayMatches = strCh.match(/^-\d+/g);
                    if (arrayMatches == null || arrayMatches.length === 0) {
                        arrayMatches = strCh.match(/\d+/g);
                    }
                    if (arrayMatches != null && arrayMatches.length > 0) {
                        strCh = arrayMatches[0];
                    }
                    return strCh;
                },
                /**
                 * @description 维护数据的户号
                 * @method _maintainHh
                 * @param arrayHouseData {Array} 户信息数组
                 * @param bHasDyh {Boolean} 是否有单元号
                 */
                _maintainHh: function _maintainHh(arrayHouseData, bHasDyh) {
                    var arrayHouseFloor = {};
                    var pHouseData;
                    var ii;
                    var key;
                    if (bHasDyh === false) {
                        for (ii = 0; ii < arrayHouseData.length; ii++) {
                            pHouseData = arrayHouseData[ii];
                            if (arrayHouseFloor[pHouseData.sjc] == null) {
                                arrayHouseFloor[pHouseData.sjc] = [];
                            }
                            arrayHouseFloor[pHouseData.sjc].push(pHouseData);
                        }
                        for (key in arrayHouseFloor) {
                            this._miantainHhFromFloor(arrayHouseFloor[key]);
                        }
                    } else {
                        for (ii = 0; ii < arrayHouseData.length; ii++) {
                            pHouseData = arrayHouseData[ii];
                            var strKey = pHouseData.sjc + 'T' + pHouseData.dyh;
                            if (arrayHouseFloor[strKey] == null) {
                                arrayHouseFloor[strKey] = [];
                            }
                            arrayHouseFloor[strKey].push(pHouseData);
                        }
                        for (key in arrayHouseFloor) {
                            this._miantainHhFromFloor(arrayHouseFloor[key]);
                        }
                    }
                },
                /**
                 * @description 维护每一层的户号
                 * @method _miantainHhFromFloor
                 * @param {Array} arrayHouseInfo
                 */
                _miantainHhFromFloor: function _miantainHhFromFloor(arrayHouseInfo) {
                    if (arrayHouseInfo == null) {
                        return;
                    }
                    arrayHouseInfo = arrayHouseInfo.sort(function (ele1, ele2) {
                        var arrayMatch1 = ele1.shbw.match(/\d+/g);
                        var arrayMatch2 = ele2.shbw.match(/\d+/g);
                        if (arrayMatch1 == null) {
                            arrayMatch1 = [0];
                        }
                        if (arrayMatch2 == null) {
                            arrayMatch2 = [0];
                        }
                        var nNum1 = Number(arrayMatch1[arrayMatch1.length - 1]);
                        var nNum2 = Number(arrayMatch2[arrayMatch2.length - 1]);
                        return nNum1 - nNum2;
                    });
                    for (var ii = 0; ii < arrayHouseInfo.length; ii++) {
                        var pHouseInfo = arrayHouseInfo[ii];
                        //自动维护户号，不再使用原始数据的户号
                        pHouseInfo.hh = ii + 1;
                    }
                }
            };
            GM.OA.Housetable.maintainData(scope.arrayhouse);
            scope.legendData = GM.OA.Housetable.legendData;

            //添加监听
            scope.$watch('arrayhouse', function (newValue, oldValue, scope) {
                GM.OA.Housetable.scope = scope;
                GM.OA.Housetable.maintainData(scope.arrayhouse);
            });
        }
    };
}]);

