/**
 * 分页模块
 * @class NgModule.pagination
 * @alias gm.ngCustom.pagination
 * @author LP
 */
angular.module('gm.ngCustom.pagination', []).directive('gmPagination', [
/**
 * @method gmPagination 分页指令
 * @member NgModule.pagination
 * @param {String} [showjump="true"] DOM属性传值，是否展示跳页功能
 * @param {Object} pagedata 作用域传值，分页指令的配置对象
 * @param {Number} [pagedata.currentPage = 1] 当前页
 * @param {Number} [pagedata.itemsPerPage = 10] 每页记录条数
 * @param {Number} pagedata.totalItems 总记录数
 * @param {Number} pagedata.pageLength 分页控件最多显示少个分页数字
 */
function () {
    return {
        restrict: 'EA',
        template: require('./pagination.tpl.html'),
        scope: {
            pagedata: '='
        },
        link: function link(scope, element, attrs) {
            // scope.pagedata = {
            //  currentPage:1,  //当前页
            //  itemsPerPage:5, //每页个数
            //  totalItems:985, //查询结果总个数
            //  pageCount:0,        //多少页
            //  pageLength:5        //分页控件上显示多少页
            // };

            scope.showJump = attrs['showjump'] !== 'false';

            //获取页面集合
            function getPagination() {
                var ii;

                if (scope.pagedata.totalItems == null || scope.pagedata.totalItems === 0) {
                    return;
                }
                //1.处理传入变量
                scope.pagedata.currentPage = parseInt(scope.pagedata.currentPage) ? parseInt(scope.pagedata.currentPage) : 1;
                scope.pagedata.totalItems = parseInt(scope.pagedata.totalItems) ? parseInt(scope.pagedata.totalItems) : 0;
                scope.pagedata.itemsPerPage = parseInt(scope.pagedata.itemsPerPage) ? parseInt(scope.pagedata.itemsPerPage) : 10;
                if (scope.pagedata.currentPage < 1) {
                    scope.pagedata.currentPage = 1;
                }
                // 定义分页的长度必须为奇数 (default:5)
                scope.pagedata.pageLength = parseInt(scope.pagedata.pageLength) ? parseInt(scope.pagedata.pageLength) : 5;
                if (scope.pagedata.pageLength % 2 === 0) {
                    // 如果不是奇数的时候处理一下
                    scope.pagedata.pageLength = scope.pagedata.pageLength - 1;
                }

                //2.获取集合
                scope.pagedata.pageCount = Math.ceil(scope.pagedata.totalItems / scope.pagedata.itemsPerPage);
                if (scope.pagedata.currentPage > scope.pagedata.pageCount) {
                    scope.pagedata.currentPage = scope.pagedata.pageCount;
                }
                scope.pagedata.pageList = [];

                //分三种情况：
                //1.没有省略号
                //2.有一个省略号，处于左侧或者右侧
                //3.两侧都有省略号
                if (scope.pagedata.pageLength >= scope.pagedata.pageCount) {
                    //没有省略号
                    for (ii = 1; ii <= scope.pagedata.pageCount; ii++) {
                        scope.pagedata.pageList.push(ii);
                    }
                } else {
                    var nOffset = (scope.pagedata.pageLength - 1) / 2;
                    if (scope.pagedata.currentPage <= nOffset + 1) {
                        //左侧没有省略号
                        for (ii = 1; ii < scope.pagedata.pageLength; ii++) {
                            scope.pagedata.pageList.push(ii);
                        }
                        scope.pagedata.pageList.push('...');
                        scope.pagedata.pageList.push(scope.pagedata.pageCount);
                    } else if (scope.pagedata.currentPage >= scope.pagedata.pageCount - nOffset) {
                        //右侧没有省略号
                        scope.pagedata.pageList.push(1);
                        scope.pagedata.pageList.push('...');
                        for (ii = scope.pagedata.pageCount - scope.pagedata.pageLength + 1; ii <= scope.pagedata.pageCount; ii++) {
                            scope.pagedata.pageList.push(ii);
                        }
                    } else {
                        //两侧都有省略号
                        scope.pagedata.pageList.push(1);
                        scope.pagedata.pageList.push('...');
                        var nStart = scope.pagedata.currentPage - nOffset + 1;
                        var nEnd = scope.pagedata.currentPage + nOffset - 1;
                        for (ii = nStart; ii <= nEnd; ii++) {
                            scope.pagedata.pageList.push(ii);
                        }
                        scope.pagedata.pageList.push('...');
                        scope.pagedata.pageList.push(scope.pagedata.pageCount);
                    }
                }
                scope.pagedata.nJumpPage = scope.pagedata.currentPage;
            }

            //跳转页面变量
            //scope.pagedata.nJumpPage = 0;

            //页面切换事件
            scope.changeCurrentPage = function (item) {
                if (item === '...') {
                    //后续添加相应操作
                } else {
                    scope.pagedata.currentPage = item;
                }
            };

            //上一页
            scope.prevPage = function () {
                if (scope.pagedata.currentPage > 1) {
                    var nOffset = scope.pagedata.pageLength - 2;
                    if (scope.pagedata.currentPage - nOffset >= 1) {
                        scope.pagedata.currentPage -= nOffset;
                    } else {
                        scope.pagedata.currentPage = 1;
                    }
                }
            };

            //下一页
            scope.nextPage = function () {
                if (scope.pagedata.currentPage < scope.pagedata.pageCount) {
                    var nOffset = scope.pagedata.pageLength - 2;
                    if (scope.pagedata.currentPage + nOffset <= scope.pagedata.pageCount) {
                        scope.pagedata.currentPage += nOffset;
                    } else {
                        scope.pagedata.currentPage = scope.pagedata.pageCount;
                    }
                }
            };

            //跳转页数
            scope.gotoPage = function () {
                var regex = /[1-9]\d*/g;
                var bRight = regex.test(scope.pagedata.nJumpPage);
                if (bRight === true) {
                    scope.pagedata.currentPage = scope.pagedata.nJumpPage;
                }
            };

            //添加变量监听
            if (scope.pagedata) {
                scope.$watch(function () {
                    var newValue = scope.pagedata.currentPage + ' ' + scope.pagedata.totalItems + ' ' + scope.pagedata.itemsPerPage;
                    return newValue;
                }, getPagination);
            }
        }
    };
}]);

