function () {
    return {
        restrict: 'ECA',
        template: require('./tabs.tpl.html'),
        scope: {
            tabdata: '='
        },
        link: function link(scope, element, attr, transclution) {
            scope.showClose = attr['showClose'] !== 'false';
            scope.nPerPageCount = getTabCount(); //每页标签的个数
            scope.nOffsetX = 0; //当前位移值（绝对值）
            scope.nMaxOffset = 0; //最大位移值（绝对值）
            var nTabWidth = scope.tabdata.nTabWidth == null ? 140 : scope.tabdata.nTabWidth; //每个标签的初始宽度
            scope.nTabWidth = nTabWidth;
            scope.nPerPageCount = getTabCount(); //每页标签的个数

            //li标签点击事件
            scope.focusClickTab = function (pItem) {
                scope.tabdata.focusTab = pItem.name;
                if (angular.isFunction(scope.$parent[scope.tabdata.clickTab])) {
                    scope.$parent[scope.tabdata.clickTab](pItem);
                }
            };

            //li标签悬停事件
            scope.overTabli = function (pItem) {
                for (var ii = 0; ii < scope.tabdata.tabs.length; ii++) {
                    var pTempItem = scope.tabdata.tabs[ii];
                    if (scope.tabdata.focusTab !== pTempItem.name) {
                        pTempItem.viewImg = null;
                    }
                }
                pItem.viewImg = pItem.activeImg;
            };

            //li标取消悬停事件
            scope.leaveTabli = function (pItem) {
                if (scope.tabdata.focusTab !== pItem.name) {
                    pItem.viewImg = null;
                }
            };

            //关闭标签页
            scope.closeTab = function (strKey) {
                var nIndex = getTabIndex(strKey);
                if (nIndex >= 0) {
                    scope.tabdata.tabs.splice(nIndex, 1);
                    if (scope.tabdata.tabs.length === 0) {
                        return;
                    }
                    if (scope.tabdata.focusTab === strKey) {
                        //如果删除的是焦点标签，则需要重置焦点
                        if (scope.tabdata.tabs.length <= nIndex) {
                            scope.tabdata.focusTab = scope.tabdata.tabs[scope.tabdata.tabs.length - 1].name;
                        } else {
                            scope.tabdata.focusTab = scope.tabdata.tabs[nIndex].name;
                        }
                    }
                }
            };

            //上一页
            scope.prePage = function ($event) {
                var nPerPageWidth = scope.nPerPageCount * nTabWidth;
                var nOffsetX = scope.nOffsetX - nPerPageWidth;
                if (nOffsetX < 0) {
