            //下一页
            scope.nextPage = function ($event) {
                var nPerPageWidth = scope.nPerPageCount * nTabWidth;
                var nOffsetX = scope.nOffsetX + nPerPageWidth;
                if (nOffsetX > scope.nMaxOffset) {
                    nOffsetX = scope.nMaxOffset;
                }
                scope.nOffsetX = nOffsetX;
                $event.stopPropagation();
            };

            //监听数据变化
            scope.$watchCollection('tabdata.tabs', function (newVal, oldVal, pScope) {
                //pScope.tabUlStyle.width = pScope.tabdata.tabs.length*nTabWidth + 'px';
                var nLength = pScope.tabdata.tabs.length;
                pScope.nPageSize = Math.ceil(nLength / pScope.nPerPageCount);
                pScope.nMaxOffset = nLength * nTabWidth - $(element).find('.tab-tabs').width();
                if (pScope.nMaxOffset <= 0) {
                    pScope.nMaxOffset = 0;
                }
                if (scope.nOffsetX > pScope.nMaxOffset) {
                    scope.nOffsetX = pScope.nMaxOffset;
                }
            });

            //监听焦点标签改变
            scope.$watch('tabdata.focusTab', function (newVal, oldVal, pScope) {
                if (pScope.tabdata.tabs.length > pScope.nPerPageCount) {
                    //标签存在滚动，则处理标签位置
                    var nIndex = getTabIndex(pScope.tabdata.focusTab);
                    if (nIndex >= 0) {
                        //确保焦点标签可以显示
                        var nTabContainerWidth = $(element).find('.tab-tabs').width();
                        var nMinX = pScope.nOffsetX;
                        var nMaxX = pScope.nOffsetX + nTabContainerWidth;
                        var nTabMinX = nTabWidth * nIndex;
                        var nTabMaxX = nTabWidth * (nIndex + 1);
                        if (nTabMinX >= nMinX && nTabMaxX <= nMaxX) {
                            //焦点标签已经处于视图范围,不做调整
                        } else {
                            //焦点标签没有处于视图范围
                            if (Math.abs(nTabMinX - nMinX) > Math.abs(nMaxX - nTabMaxX)) {
                                //焦点标签设置为紧挨右侧
                                var nTempOffset = nTabMaxX - nTabContainerWidth;
                                if (nTempOffset > 0) {
                                    pScope.nOffsetX = nTempOffset;
                                }
                            } else {
                                //焦点标签设置为紧挨左侧
                                pScope.nOffsetX = nTabMinX;
                            }
                        }
                    }
                }

                //修改菜单样式
                for (var ii = 0; ii < scope.tabdata.tabs.length; ii++) {
                    var pTempItem = scope.tabdata.tabs[ii];
                    if (pTempItem.name === pScope.tabdata.focusTab) {
                        pTempItem.viewImg = pTempItem.activeImg;
                    } else {
                        pTempItem.viewImg = null;
                    }
                }
            });

            //监听tab宽度改变事件
            scope.$watch('tabdata.nTabWidth', function (newVal, oldVal, pScope) {
                nTabWidth = scope.tabdata.nTabWidth == null ? 140 : scope.tabdata.nTabWidth;
                scope.nTabWidth = nTabWidth;
                scope.nPerPageCount = getTabCount(); //每页标签的个数
            });

            scope.$watch('nOffsetX', function (newVal, oldVal, pScope) {
                pScope.tabUlStyle.transform = 'translateX(-' + pScope.nOffsetX + 'px)';
            });

            //注册窗口改变事件
            $(window).resize(function () {
                var nTabCount = getTabCount();
                scope.nPerPageCount = nTabCount;
                scope.$apply('nPerPageCount');
                scope.nOffsetX = 0;
                scope.$apply('nOffsetX');
            });

            //标签动态样式
            scope.tabUlStyle = {
                transform: 'translateX(-' + scope.nOffsetX + 'px)'
                //width:scope.tabdata.tabs.length*nTabWidth + 'px'
            };

            //获取当前页面能够显示的标签个数
            function getTabCount() {
                var nWidth = $(element).find('.tab-tabs').width();
                var nCount = Math.floor(nWidth / nTabWidth);
                return nCount;
            }

            //获取数组索引
            function getTabIndex(strTabName) {
                var pEle = GM.CommonOper.getArrayEleByAtt(scope.tabdata.tabs, 'name', strTabName);
                var nIndex = GM.CommonOper.getArrayEleIndex(scope.tabdata.tabs, pEle);
                return nIndex;
            }
        }
    };
}]);

