     * @param  {String} strPath 路由地址
     * @param  {Boolean} bReload 是否重新加载页面
     * @author LP
     */
    $scope.activeNaviFromPath = function (strPath, bReload) {
        if (bReload === true) {
            //需要从新加载页面
            $scope.refreshPage(strPath);
        }
        Util.activeNaviFromPath(strPath);
        $scope.goPath(strPath);
    };

    /**
