     * @method refreshPage 刷新路由页面
     * @param  {String} strPath 路由地址
     * @author LP
     */
    $scope.refreshPage = function (strPath) {
        var strName = strPath.substr(1);
        var pEle = GM.CommonOper.getArrayEleByAtt($scope.tabData.tabs, 'name', strName);
        if (pEle) {
            var strUrl = GM.LayoutOper.getURLWithoutParams(pEle.url);
            var strTime = new Date().getTime();
            strUrl = GM.LayoutOper.addURLParam(strUrl, 'timestamp', strTime);
            pEle.url = strUrl;
        }
    };

    /**
     * @member NgModule.frame2.frameCtrl
     * @method deletePage 删除路由页面
     * @param  {String} strPath 路由地址
     * @return {Boolean} 是否删除成功
     */
    $scope.deletePage = function (strPath) {
        var bRt = false;
        if (!GM.CommonOper.isStrNullOrEmpty(strPath)) {
            var strName = strPath.substr(1);
            var pEle = GM.CommonOper.getArrayEleByAtt($scope.tabData.tabs, 'name', strName);
            if (pEle) {
                GM.CommonOper.removeItemFromArray($scope.tabData.tabs, pEle);
                bRt = true;
            }
        }
        return bRt;
    };

    /**
     * @member NgModule.frame2.frameCtrl
     * @method goHomePage 返回首页
     * @author LP
     */
    $scope.goHomePage = function () {
        $scope.goPath($scope.frameData.homePage.path);

        //焦点置空
        var pActiveItem = Util.getActiveChildItem();
        if (pActiveItem != null) {
            pActiveItem.active = false;
        }
        $scope.activeChildItem = null;
    };

    //设置用户头像
