        background: 'url(' + require('./img/user.png') + ') no-repeat 0 0 / contain'
    };

    //函数定义
    var Util = {
        //获取处于焦点的二级子节点
        getActiveChildItem: function getActiveChildItem() {
            var pNaviData = $scope.naviData;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                for (var jj = 0; jj < oneItem.children.length; jj++) {
                    var twoItem = oneItem.children[jj];
                    if (twoItem.active === true) {
                        return twoItem;
                    }
                }
            }
        },
        //根据路由路径激活不同的导航项
        activeNaviFromPath: function activeNaviFromPath(strPath) {
            Util.initNaviData();
            var pNaviData = $scope.naviData;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                for (var jj = 0; jj < oneItem.children.length; jj++) {
                    var twoItem = oneItem.children[jj];
                    if (twoItem.path === strPath) {
                        twoItem.active = true;
                        oneItem.down = true;
                        $scope.activeChildItem = twoItem;
                        return;
                    }
                    if (twoItem.children) {
                        for (var kk = 0; kk < twoItem.children.length; kk++) {
                            var threeItem = twoItem.children[kk];
                            if (threeItem.path === strPath) {
                                threeItem.active = true;
                                twoItem.active = true;
                                oneItem.down = true;
                                $scope.threeNaviHide = false;
                                $scope.activeChildItem = twoItem;
                                return;
                            }
                        }
                    }
                }
            }
        },
        //初始化导航数据
        initNaviData: function initNaviData() {
            var pNaviData = $scope.naviData;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                oneItem.down = false;
                for (var jj = 0; jj < oneItem.children.length; jj++) {
                    var twoItem = oneItem.children[jj];
                    twoItem.active = false;
                    if (twoItem.children) {
                        for (var kk = 0; kk < twoItem.children.length; kk++) {
                            var threeItem = twoItem.children[kk];
                            threeItem.active = false;
                        }
                    }
                }
            }
        },
        setTheme: function setTheme(strThemeName) {
            for (var key in $scope.themeData) {
                $('html').removeClass(key);
            }
            $('html').addClass(strThemeName);
