        $event.preventDefault();
    };

    $scope.changePassword = function ($event) {
        GM.FrameManager.logout('1');
        $event.preventDefault();
    };

    //设置导航模式
    $scope.setNaviMode = function () {
        $scope.naviHide = !$scope.naviHide;
    };

    //导航条父级节点点击事件
    $scope.clickParentItem = function (parentItem) {
        var bDown = !parentItem.down;
        var pNaviData = $scope.naviData;

        for (var ii = 0; ii < pNaviData.length; ii++) {
            if (!angular.equals(pNaviData[ii], parentItem)) {
                pNaviData[ii].down = false;
            }
        }
        parentItem.down = bDown;
    };

    //导航条子级节点击事件
    $scope.clickChildItem = function (childItem) {
        //设置焦点节点
        var pNaviData = $scope.naviData;
        for (var ii = 0; ii < pNaviData.length; ii++) {
            var oneItem = pNaviData[ii];
            for (var jj = 0; jj < oneItem.children.length; jj++) {
                var twoItem = oneItem.children[jj];
                twoItem.active = false;
            }
        }
        childItem.active = true;
        $scope.activeChildItem = childItem;
        $scope.goPath(childItem.path);
    };

    //通过事件路由到具体页面
    $scope.goPathFromFrame = function (event) {
        if (event != null) {
            var strPath = event.target.getAttribute('path');
            $scope.goPath(strPath, true);
        }
    };

    //路由到具体页面
    $scope.goPath = function (strPath, bActiveItem) {
        bActiveItem = bActiveItem == null ? false : bActiveItem;
        if (strPath != null && strPath !== '') {
            //处理导航条焦点
            if (bActiveItem === true) {
                Util.activeNaviFromPath(strPath);
            }
            if ($scope.tabWrapper === true) {
                //标签模式
                var strTabName = strPath.substr(1);
                var pTabItem = GM.CommonOper.getArrayEleByAtt($scope.tabData.tabs, 'name', strTabName);
                if (pTabItem != null) {
                    //已经存在，则焦点之
                } else {
                    //不存在，则添加之
                    $scope.tabData.tabs.push({
                        name: strTabName,
                        caption: $scope.activeChildItem.name,
                        url: $scope.activeChildItem.template
                    });
                }
