        if (strPermission != null && $scope.hasPermission(strPermission) === false) {
            //没有首页权限
        } else {
            var strTabName = $scope.frameData.homePage.path.substr(1);
            $scope.tabData.tabs.push({
                name: strTabName,
                caption: $scope.frameData.homePage.name,
                url: $scope.frameData.homePage.template
            });
            $scope.tabData.focusTab = strTabName;
