'use strict';

require('frame');

require('./frame.less');

var frameTem = require('./frame.html');

/**
 * @class NgModule.frame3 frame3模块
 * @alias frame3
 * @author LP
 */
var frameApp = angular.module('frameApp', ['ui.router', 'ngAnimate', 'gm.ngCustom.control', 'ngResource', 'ngSanitize', 'ngCookies']);

GM.FrameManager.configModule(frameApp);

/**
 * @class NgModule.frame3.frameCtrl 框架控制器
 * @extends NgModule.frame.frameCtrl
 * @author LP
 */
frameApp.controller('frameCtrl', ['$scope', '$location', '$gmRestful', '$cookies', '$state', function ($scope, $location, $gmRestful, $cookies, $state) {
    var ii;
    $scope.frameData = GLOBAL_FRAME_CONFIG;
    //获取登出路径
    var strPath = GM.LayoutOper.getURLWithoutParams($location.absUrl());
    $scope.frameData.loginUrl = LOGIN_PATH + '?service=' + strPath + '&logout=1';
    $scope.frameData.indexUrl = MAIN_PAGE_PATH;
    $scope.naviHide = false; //是否隐藏大导航框
    $scope.activeChildItem = null; //处于焦点的二级子节点
    $scope.threeNaviHide = true; //三级菜单是否隐藏
    $scope.fullScreen = $cookies.getObject('fullScreen');
    $scope.bMenuShow = false; //在全屏模式下时候显示菜单
    $scope.themeName = $cookies.get('themeName'); //系统主题
    $scope.themeName = $scope.themeName == null ? 'blue' : $scope.themeName;
    $scope.mapMenuItems = []; //与地图相关的菜单项
    $scope.showMapMenuPanel = true; //是否展示地图上的左侧菜单面板

    //获取标签数据
    $scope.menuTagData = {
        focusTab: '',
        tabs: [],
        nTabWidth: 130,
        showIcon: true,
        clickTab: 'clickTab'
    };

    //组织数据
    for (ii = 0; ii < GLOBAL_FRAME_CONFIG.navi.length; ii++) {
        var pItem = GLOBAL_FRAME_CONFIG.navi[ii];
        var bRt = GM.FrameManager.hasPermission(pItem.permission);
        if (bRt === true) {
            var tagItem = pItem;
            tagItem.name = pItem.name;
            tagItem.caption = pItem.name;
            $scope.menuTagData.tabs.push(tagItem);
            if (pItem.map) {
                $scope.mapMenuItems.push(tagItem);
            }
            if (tagItem.children) {
                var newChildren = [];
                for (var jj = 0; jj < tagItem.children.length; jj++) {
                    var pSubItem = tagItem.children[jj];
                    bRt = GM.FrameManager.hasPermission(pSubItem.permission);
                    if (bRt) {
                        newChildren.push(pSubItem);
                        if (pSubItem.map) {
                            $scope.mapMenuItems.push(pSubItem);
                        }
                    }
                }
                tagItem.children = newChildren;
            }
        }
    }

    //数据准备
    var pRestful = new $gmRestful();
    GM.FrameManager.prepareData($scope, pRestful);

    //添加公共接口
    GM.FrameManager.addCommonApi($scope);
    //地图模式中，左侧面板关闭函数
    $scope.back = function () {
        $scope.showMapMenuPanel = false;
    };

    //设置用户头像
    $scope.userImg = {
        background: 'url(' + STATIC_RESOURCES_PATH + 'custom/frame/frame3/img/user.png' + ') no-repeat 0 0 / contain'
    };

    //函数定义
    var Util = {
        //根据路由路径激活不同的导航项
        activeNaviFromPath: function activeNaviFromPath(strPath) {
            var pNaviData = $scope.menuTagData.tabs;
            for (var ii = 0; ii < pNaviData.length; ii++) {
                var oneItem = pNaviData[ii];
                if (oneItem.path === strPath) {
                    $scope.focusMenu = oneItem;
                    $scope.focusSubMenu = oneItem;
                    $scope.menuTagData.focusTab = oneItem.name;
                    break;
                }
                if (oneItem.children) {
                    for (var jj = 0; jj < oneItem.children.length; jj++) {
                        var twoItem = oneItem.children[jj];
                        if (twoItem.path === strPath) {
                            $scope.focusMenu = oneItem;
                            $scope.focusSubMenu = twoItem;
                            $scope.menuTagData.focusTab = oneItem.name;
                            return;
                        }
                    }
                }
            }
        }
    };

    $scope.$on('$locationChangeSuccess', function (angularEvent, newUrl, oldUrl) {
        Util.activeNaviFromPath($location.path());
    });

    $scope.logout = function ($event) {
        GM.FrameManager.logout();
        $event.preventDefault();
    };

    //对外调用的标签跳转函数
    $scope.activeNaviFromPath = function (strPath) {
        Util.activeNaviFromPath(strPath);
        $scope.goPath(strPath);
    };

    //刷新路由页面
    $scope.refreshPage = function (strPath) {
        var strName = strPath.substr(1);
        $state.reload(strName);
    };

    //设置导航模式
    $scope.setNaviMode = function () {
        $scope.naviHide = !$scope.naviHide;
    };

    //选择主菜单
    $scope.clickTab = function (pItem) {
        $scope.focusMenu = pItem;
        if ($scope.focusMenu.children == null || $scope.focusMenu.children.length === 0) {
            $scope.focusSubMenu = $scope.focusMenu;
            if ($scope.focusSubMenu.map !== true) {
                $scope.goPath($scope.focusSubMenu.path);
            } else {
                //地图模式
                $scope.$broadcast('clickMenu', $scope.focusSubMenu);
                $scope.showMapMenuPanel = true;
            }
        } else {
            $scope.clickChildItem($scope.focusMenu.children[0]);
        }
    };

    //导航条子级节点击事件
    $scope.clickChildItem = function (pSubItem) {
        //设置焦点节点
        $scope.focusSubMenu = pSubItem;
        if ($scope.focusSubMenu.map !== true) {
            $scope.goPath($scope.focusSubMenu.path);
        } else {
            //地图模式
            $scope.showMapMenuPanel = true;
            $scope.$broadcast('clickMenu', $scope.focusSubMenu);
        }
    };

    //通过事件路由到具体页面
    $scope.goPathFromFrame = function (event) {
        if (event != null) {
            var strPath = event.target.getAttribute('path');
            $scope.goPath(strPath, true);
        }
    };

    //路由到具体页面
    $scope.goPath = function (strPath, bActiveItem) {
        bActiveItem = bActiveItem == null ? false : bActiveItem;
        if (strPath != null && strPath !== '') {
            //处理导航条焦点
            if (bActiveItem === true) {
                Util.activeNaviFromPath(strPath);
            }
            var strOldPath = $location.path();
            if (strOldPath === strPath && !GM.CommonOper.isStrNullOrEmpty(strPath)) {
                //刷新路由页面
                var strName = strPath.substr(1);
                if (!GM.CommonOper.isStrNullOrEmpty(strName)) {
                    $state.reload(strName);
                }
            } else {
                //切换路由
                $location.path(strPath).replace();
            }
        }
    };

    //全图
    $scope.setFullScreen = function () {
        $scope.fullScreen = !$scope.fullScreen;
        $cookies.put('fullScreen', $scope.fullScreen);

        //设置样式
    };

    //设置菜单是否可见
    $scope.showMenu = function () {
        $scope.bMenuShow = !$scope.bMenuShow;
    };

    //---------------tab模式---------------

    //默认展示标签
    if ($scope.defaultViewTabs) {
        for (ii = 0; ii < $scope.defaultViewTabs.length; ii++) {
            $scope.activeNaviFromPath($scope.defaultViewTabs[ii]);
        }
    }
    //---------------查询表单---------------

    //根据地址设置菜单焦点
    Util.activeNaviFromPath($location.path());
}]);

//验证token
GM.FrameManager.addToken(frameApp);

//模板申明
frameApp.run(['$templateCache', function ($templateCache) {
    $templateCache.put('frame.tpl.html', frameTem);
}]);

