    getUUID: function getUUID() {
        var chars = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'.split('');
        var uuid = [],
            i;
        var radix = 16 || chars.length;

        if (32) {
            // Compact form
            for (i = 0; i < 32; i++) {
                uuid[i] = chars[0 | Math.random() * radix];
            }
        } else {
            // rfc4122, version 4 form
            var r;

            // rfc4122 requires these characters
            uuid[8] = uuid[13] = uuid[18] = uuid[23] = '-';
            uuid[14] = '4';

            // Fill in random data. At i==19 set the high bits of clock sequence
            // as
            // per rfc4122, sec. 4.1.5
            for (i = 0; i < 36; i++) {
                if (!uuid[i]) {
                    r = 0 | Math.random() * radix;
                    uuid[i] = chars[i == 19 ? r & 0x3 | 0x8 : r];
                }
            }
        }

        return uuid.join('');
    },
    /**
     * 展示一个对话框
     * @param url 对话框内容URL
     * @param size 对话框尺寸类型：lg、sm、fullscreen、''默认大小
     * @param reload 关闭对话框时的回调函数
     */
    showDialog: function showDialog(url, title, size, callback) {
        var w, h;

        var option = {
            iframe: url + ((url.indexOf('?') > -1 ? '&' : '?') + 'version=' + new Date()),
            title: title,
            backdrop: 'static'
        };

        if (size && size.indexOf(',') > -1) {
            var arrSize = size.split(',');

            try {
                w = parseInt(arrSize[0]) + 'px';
            } catch (e) {
                w = '1000px';
            }

            try {
                h = parseInt(arrSize[1]) + 'px';
            } catch (e) {
                h = '800px';
            }

            option['width'] = w;
            option['height'] = h;
        } else {
            option['size'] = size;
        }

        var myModalTrigger = new $.zui.ModalTrigger(option);

        myModalTrigger.show({
            hidden: function hidden() {
                if (callback) {
                    callback();
                }
            }
        });
    },
    /**
     * 取数组元素中对应属性的值组成新的数组
     * @param array
