     *
     * @param {String} url                  url地址
     * @param {Function} successCallback    成功时的回调函数
     * @param {Function} errorCallback      失败时的回调函数
     * @param {Object} param                传入数据
     */
    syncAjaxPost: function syncAjaxPost(url, successCallback, errorCallback, param) {
        this._syncAjax('POST', url, successCallback, errorCallback, param);
    },
    _syncAjax: function _syncAjax(type, url, successCallback, errorCallback, param) {
        var opt = {
            url: url,
            async: false,
            type: type,
            success: function success(res) {
                successCallback(res);
            },
            error: function error(res) {
                errorCallback(res);
            }
        };

        if (param) {
            opt.data = param;
        }

        $.ajax(opt);
    },
    /**
     * 获取金额大写
     * @param {Number} n
     */
    getCnFormatMoney: function getCnFormatMoney(n) {
        var fraction = ['角', '分'];
        var digit = ['零', '壹', '贰', '叁', '肆', '伍', '陆', '柒', '捌', '玖'];
        var unit = [['元', '万', '亿'], ['', '拾', '佰', '仟']];
        var head = n < 0 ? '欠' : '';
        n = Math.abs(n);
        var s = '';
        for (var i = 0; i < fraction.length; i++) {
            s += (digit[Math.floor(n * 10 * Math.pow(10, i)) % 10] + fraction[i]).replace(/零./, '');
        }
        s = s || '整';
        n = Math.floor(n);
        for (var i = 0; i < unit[0].length && n > 0; i++) {
            var p = '';
            for (var j = 0; j < unit[1].length && n > 0; j++) {
                p = digit[n % 10] + unit[1][j] + p;
                n = Math.floor(n / 10);
            }
            s = p.replace(/(零.)*零$/, '').replace(/^$/, '零') + unit[0][i] + s;
        }
        return head + s.replace(/(零.)*零元/, '元').replace(/(零.)+/g, '零').replace(/^整$/, '零元整');
    },
    setObjectAttrVal: function setObjectAttrVal(obj, exp, index, val) {
        if (!obj) {
            return;
        }

        var arr = exp.split('.');

        if (index == arr.length - 1) {
            obj[arr[index]] = val;
            return;
        }

        obj = obj[arr[index]];
        index++;

        this.setObjectAttrVal(obj, exp, index, val);
    },
    cloneObject: function cloneObject(obj) {
        // Handle the 3 simple types, and null or undefined
        if (null == obj || 'object' !== (typeof obj === 'undefined' ? 'undefined' : _typeof(obj))) return obj;

        // Handle Date
        if (obj instanceof Date) {
            var copy = new Date();
            copy.setTime(obj.getTime());
            return copy;
        }

        // Handle Array
        if (obj instanceof Array) {
            var copy = [];
            for (var i = 0, len = obj.length; i < len; ++i) {
                copy[i] = GM.Form.CommonOper.cloneObject(obj[i]);
            }
            return copy;
        }

        // Handle Object
        if (obj instanceof Object) {
            var copy = {};
            for (var attr in obj) {
                if (obj.hasOwnProperty(attr)) copy[attr] = GM.Form.CommonOper.cloneObject(obj[attr]);
            }
            return copy;
        }

        throw new Error("Unable to copy obj! Its type isn't supported.");
    },
    /**
