				if (grid.length > 0) {
					sDataItems = grid.attr('data-items');
					sTemplateItems = grid.attr('template-items');
				}

				this._getGridOption(scope, dgds[i], null, null, sDataItems, sTemplateItems);
			}
		}
	},
	/**
  * 根据formid初始化相关的所有数据列表对象
  *
  * @param {Object} scope 当前作用域
  * @param {String} formid 表单ID
  */
	_initDataGridOptions: function _initDataGridOptions(scope, formid) {
		var _this = this;
		var url = window.FORM_REST_URL.workflow.getDataGridDataSourceNames + '?formids=' + formid;
		GM.Form.CommonOper.syncAjaxGet(url, function (res) {
			if (res.success === true && res.data) {
				_this._initOptions(scope, res.data);
			}
		}, function (res) {});
	},
	/**
  * 获取Redis缓存的数据
  *
  * @param {String} key 键
  */
	_getRedisString: function _getRedisString(key) {
		var result;
		GM.Form.CommonOper.syncAjaxGet(window.FORM_REST_URL.workflow.getRedisString + '?key=' + key, function (res) {
			if (res.success === true && !!res.data) {
				result = res.data;
