                });

                GM.Form.formScope.pendingPageLz.totalItems = response.data.total;
                GM.Form.formScope.lzGridOptions.data = response.data.records;
            }
        }, function (res) {});
    },
    _isTaskClaimed: function _isTaskClaimed() {
        var url = FORM_REST_URL.workflow.isClaimed + '?bizKey=' + GM.Form.formScope.taskInfo.bizKey + '&taskkey=' + encodeURIComponent(GM.Form.formScope.taskInfo.taskKey);
        GM.Form.restful.get(url, null).then(function (response) {
            var isClaimed = response.success === true && response.data === true;
            GM.Form.formScope.taskInfo.isClaimed = response.success === true && response.data === true;

            if (!isClaimed) {
                GM.Form.formScope.readonly = true;
            }
        }, function (response) {});
