//收件材料服务
angular.module('frameApp').factory('$fileService', ['$gmRestful', function ($gmRestful) {
    var result = {
        /**
         * 获取作为草稿的附件材料树
         *
         * @param {String} draftBizKey
         * @param {String} processKey
         * @param {String} subType
         */
        getDraftAttaches: function getDraftAttaches(draftBizKey, processKey, taskKey, subType) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.get(RESTURL.file.getDraftAttaches, {
                draftBizKey: draftBizKey,
                processKey: processKey,
                taskKey: taskKey,
                subType: subType
            });
            return pPromise;
        },
        getAttachesFromRedis: function getAttachesFromRedis(draftBizKey, processKey, taskKey, subType) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.get(RESTURL.file.getAttachesFromRedis, {
                draftBizKey: draftBizKey,
                processKey: processKey,
                taskKey: taskKey,
                subType: subType
            });
            return pPromise;
        },
        /**
         * @description 查询文件目录树
         * @param strYwh
         * @returns {*}
         */
        queryTree: function queryTree(strYwh, subType, seqNo) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.get(RESTURL.file.queryTree, {
                ywh: strYwh,
                zlx: subType,
                sxh: seqNo
            });
            return pPromise;
        },
        /**
         * @description 保存材料信息
         * @param folderData
         * @returns {*}
         */
        saveFolder: function saveFolder(folderData) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.post(RESTURL.file.saveFolder, {}, folderData);
            return pPromise;
        },
        /**
         * @description 删除材料
         * @param strId {String} 材料ID
         */
        removeFolder: function removeFolder(strId) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.post(RESTURL.file.deleteFolder, {}, {
                id: strId
            });
            return pPromise;
        },
        /**
         * @description 删除文件
         * @param strId
         */
        removeFile: function removeFile(strId, strFileId) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.post(RESTURL.file.deleteFile, {}, {
                id: strId,
                fileId: strFileId,
                status: 1
            });
            return pPromise;
        },
        /**
         * @description 保存文件
         * @param fileData
         */
        saveFile: function saveFile(fileData) {
            var pRestful = new $gmRestful();
            var pPromise = pRestful.post(RESTURL.file.saveFile, {}, fileData);
            return pPromise;
        },
        /**
         * @description 修证树节点数据
         * @param treeNodes {Array} 树节点数据
         * @param strStepName {String} 节点名称
         * @param bCliam {是否签收}
         */
        formatTreeData: function formatTreeData(treeNodes, strStepName, bCliam) {
            if (treeNodes == null) {
                return;
            }
            var arrayNodes = GM.TreeOper.getAllNodes(treeNodes);
            for (var ii = 0; ii < arrayNodes.length; ii++) {
                var pNode = arrayNodes[ii];
                if (pNode.processDefId) {
                    //文件夹节点
                    pNode.parent = true;
                    var bEnable = this._isFileEnable(pNode, strStepName, bCliam);
                    if (bEnable === false) {
                        pNode.nodeType = 'disabled';
                        if (pNode.children != null && pNode.children.length > 0) {
                            for (var jj = 0; jj < pNode.children.length; jj++) {
                                var pItemNode = pNode.children[jj];
                                pItemNode.nodeType = pNode.nodeType;
                            }
                        }
                    } else {
                        //材料是否必须
                        var bNessary = this._isFileNecessary(pNode);
                        if (bNessary === true && (pNode.children == null || pNode.children.length === 0)) {
                            pNode.nodeType = 'error';
                        } else {
                            pNode.nodeType = 'default';
                        }
                    }
                } else {
                    pNode.filename = pNode.name;
                }
            }
            return treeNodes;
        },
        initTree: function initTree(treeScope, bizKey, taskKey, subType, seqNo, urlParams) {
            var _this = this;
            var sSubType = subType || '';
            var sSeqNo = seqNo || '';

            if (urlParams.duuid) {
                _this.getDraftAttaches(bizKey, urlParams.processid, taskKey, sSubType).then(function (response) {
                    var pTreeNode = _this.formatTreeData(response.data, urlParams.blbz, true);
                    treeScope.fileTree = pTreeNode;
                });
            } else if (urlParams.did) {
                _this.getAttachesFromRedis(bizKey, urlParams.processid, taskKey, sSubType).then(function (response) {
                    var pTreeNode = _this.formatTreeData(response.data, urlParams.blbz, true);
                    treeScope.fileTree = pTreeNode;
                });
            } else {
                //查询文件树数据
                _this.queryTree(bizKey, sSubType, sSeqNo).then(function (response) {
                    var pTreeNode = _this.formatTreeData(response.data, urlParams.blbz, true);
                    treeScope.fileTree = pTreeNode;
                });
            }
        },
        /**
         * @description 材料是否可以编辑
         * @param pNode
         * @param strStepName
         * @param bCliam
         * @returns {boolean}
         * @private
         */
        _isFileEnable: function _isFileEnable(pNode, strStepName, bCliam) {
            if (bCliam == false) {
                //未签收
                return false;
            }
            //配置节点比对

            return true;
        },
        /**
         * @description 材料是否必要
         * @param pNode
         * @private
         */
        _isFileNecessary: function _isFileNecessary(pNode) {
            return true;
        }
    };
    return result;
}]);

//拍摄服务
angular.module('frameApp').factory('$captureService', ['$gmRestful', function ($gmRestful) {
    var pRestful = new $gmRestful();
    //基础操作
    var BaseCapture = {
        /**
         * @description 展示对话框
         */
        showDialog: function showDialog() {
            var nWindowHeight = $(window).height();
            var nHeight = 650;
            if (nHeight > nWindowHeight) {
                nHeight = nWindowHeight;
            }
            var nWidth = (nHeight - 115) / 0.75 + 35 + 300;
            $('.oa-capture-dialog').dialog({
                title: '材料扫描',
                modal: true,
                width: nWidth,
                height: nHeight
            });
        }
    };

    //通用摄像头控件
    GM.DotNetCapture = {
        showDialog: function showDialog($scope) {
            this.$scope = $scope;
            this.$scope.tableData = [];
            this.options = {
                camera: 0,
                resolution: 0
            };
            this._innitLayout();
            this._initEvent();
            BaseCapture.showDialog();
        },
        /**
         * @description 初始化页面
         */
        _innitLayout: function _innitLayout() {
            //修改页面对应的设备
            this._modifyLayout();

            if (this.device_camera == null) {
                this.device_camera = $('#CaptureView')[0];
            }
            this.device_camera.CameraStop();
            this.device_camera.Init();
            var strCameras = this.device_camera.getDeviceNames();

            if (strCameras) {
                //设置默认显示摄像头的参数
                var arrayCameras = strCameras.split(';');
                //清除为空的数组元素
                for (var ii = 0; ii < arrayCameras.length; ii++) {
                    if (GM.CommonOper.isStrNullOrEmpty(arrayCameras[ii])) {
                        GM.CommonOper.removeItemFromArray(arrayCameras, arrayCameras[ii]);
                        ii--;
                    }
                }
                this.$scope.arrayCameras = arrayCameras;
            }
            this.device_camera.CameraConn(this.options.camera, this.options.resolution);
            this.$scope.selectCamera = String(this.options.camera);
            this.$scope.arrayResolutions = this._getResolutions();
            this.$scope.selectResolution = String(this.options.resolution);
        },
        _modifyLayout: function _modifyLayout() {
            $('#CaptureView').remove();
            $('.oa-capture-view').append('<OBJECT ID="CaptureView" CLASSID="CLSID:A14B31B4-C560-4DF7-A30C-59A3AE8C598D" ' + 'style="height:100%;width:100%"></OBJECT>');
            var strClassID = $('#CaptureView').attr('CLASSID');
            if (strClassID !== 'CLSID:A14B31B4-C560-4DF7-A30C-59A3AE8C598D') {
                $('#CaptureView').remove();
                $('.oa-capture-view').append('<OBJECT ID="CaptureView" CLASSID="CLSID:A14B31B4-C560-4DF7-A30C-59A3AE8C598D" ' + 'style="height:100%;width:100%"></OBJECT>');
            }
        },
        _initEvent: function _initEvent() {
            var _this = this;
            this.$scope.chooseCamera = function (nIndex) {
                var nSelectIndex = Number(_this.$scope.selectCamera);
                var nResolutionIndex = Number(_this.$scope.selectResolution);
                _this.device_camera.CameraConn(nSelectIndex, 0);
                _this.$scope.arrayResolutions = _this._getResolutions();
                _this.$scope.selectResolution = '0';
            };
            this.$scope.chooseResolution = function (nIndex) {
                var nSelectIndex = Number(_this.$scope.selectCamera);
                var nResolutionIndex = Number(_this.$scope.selectResolution);
                _this.device_camera.CameraConn(nSelectIndex, nResolutionIndex);
            };

            //刷新设备
            this.$scope.updateCamera = function () {
                _this.updateDevice();
            };

            //拍摄
            this.$scope.shoot = function () {
                var strBase64 = _this.device_camera.MakeCapture();
                if (strBase64) {
                    _this.$scope.tableData.push({
                        name: _this.$scope.tableData.length + 1,
                        strBase64: strBase64,
                        uploadStatus: '待上传'
                    });
                }
                //刷新摄像头
                _this.updateDevice();
            };

            //重新拍摄
            this.$scope.reshoot = function () {
                _this.$scope.view = 'camera';
            };

            //查看图片
            this.$scope.viewImg = function (pItem) {
                _this.$scope.view = 'img';
                _this.$scope.imgBase64 = pItem.strBase64;
            };

            //删除拍摄图片
            this.$scope.deleteItem = function (pItem) {
                GM.CommonOper.removeItemFromArray(_this.$scope.tableData, pItem);
            };

            //上传材料
            this.$scope.uploadFile = function () {
                for (var ii = 0; ii < _this.$scope.tableData.length; ii++) {
                    var pUploadItem = _this.$scope.tableData[ii];
                    if (pUploadItem.uploadStatus === '待上传') {
                        var pResult = pRestful.ajax(RESTURL.file.uploadBase64, pUploadItem);
                        if (pResult && pResult.success === true) {
                            pUploadItem.uploadStatus === '上传成功';

                            //添加节点
                            if (_this.$scope.pActiveNode.children == null) {
                                _this.$scope.pActiveNode.children = [];
                            }
                            var pData = pResult.data;
                            _this.$scope.pActiveNode.children.push(pData);
                        }
                    }
                }
            };

            //注册对话框关闭事件
            $('.oa-capture-dialog').on('dialogbeforeclose', function () {
                _this.destory();
            });

