angular.module('frameApp').factory('$handOverService', ['$gmRestful', function ($gmRestful) {
    var service = {
        loadPage: function loadPage(scope) {
            var _this = this;

            if (!scope.taskInfo || !scope.taskInfo.hasOwnProperty('taskKey') || !scope.taskInfo.taskKey) {
                GM.Form.CommonOper.showFailureMsg('无法获取移交相关参数！');
                return;
            }

            scope.result = {};
            scope.nextNodes = [];
            scope.handOverConfig = {
                name: 'name',
                children: 'childrens',
                checkNode: 'checkItem'
            };

            _this._bindEvents(scope);

            //先获取移交参数
            _this._getHandOverChecks(scope.taskInfo.processDefId, scope.taskInfo.taskKey, scope.taskInfo.bizKey).then(function (r) {
                if (r.success === true) {
                    scope.handOverVar = r.data;

                    _this._getHandOverTarget(scope.taskInfo.processDefId, scope.taskInfo.taskKey, r.data).then(function (res) {
                        if (res.success === true && res.data && res.data != null) {
                            scope.nextNodes = res.data;
                            scope.isOnlyLastNode = res.data.length === 1 && res.data[0].node == '__结束__';
                            var userArray = [];
                            var subArray = angular.copy(res.data);

                            for (var i = 0; i < subArray.length; i++) {
                                userArray[i] = {
                                    name: subArray[i].node,
                                    childrens: subArray[i].groups
                                };

                                if (!subArray[i].groups) {
                                    continue;
                                }

                                for (var j = 0; j < subArray[i].groups.length; j++) {
                                    userArray[i].childrens[j] = {
                                        name: subArray[i].groups[j].name,
                                        id: subArray[i].groups[j].id,
                                        childrens: subArray[i].groups[j].users
                                    };
                                }
                            }

                            scope.chooseRole = userArray;
                        } else if (res.message) {
                            GM.Form.CommonOper.showFailureMsg(res.message);
                        }
                    }, function (res) {
                        GM.Form.CommonOper.showFailureMsg(res.message);
                    });
                }
            }, function (r) {
                GM.Form.CommonOper.showFailureMsg(r.message);
            });
        },
        _bindEvents: function _bindEvents(scope) {
            var _this = this;

            scope.checkItem = function (item) {
                _this._checkItem(scope, item);
            };

            scope.modelHandOver = function () {
                _this._doHandOver(scope);
            };

            scope.unselectUser = function (node, id, name) {
                _this._unselect(scope, node, id, name);
            };
        },
        _checkItem: function _checkItem(scope, item) {
            var _this = this;
            scope.selectedUsers = [];

            if (item instanceof Array) {
                for (var i = 0; i < item.length; i++) {
                    if (item[i].flag == '3') {
                        _this._selectUser(scope, item[i].node, item[i].id, item[i].name);
                    }
                }
            } else {
                if (item && item.flag == '3') {
                    _this._selectUser(scope, item.node, item.id, item.name);
                }
            }
        },
        _selectUser: function _selectUser(scope, node, id, name) {
            var user = {
                id: id,
                name: name
            };

            var bFlag = false;

            for (var i = 0; i < scope.selectedUsers.length; i++) {
                if (scope.selectedUsers[i]['node'] && scope.selectedUsers[i]['node'] == node) {
                    if (!GM.CommonOper.isArrayContainsEle(scope.selectedUsers[i]['users'], user)) {
                        var users = scope.selectedUsers[i]['users'] || [];
                        users.push(user);
                        scope.selectedUsers[i]['users'] = users;
                    }

                    bFlag = true;
                    break;
                }
            }

            if (!bFlag) {
                scope.selectedUsers.push({
                    node: node,
                    users: [user]
                });
            }
        },
        _unselect: function _unselect(scope, node, id, name) {
            var _this = this;
            _this._uncheckTreeUser(scope, node, id, name);

            for (var i = 0; i < scope.selectedUsers.length; i++) {
                if (scope.selectedUsers[i]['node'] && scope.selectedUsers[i]['node'] == node) {
                    var users = scope.selectedUsers[i]['users'];
                    var deleteItem = {
                        id: id,
                        name: name
                    };
                    GM.CommonOper.removeItemFromArray(users, deleteItem, 'id');

                    if (users.length === 0) {
                        scope.selectedUsers.splice(i, 1);
                    } else {
                        scope.selectedUsers[i]['users'] = users;
                    }
                }
            }
        },
        _uncheckUserOnTree: function _uncheckUserOnTree(cur, nodeName, id, name) {
            var _this = this;
            if (cur.childrens && cur.childrens.length > 0) {
                for (var i = 0; i < cur.childrens.length; i++) {
                    _this._uncheckUserOnTree(cur.childrens[i], nodeName, id, name);
                }
            } else if (cur.flag == '3' && cur.id == id && cur.node == nodeName) {
                cur.check = false;
            }
        },
        _uncheckTreeUser: function _uncheckTreeUser(scope, nodeName, id, name) {
            for (var i = 0; i < scope.chooseRole.length; i++) {
                this._uncheckUserOnTree(scope.chooseRole[i], nodeName, id, name);
            }
        },
        _doHandOver: function _doHandOver(scope) {
            if ((!scope.isOnlyLastNode || scope.isOnlyLastNode !== true) && (!scope.selectedUsers || scope.selectedUsers.length === 0)) {
                GM.Form.CommonOper.showFailureMsg('请先选择移交对象！');
                return;
            }

            var _this = this;
            var param = {
                userId: top.GM.userInfo.loginName,
                bizKey: scope.taskInfo.bizKey,
                taskKey: scope.taskInfo.taskKey,
                variables: scope.handOverVar
            };

            if (scope.isOnlyLastNode !== true) {
                var target = {
                    target: []
                };
                var arrayIds = null;

                for (var u = 0; u < scope.selectedUsers.length; u++) {
                    arrayIds = GM.Form.CommonOper.mapFieldArray(scope.selectedUsers[u].users, 'id');
                    target.target.push({
                        node: scope.selectedUsers[u].node,
                        users: arrayIds
                    });
                }

                param.targets = JSON.stringify(target);
            }

            this._handOver(param).then(function (response) {
                if (response.success === true) {
                    if (response.data) {
                        _this._saveBizFlowInfo(response.data, scope.taskInfo.bizKey);
                    }

                    var sMsg = scope.isOnlyLastNode === true ? '流程已结束！' : '移交成功！';
                    GM.Form.CommonOper.showSuccessMsg(sMsg);
                    GM.Form.formScope.closeDialog();
                    var strBoxName = scope.handOverParam && scope.handOverParam.boxName;
                    top.goPendingPage(strBoxName);
                } else {
                    GM.Form.CommonOper.showFailureMsg(response.message);
                }
            }, function (response) {
                GM.Form.CommonOper.showFailureMsg(response.message);
            });
        },
        _getBizInfo: function _getBizInfo(userId, bizKey, taskKey) {
            return $gmRestful().get(FORM_REST_URL.workflow.getBizInfo, {
                userId: userId,
                bizKey: bizKey,
                taskKey: taskKey
            });
        },
        _getHandOverChecks: function _getHandOverChecks(processDefId, taskKey, bizKey) {
            return $gmRestful().get(GM.Form.parameters.servers.getHandOverVarServer, {
                processDefId: processDefId,
                taskKey: taskKey,
                bizKey: bizKey
            });
        },
        _getHandOverTarget: function _getHandOverTarget(processDefId, taskKey, v) {
            return $gmRestful().get(FORM_REST_URL.workflow.getHandOverTarget, {
                processDefId: processDefId,
                taskKey: taskKey,
                'var': JSON.stringify(v)
            });
        },
        _handOver: function _handOver(param) {
            return $gmRestful().post(FORM_REST_URL.workflow.handOver, param);
        },
        _saveBizFlowInfo: function _saveBizFlowInfo(id, bizKey) {
            GM.Form.CommonOper.syncAjaxGet(GM.Form.parameters.servers.updateBizFlowInfoServer + '?bizKey=' + bizKey, function (res) {}, function (res) {});
        }
    };

    return service;
}]);

