 ------------------------------*/
.query-form {
  padding: 0 20px;
  height: 100%;
  display: flex;
  flex-direction: column;
}
.query-form > header {
  border-bottom: 1px solid #e7e7e7;
  display: none;
}
.query-form > header h4 {
  padding: 20px 0 20px 20px;
  margin: 0;
}
.query-form .query-content {
  padding: 0 8px;
  display: flex;
  flex-direction: column;
  height: 200px;
  flex: 1;
}
.query-form .query-content .query-tags {
  padding: 5px 0;
  border-bottom: 1px solid #e7e7e7;
}
.query-form .query-content .query-tags .tag-item {
  display: flex;
}
.query-form .query-content .query-tags .tag-item h4 {
  padding: 0 10px;
  width: 100px;
  text-align: right;
}
.query-form .query-content .query-tags .tag-item .tag-container {
  flex: 1;
  width: 100px;
}
.query-form .query-content .query-condition {
  display: flex;
  padding: 20px 0;
  border-bottom: 1px solid #e7e7e7;
}
.query-form .query-content .query-condition .query-input {
  display: flex;
  flex-wrap: wrap;
  width: 660px;
}
.query-form .query-content .query-condition .query-input .query-item {
  width: 330px;
  display: flex;
  align-items: center;
  margin-bottom: 10px;
}
.query-form .query-content .query-condition .query-input .query-item > label {
  width: 100px;
  margin: 0;
  text-align: right;
  padding: 0 10px;
  white-space: nowrap;
}
.query-form .query-content .query-condition .query-input .query-item > div {
  width: 230px;
}
.query-form .query-content .query-condition .query-input .query-item > div > input,
.query-form .query-content .query-condition .query-input .query-item > div > select,
.query-form .query-content .query-condition .query-input .query-item > div > textarea {
  width: 100%;
  border-radius: 0;
}
.query-form .query-content .query-condition .query-input .query-item > .two-condition {
  display: flex;
  align-items: center;
}
.query-form .query-content .query-condition .query-input .query-item > .two-condition > label {
  padding: 0 5px;
}
.query-form .query-content .query-condition .query-input .query-item > .two-condition > div > input,
.query-form .query-content .query-condition .query-input .query-item > .two-condition > div > select,
.query-form .query-content .query-condition .query-input .query-item > .two-condition > div > textarea {
  width: 100%;
  border-radius: 0;
}
.query-form .query-content .query-condition .query-input .query-item .ui-select-bootstrap .ui-select-match-text {
  text-overflow: ellipsis;
  white-space: nowrap;
  overflow: hidden;
}
.query-form .query-content .query-condition .query-tool {
  flex: 1;
  padding-left: 65px;
}
.query-form .query-content .query-condition .query-extra {
  width: 110px;
}
.query-form .query-content .tool-bar {
  display: flex;
  height: 50px;
  align-items: center;
}
.query-form .query-content .tool-bar .tool-tag {
  flex: 1;
  display: flex;
  width: 100px;
}
.query-form .query-content .tool-bar .tool-tag h4 {
  padding: 0 10px;
  width: 100px;
}
.query-form .query-content .tool-bar .tool-tag .tag-container {
  flex: 1;
  width: 100px;
}
.query-form .query-content .tool-bar .tool-buttons > button {
  min-width: inherit;
}
.query-form .query-content .content-table {
  flex: 1;
  height: 10px;
}
.query-form .query-content .content-table > table {
  margin: 0;
}
.query-form .query-content .content-table > div {
  height: 100%;
}
.query-form-mini {
  height: 100%;
  position: relative;
  display: flex;
  flex-direction: column;
}
.query-form-mini > header {
  height: 45px;
  margin: 0 10px;
  border-bottom: solid 1px #c6cace;
}
.query-form-mini > header h4 {
  font-size: 16px;
  padding: 16px 0 0 5px;
  margin: 0;
}
.query-form-mini > header button {
  position: absolute;
  top: 9px;
  right: 10px;
}
.query-form-mini .query-content {
  height: 100px;
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 10px 10px;
}
.query-form-mini .query-content .query-condition {
  margin-bottom: 10px;
}
.query-form-mini .query-content .query-condition .query-item {
  display: flex;
}
.query-form-mini .query-content .query-condition .query-item label {
  padding: 5px 10px;
  width: 50px;
}
.query-form-mini .query-content .query-condition .query-item > div {
  flex: 1;
  width: 10px;
}
.query-form-mini .query-content .tool-bar {
  text-align: center;
  margin-bottom: 10px;
}
.query-form-mini .query-content .content-table {
  flex: 1;
  height: 10px;
  overflow-y: auto;
  overflow-x: hidden;
}
/*----------------------------
          对话框样式
 -----------------------------*/
.ngdialog.ngdialog-theme-default.custom {
  overflow: hidden;
  height: 100%;
}
.ngdialog.ngdialog-theme-default.custom .ngdialog-content {
  background: white;
  border-radius: 0;
  padding: 0;
  width: 1120px;
  height: 100%;
  display: flex;
  flex-direction: column;
}
.ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-header {
  padding: 20px;
  border-bottom: 2px solid #047adf;
  color: #047adf;
}
.ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-content {
  height: 300px;
  overflow-y: auto;
  overflow-x: hidden;
  padding: 10px 50px;
  flex: 1;
  position: relative;
}
.ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-buttons {
  text-align: center;
  padding: 10px 0;
  border-top: 1px solid #f2f2f2;
}
.ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-buttons > button {
  margin-right: 40px;
}
.ngdialog.ngdialog-theme-default.custom.sm .ngdialog-content {
  width: 670px;
}
/*----------------------------
        JQueryDialog对话框
 -----------------------------*/
div.ui-dialog {
  padding: 0;
}
div.ui-dialog .ui-dialog-titlebar {
  background: #337fe5;
}
div.ui-dialog .ui-dialog-titlebar .ui-dialog-title {
  color: white;
}
div.ui-dialog .ui-dialog-titlebar .ui-dialog-titlebar-close {
  border: none;
  border-radius: 0;
}
/*----------------------------
        表格插件样式
 -----------------------------*/
.ui-grid-cell-contents.operate a {
  padding: 0 5px;
}
.grid.ui-grid {
  border: none;
}
/*----------------------------
        向导插件样式
 -----------------------------*/
.ui-tour-backdrop {
  background-color: rgba(0, 0, 0, 0.5);
  fill: rgba(0, 0, 0, 0.5);
}
