.form-simple{
  tr.catagory{
    border-bottom: 1px solid #eeeeee;
    //height:70px;
    td{
      padding: 0;
    }
    span{
      border-bottom: 2px solid #047adf;
      display: inline-block;
      padding: 0 30px 5px 5px;
      color: #047adf;
      margin-top: 42px;
    }
  }
  td{
    border: 0;
    overflow: inherit;
    position: relative;
    vertical-align: bottom;
    &>label{
      margin-bottom:0; 
    }
    &.td-value{
      padding-right: 50px;
    }
    &.td-label{
      label{
        margin-top: 8px;
      }
    }
    &.scrollTable{
      overflow-x: scroll;
      .inner-table{
        width: 100%;
        border: 1px solid #ddd;
        background-color: #f5f5f5;
        th,td{
          text-align: center;
          border: 1px solid #ddd;
          vertical-align:middle;
        }
        .inputBox{
          padding: 0;
          background: #fff;
          input{
            border: none;
            text-align: center;
          }
        }
      }
    }
  }
  td .date-choose-div .icon{
    top: 5px;
  }
  td>input,
  td>textarea,
  td>select,
  .gm-combotree-div>input,
  .date-choose-div>input{
    border-width: 0 0 2px 0;
    width: 100%;
    background: none;
    outline: 0;
    box-shadow: none;
    border-radius: 0;
    border-color: #929292;
    height: 30px;
    &:focus{
      border-width: 0 0 2px 0;
      border-color: @themeColor;
      outline: 0;
      box-shadow: none;
    }
    &:not(select):read-only{
      color: #a09898;
      border-color: #eeeeee;
    }
    &.ng-invalid{
      border-color:red;
    }
    &:not(:read-only):hover{
      border-color: @themeColor;
    }
  }
  td .ui-select-container .ui-select-toggle,
  td .ui-select-container{
    border-width: 0 0 2px 0;
    box-shadow: none;
    border-color: #929292;
    white-space: normal;
    border-radius: 0;
    &:hover{
      background-color:white;
      border-color: @themeColor;
    }
    &:focus{
      border-width: 0 0 2px 0;
      border-color: @themeColor;
      background-color:white;
      outline: 0;
    }
    .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control{
      background: none;
    }
  }
  td table{
    th{
      background-color: #8eb1d0;
      text-align: center;
    }
    td{
      border: 1px solid #ddd;
      text-align: center;
    }
  }
  .form-table-tool{
    text-align: right;
    &>button{
      min-width:inherit;
    }
  }
}

