        .menu {
            background-color: rgb(47, 58, 76);
            color: @selectFontColor;
            .menu-item {
                >.nav {
                    height: 63px;
                    font-size: 14px;
                    padding: 20px 0 0 30px;
                    border-bottom: 1px dashed rgb(60, 71, 89);
                    >i {
                        color: #fff;
                    }
                    >span.icon {
                        color: @selectFontColor;
                    }
                }
                >.nav:hover {
                    background: none;
                }
                >.nav.active {
                    background: none;
                    >i {
                        color: @selectFontColor;
                    }
                }
                .sub-item {
                    >li {
                        border-bottom: 1px dashed rgb(60, 71, 89);
                        background-color: #262e3b;
                        &:hover {
                            background-color: @menuItemSelect;
                        }
                        &.active {
                            background-color: @menuItemSelect;
                        }
                        .subitem-icon {
                            color: #fff;
                        }
                    }
                }
            }
        }
    }
    /*----------------------------------
          控件主题
 ----------------------------------*/
    .tab-wrapper {
        .tab-tabs {
            ul>li {
                background: #0e6deb;
                color: white;
                &.active {
                    background: rgb(2, 81, 174);
                    border-top: 2px solid rgb(2, 225, 1);
                }
                .close-button:hover {
                    color: #fff;
                }
            }
        }
        .tab-tool {
