/*-------------------
        蓝色主题
 --------------------*/
html.blue{
    color: @fontColor;
    background-color: @themeBackground;
    .header-up {
        background: @themeColor url(img/head-bg.png) no-repeat 100% 130%;
        .system-info {
            color: @selectFontColor;
        }
        .user-info {
            color: @selectFontColor;
        }
    }
    .header-down {
        background: @subHeaderColor;
        .menu-header {
            background-color: @menuItemColor;
            color: @selectFontColor;
            &.full-screen:hover {
                background-color: @menuItemSelect;
            }
        }
        .tab-header {
            .tool-item {
                background: #0e6deb;
                color: rgb(133, 180, 236);
                &:hover {
                    background-color: rgb(2, 81, 174);
                }
            }
        }
    }
    .frame-content {
        .menu {
            background-color: rgb(47, 58, 76);
            color: @selectFontColor;
            .menu-item {
                >.nav {
                    height: 63px;
                    font-size: 14px;
                    padding: 20px 0 0 30px;
                    border-bottom: 1px dashed rgb(60, 71, 89);
                    >i {
                        color: #fff;
                    }
                    >span.icon {
                        color: @selectFontColor;
                    }
                }
                >.nav:hover {
                    background: none;
                }
                >.nav.active {
                    background: none;
                    >i {
                        color: @selectFontColor;
                    }
                }
                .sub-item {
                    >li {
                        border-bottom: 1px dashed rgb(60, 71, 89);
                        background-color: #262e3b;
                        &:hover {
                            background-color: @menuItemSelect;
                        }
                        &.active {
                            background-color: @menuItemSelect;
                        }
                        .subitem-icon {
                            color: #fff;
                        }
                    }
                }
            }
        }
    }
    /*----------------------------------
          控件主题
 ----------------------------------*/
    .tab-wrapper {
        .tab-tabs {
            ul>li {
                background: #0e6deb;
                color: white;
                &.active {
                    background: rgb(2, 81, 174);
                    border-top: 2px solid rgb(2, 225, 1);
                }
                .close-button:hover {
                    color: #fff;
                }
            }
        }
        .tab-tool {
            color: rgb(133, 180, 236);
            background: #1b5fcc;
            &:hover {
                background-color: rgb(2, 81, 174);
            }
            &.disabled {
                &:hover {
                    background: #1b5fcc;
                }
            }
        }
    }
    /*-----------------------
        进度条样式
    ------------------------*/
    #loading-bar .bar {
        background-color: @themeColor;
    }
    #loading-bar-spinner .spinner-icon {
        border-top-color: @themeColor;
        border-left-color: @themeColor;
    }
