/*----------------------------
            框架样式
 -----------------------------*/
html{
  font-family:"Microsoft Yahei","Droid Sans Fallback","Arial","Helvetica","sans-serif","宋体";font-size:14px;outline: none;color: @fontColor;
  background-color: @themeBackground;
}

.frame2{
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  >header{
    height:@headerHeight;
    z-index: 1;
    user-select: none;
    &.full-screen{
      height:@subHeaderHeight;
    }
  }
  .frame-content{
    display: flex;
    flex:1 1 auto;
    height: 1px;
    z-index: 0;
  }
  >footer{
    height:0;
  }
  .notify{
    position: absolute;
    right: 400px;
    width: 300px;
    align-self: center;
    top: 25px;
  }
  .notify.full-screen{
    top: 6px;
  }
}
.header-up{
  height:@headerHeight - @subHeaderHeight;
  display: flex;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
  &.full-screen{
    height: 0;
    *{
      display:none;
    }
  }
  .system-info{
    color: @selectFontColor;
    display: flex;
    .logo img{
      height:100%;
      width: 80px;
    }
    .system-name{
      padding: 16px 0 0 10px;
      >div:first-child{
        font-size: 20px;
        font-weight: bold;
      }
    }
  }
  .tools{
    flex:1;
    .theme-option{
      height: 100%;
      color: white;
      text-align: right;
      padding-right: 30px;
      display: flex;
      .dropdown{
          .dropdown-toggle{
            line-height: 80px;
            margin-left: 10px;
            flex: 1;
            padding: 0 10px;
            cursor: pointer;
            height: 80px;
          }
          ul{
              li{
                  .theme-item{
                    float: left;
                    background: red;
                    border-radius: 50px;
                    padding: 2px;
                    border: 2px solid white;
                    margin-right: 5px;
                    height: 20px;
                    width: 20px;
                    align-self: center;
                    text-align: center;
                    cursor: pointer;
                    i{
                      position: relative;
                      top: -3px;
                    }
                  }
              }
          }
      }
    }
  }
  .user-info{
    width: 180px;
    color:white;
    >.btn-group{
      display: flex;
      .user-img{
        height: 60px;
        width: 60px;
        background: aliceblue;
        border-radius: 30px;
        margin-top: 10px;
        >div{
          height: 54px;
          width: 54px;
          background: red;
          border-radius: 30px;
          margin: 3px;
        }
      }
      .dropdown-toggle{
        line-height: 80px;
        margin-left: 10px;
        flex: 1;
        padding: 0 10px;
        cursor: pointer;
        height: 80px;
        >span:first-child{
          max-width: 65px;
          display: inline-block;
          line-height: 80px;
        }
        >span:last-child{
          font-size: 12px;
          padding-left: 5px;
          display: inline-block;
          position: absolute;
          top: 33px;
        }
      }
    }
  }
}
.header-down{
  height:@subHeaderHeight;
  background:@subHeaderColor;
  box-shadow: 0px 2px 5px rgba(0,0,0,.15);
  display: flex;
  .menu-header{
    width: @menuWidth;
    background-color: @menuItemColor;
    text-align: center;
    text-transform: uppercase;
    color: @selectFontColor;
    padding-top: 13px;
    position: relative;
    z-index: 2;
    pointer-events: none;
    &.full-screen{
      width:100px;
      cursor: pointer;
      pointer-events: initial;
      &:hover{
        background-color: @menuItemSelect;
      }
    }
  }
  .tab-header{
    flex:1;
    display: flex;
    .tool-item{
      color: rgb(133,180,236);
      width: 30px;
      text-align: center;
      cursor: pointer;
      background: #1b5fcc;
      position: relative;
      z-index: 1;
      i{
        line-height: 44px;
        font-size: 15px;
      }
      &:hover{
        background-color: rgb(2,81,174);
      }
    }
    .tool-tab{
      flex:1;
    }
  }
}
.frame-content{
  .menu{
    height:100%;
    width:@menuWidth;
    cursor: pointer;
    user-select: none;
    -webkit-transition: all 300ms;
    -moz-transition: all 300ms;
    -ms-transition: all 300ms;
    -o-transition: all 300ms;
    transition: all 300ms;
    overflow-y: auto;
    overflow-x: hidden;
    &.full-screen{
      position: absolute;
      z-index: 999;
      top: 44px;
    }
    &.hidden1{
      transform:translateY(-120%);
    }
    .menu-item{
      >.nav{
        height: 63px;
        font-size: 14px;
        padding: 20px 0 0 30px;
        >i{
          font-size: 20px;
        }
        >.nav-icon{
          height: 20px;
          margin-top: -6px;
        }
        >.title{
          padding-left: 10px;
        }
        >span.icon{
          float: right;
          position: relative;
          top: 5px;
          right: 30px;
          -webkit-transition: all 300ms;
          -moz-transition: all 300ms;
          -ms-transition: all 300ms;
          -o-transition: all 300ms;
          transition: all 300ms;
          &.click-down{
            -webkit-transform: rotate(180deg);
            -ms-transform: rotate(180deg);
            -o-transform: rotate(180deg);
            transform: rotate(180deg);
          }
        }
      }
      .sub-item{
        list-style-type: none;
        margin: 0;
        padding: 0;
        -webkit-transition: all 300ms;
        -moz-transition: all 300ms;
        -ms-transition: all 300ms;
        -o-transition: all 300ms;
        transition: all 300ms;
        &.hidden1{
          height: 0 !important;
          li{
            display: none;
          }
        }
        >li{
          width: @menuWidth;
          height:@subItemHeight;
          display: flex;
          padding-top: 18px;
          .subitem-icon{
            padding-left: 50px;
            .nav-icon{
              height:15px;
            }
          }
          .subitem-title{
            flex: 1;
            padding-left: 10px;
          }
          &:hover{
            background-color: @menuItemSelect;
          }
          &.active{
            background-color: @menuItemSelect;
          }
        }
      }
    }
  }
  .content{
    margin: 10px;
    -webkit-box-shadow: 0 0 5px #eaeaea;
    -moz-box-shadow: 0 0 5px #eaeaea;
    box-shadow: 0 0 5px #eaeaea;
    flex: 1;
    border: 1px solid #dfdfdf;
    height:calc(~"100% - 15px");
    width:500px;
    background: white;
    position: relative;
    .view-content{
      height: 100%;
      overflow-y: auto;
      overflow-x: hidden;
      >div{
        height:100%;
      }
    }
  }
}

